101→  //    This handles newly-created subfolders whose IPNS hasn't propagated yet
102→  if (!resolved) {
103→    console.warn(
104→      `IPNS resolution returned null for folder "${name}" (${ipnsName}). Marking as not loaded for retry.`
105→    );
106→    return {
107→      id: folderId ?? 'root',
108→      name,
109→      ipnsName,
110→      parentId,
111→      children: [],
