176→
177→  // 4. Wrap keys with user's public key (ECIES encryption)
178→  // The 64-byte libp2p format private key (privateKey || publicKey) is wrapped
179→  const ipnsPrivateKeyEncrypted = bytesToHex(
180→    await wrapKey(ipnsKeypair.privateKey, params.userPublicKey)
181→  );
182→  const folderKeyEncrypted = bytesToHex(await wrapKey(folderKey, params.userPublicKey));
183→
184→  // 5. TEE-02: Encrypt IPNS private key with TEE public key for republishing
185→  let encryptedIpnsPrivateKey: string | undefined;
186→  let keyEpoch: number | undefined;
187→
188→  const teeKeys = useAuthStore.getState().teeKeys;
189→  if (teeKeys?.currentPublicKey) {
190→    const teePublicKey = hexToBytes(teeKeys.currentPublicKey);
191→    const encryptedKey = await wrapKey(ipnsKeypair.privateKey, teePublicKey);
192→    encryptedIpnsPrivateKey = bytesToHex(encryptedKey);
193→    keyEpoch = teeKeys.currentEpoch;
194→  }
195→
196→  // 6. Create folder entry for parent's metadata
197→  const now = Date.now();
198→  const folder: FolderEntry = {
199→    type: 'folder',
200→    id: crypto.randomUUID(),
201→    name: params.name,
202→    ipnsName,
203→    ipnsPrivateKeyEncrypted,
204→    folderKeyEncrypted,
205→    createdAt: now,
206→    modifiedAt: now,
207→  };
208→
209→  return {
210→    folder,
211→    ipnsPrivateKey: ipnsKeypair.privateKey,
212→    folderKey,
213→    encryptedIpnsPrivateKey,
214→    keyEpoch,
215→  };
216→}
217→
218→/**
219→ * Update folder metadata and publish to IPNS (v2 format).
220→ *
221→ * Encrypts the metadata with the folder key, uploads to IPFS,
222→ * and publishes an updated IPNS record pointing to the new CID.
223→ *
224→ * @param params.folderId - Folder being updated
225→ * @param params.children - New children array (FolderChild[])
226→ * @param params.folderKey - Decrypted AES-256 folder key
227→ * @param params.ipnsPrivateKey - Decrypted Ed25519 IPNS private key
228→ * @param params.ipnsName - IPNS name for this folder
229→ * @param params.sequenceNumber - Current sequence number
230→ * @param params.encryptedIpnsPrivateKey - TEE-wrapped key (first publish only)
231→ * @param params.keyEpoch - TEE key epoch (required with encryptedIpnsPrivateKey)
232→ * @returns New CID and sequence number
233→ */
234→export async function updateFolderMetadata(params: {
235→  folderId: string;
236→  children: FolderChild[];
237→  folderKey: Uint8Array;
238→  ipnsPrivateKey: Uint8Array;
239→  ipnsName: string;
240→  sequenceNumber: bigint;
241→  encryptedIpnsPrivateKey?: string;
242→  keyEpoch?: number;
243→}): Promise<{ cid: string; newSequenceNumber: bigint }> {
244→  // 1. Create v2 folder metadata
245→  const metadata: FolderMetadata = {
246→    version: 'v2',
247→    children: params.children,
248→  };
249→
250→  // 2. Encrypt metadata with folder key
251→  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
252→
253→  // 3. Upload to IPFS via backend relay
254→  const blob = new Blob([JSON.stringify(encrypted)], {
255→    type: 'application/json',
256→  });
257→  const { cid } = await addToIpfs(blob);
258→
259→  // 4. Publish IPNS record pointing to new CID
260→  const newSeq = params.sequenceNumber + 1n;
261→  await createAndPublishIpnsRecord({
262→    ipnsPrivateKey: params.ipnsPrivateKey,
263→    ipnsName: params.ipnsName,
264→    metadataCid: cid,
265→    sequenceNumber: newSeq,
266→    encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
267→    keyEpoch: params.keyEpoch,
268→  });
269→
270→  return { cid, newSequenceNumber: newSeq };
271→}
