273→/**
274→ * Build a folder IPNS record payload for batch publishing.
275→ *
276→ * Creates and signs an IPNS record locally without publishing.
277→ * Returns the payload that can be included in a batch publish call.
278→ *
279→ * @returns Folder IPNS record payload for batch publish
280→ */
281→async function buildFolderIpnsRecord(params: {
282→  children: FolderChild[];
283→  folderKey: Uint8Array;
284→  ipnsPrivateKey: Uint8Array;
285→  ipnsName: string;
286→  sequenceNumber: bigint;
287→  encryptedIpnsPrivateKey?: string;
288→  keyEpoch?: number;
289→}): Promise<{
290→  cid: string;
291→  record: FileIpnsRecordPayload & { recordType: 'folder' };
292→  newSequenceNumber: bigint;
293→}> {
294→  // 1. Create v2 folder metadata
295→  const metadata: FolderMetadata = {
296→    version: 'v2',
297→    children: params.children,
298→  };
299→
300→  // 2. Encrypt metadata with folder key
301→  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
302→
303→  // 3. Upload to IPFS
304→  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
305→  const { cid } = await addToIpfs(blob);
306→
307→  // 4. Create IPNS record
308→  const newSeq = params.sequenceNumber + 1n;
309→  const record = await createIpnsRecord(
310→    params.ipnsPrivateKey,
311→    `/ipfs/${cid}`,
312→    newSeq,
313→    24 * 60 * 60 * 1000
314→  );
315→  const recordBytes = marshalIpnsRecord(record);
316→  const recordBase64 = uint8ToBase64(recordBytes);
317→
318→  return {
319→    cid,
320→    record: {
321→      ipnsName: params.ipnsName,
322→      recordBase64,
323→      metadataCid: cid,
324→      encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
325→      keyEpoch: params.keyEpoch,
326→      recordType: 'folder',
327→    },
328→    newSequenceNumber: newSeq,
329→  };
330→}
