332→/**
333→ * Rename a folder within its parent.
334→ *
335→ * Updates the folder entry's name in the parent's metadata and publishes
336→ * an updated IPNS record for the parent folder.
337→ *
338→ * @param params.folderId - ID of folder to rename
339→ * @param params.newName - New name for the folder
340→ * @param params.parentFolderState - Parent folder containing this folder
341→ * @throws Error if folder not found or name collision exists
342→ */
343→export async function renameFolder(params: {
344→  folderId: string;
345→  newName: string;
346→  parentFolderState: FolderNode;
347→}): Promise<void> {
348→  // 1. Find folder entry in parent's children
349→  const children = [...params.parentFolderState.children];
350→  const folderIndex = children.findIndex((c) => c.type === 'folder' && c.id === params.folderId);
351→
352→  if (folderIndex === -1) throw new Error('Folder not found');
353→
354→  // 2. Check for name collision
355→  const nameExists = children.some((c) => c.name === params.newName && c.id !== params.folderId);
356→  if (nameExists) throw new Error('An item with this name already exists');
357→
358→  // 3. Update name and modifiedAt
359→  const folder = children[folderIndex] as FolderEntry;
360→  children[folderIndex] = {
361→    ...folder,
362→    name: params.newName,
363→    modifiedAt: Date.now(),
364→  };
365→
366→  // 4. Update parent folder metadata and publish
367→  await updateFolderMetadata({
368→    folderId: params.parentFolderState.id,
369→    children,
370→    folderKey: params.parentFolderState.folderKey,
371→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
372→    ipnsName: params.parentFolderState.ipnsName,
373→    sequenceNumber: params.parentFolderState.sequenceNumber,
374→  });
375→}
