499→/**
500→ * Add a file to a folder after successful upload (v2).
501→ *
502→ * Creates a FilePointer from the file's IPNS record and adds it to the folder's
503→ * children array. Publishes both the file IPNS record and the updated folder
504→ * metadata via a single batch API call.
505→ *
506→ * @param params.parentFolderState - Parent folder to add file to
507→ * @param params.fileId - Pre-generated file UUID
508→ * @param params.name - Original file name
509→ * @param params.fileIpnsRecord - File IPNS record payload from createFileMetadata
510→ * @returns Created file pointer and new sequence number
511→ * @throws Error if name collision exists
512→ */
513→export async function addFileToFolder(params: {
514→  parentFolderState: FolderNode;
515→  fileId: string;
516→  name: string;
517→  fileIpnsRecord: FileIpnsRecordPayload;
518→}): Promise<{ filePointer: FilePointer; newSequenceNumber: bigint }> {
519→  // 1. Check for name collision
520→  const nameExists = params.parentFolderState.children.some((c) => c.name === params.name);
521→  if (nameExists) {
522→    throw new Error('A file with this name already exists');
523→  }
524→
525→  // 2. Create FilePointer (slim reference to per-file IPNS record)
526→  const now = Date.now();
527→  const filePointer: FilePointer = {
528→    type: 'file',
529→    id: params.fileId,
530→    name: params.name,
531→    fileMetaIpnsName: params.fileIpnsRecord.ipnsName,
532→    createdAt: now,
533→    modifiedAt: now,
534→  };
535→
536→  // 3. Add FilePointer to parent's children
537→  const children: FolderChild[] = [...params.parentFolderState.children, filePointer];
538→
539→  // 4. Build folder IPNS record for batch publish
540→  const folderResult = await buildFolderIpnsRecord({
541→    children,
542→    folderKey: params.parentFolderState.folderKey,
543→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
544→    ipnsName: params.parentFolderState.ipnsName,
545→    sequenceNumber: params.parentFolderState.sequenceNumber,
546→  });
547→
548→  // 5. Batch publish: file IPNS record + folder IPNS record
549→  const publishResult = await batchPublishIpnsRecords([
550→    { ...params.fileIpnsRecord, recordType: 'file' },
551→    folderResult.record,
552→  ]);
553→
554→  if (publishResult.totalFailed > 0) {
555→    console.error(
556→      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
557→    );
558→    throw new Error('Failed to publish one or more IPNS records');
559→  }
560→
561→  return { filePointer, newSequenceNumber: folderResult.newSequenceNumber };
562→}
563→
564→/**
565→ * Add multiple files to a folder after successful upload (v2 batch).
566→ *
567→ * Creates FilePointer objects for all files, checks for name collisions,
568→ * then publishes all N file IPNS records + 1 folder IPNS record via a
569→ * single batch API call.
570→ *
571→ * @param params.parentFolderState - Parent folder to add files to
572→ * @param params.files - Array of file data with pre-created IPNS records
573→ * @returns Created file pointers and new sequence number
574→ * @throws Error if any name collision exists
575→ */
576→export async function addFilesToFolder(params: {
577→  parentFolderState: FolderNode;
578→  files: Array<{
579→    fileId: string;
580→    name: string;
581→    fileIpnsRecord: FileIpnsRecordPayload;
582→  }>;
583→}): Promise<{ filePointers: FilePointer[]; newSequenceNumber: bigint }> {
584→  // 1. Build a set of existing child names for collision detection
585→  const existingNames = new Set(params.parentFolderState.children.map((c) => c.name));
586→
587→  // 2. Create FilePointer for each file, checking collisions
588→  const filePointers: FilePointer[] = [];
589→  const now = Date.now();
590→
591→  for (const file of params.files) {
592→    if (existingNames.has(file.name)) {
593→      throw new Error(`A file with name "${file.name}" already exists`);
594→    }
595→    existingNames.add(file.name);
596→
597→    const filePointer: FilePointer = {
598→      type: 'file',
599→      id: file.fileId,
600→      name: file.name,
601→      fileMetaIpnsName: file.fileIpnsRecord.ipnsName,
602→      createdAt: now,
603→      modifiedAt: now,
604→    };
605→    filePointers.push(filePointer);
606→  }
607→
608→  // 3. Build updated children array
609→  const children: FolderChild[] = [...params.parentFolderState.children, ...filePointers];
610→
611→  // 4. Build folder IPNS record
612→  const folderResult = await buildFolderIpnsRecord({
613→    children,
614→    folderKey: params.parentFolderState.folderKey,
615→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
616→    ipnsName: params.parentFolderState.ipnsName,
617→    sequenceNumber: params.parentFolderState.sequenceNumber,
618→  });
619→
620→  // 5. Batch publish: all N file IPNS records + 1 folder IPNS record (single API call)
621→  const allRecords = [
622→    ...params.files.map((f) => ({ ...f.fileIpnsRecord, recordType: 'file' as const })),
623→    folderResult.record,
624→  ];
625→  const publishResult = await batchPublishIpnsRecords(allRecords);
626→
627→  if (publishResult.totalFailed > 0) {
628→    console.error(
629→      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
630→    );
631→    throw new Error('Failed to publish one or more IPNS records');
632→  }
633→
634→  return { filePointers, newSequenceNumber: folderResult.newSequenceNumber };
635→}
