637→/**
638→ * Replace file content in v2 metadata (content update).
639→ *
640→ * THIS IS THE PRIMARY BENEFIT OF PER-FILE IPNS: content updates publish
641→ * ONLY the file's IPNS record. The folder metadata is NOT touched because
642→ * the FilePointer still points to the same fileMetaIpnsName, which now
643→ * resolves to the updated content.
644→ *
645→ * @param params.fileId - ID of file to update
646→ * @param params.fileIpnsRecord - Updated file IPNS record from updateFileMetadata
647→ * @param params.parentFolderState - Parent folder (used only to find the FilePointer)
648→ * @returns Void -- no folder metadata update needed
649→ * @throws Error if file not found
650→ */
651→export async function replaceFileInFolder(params: {
652→  fileId: string;
653→  fileIpnsRecord: FileIpnsRecordPayload;
654→  parentFolderState: FolderNode;
655→}): Promise<void> {
656→  // 1. Verify file exists in parent's children
657→  const fileExists = params.parentFolderState.children.some(
658→    (c) => c.type === 'file' && c.id === params.fileId
659→  );
660→
661→  if (!fileExists) throw new Error('File not found');
662→
663→  // 2. Publish ONLY the file IPNS record (folder metadata untouched!)
664→  // This is the key optimization: content update does NOT modify folder metadata.
665→  const publishResult = await batchPublishIpnsRecords([
666→    { ...params.fileIpnsRecord, recordType: 'file' },
667→  ]);
668→
669→  if (publishResult.totalFailed > 0) {
670→    throw new Error('Failed to publish file IPNS record');
671→  }
672→}
673→
674→/**
675→ * Calculate the maximum depth of a folder's subtree.
676→ *
677→ * Used to check if moving a folder would exceed the depth limit.
678→ *
679→ * @param folderId - Folder ID to calculate subtree depth for
680→ * @param folders - Current folder tree
681→ * @returns Maximum depth in the subtree (0 if folder has no children)
682→ */
683→export function calculateSubtreeDepth(
684→  folderId: string,
685→  folders: Record<string, FolderNode>
686→): number {
687→  const folder = folders[folderId];
688→  if (!folder) return 0;
689→
690→  let maxChildDepth = 0;
691→  for (const child of folder.children) {
692→    if (child.type === 'folder') {
693→      const childDepth = 1 + calculateSubtreeDepth(child.id, folders);
694→      maxChildDepth = Math.max(maxChildDepth, childDepth);
695→    }
696→  }
697→
698→  return maxChildDepth;
699→}
700→
701→/**
702→ * Check if a folder is a descendant of a potential ancestor.
703→ *
704→ * Used to prevent moving a folder into itself or its descendants.
705→ *
706→ * @param folderId - Folder to check
707→ * @param potentialAncestorId - Potential ancestor folder ID
708→ * @param folders - Current folder tree
709→ * @returns true if folderId is a descendant of potentialAncestorId
710→ */
711→export function isDescendantOf(
712→  folderId: string,
713→  potentialAncestorId: string,
714→  folders: Record<string, FolderNode>
715→): boolean {
716→  let currentId: string | null = folderId;
717→
718→  while (currentId !== null) {
719→    if (currentId === potentialAncestorId) return true;
720→    const currentFolder: FolderNode | undefined = folders[currentId];
721→    if (!currentFolder) break;
722→    currentId = currentFolder.parentId;
723→  }
724→
725→  return false;
726→}
727→
728→/**
729→ * Move a folder from one parent to another.
730→ *
731→ * Uses add-before-remove pattern to prevent data loss on failure.
732→ * Validates depth limit and prevents moving folder into itself.
733→ *
734→ * @param params.folderId - ID of folder to move
735→ * @param params.sourceFolderState - Current parent folder
736→ * @param params.destFolderState - Destination parent folder
737→ * @param params.folders - Full folder tree (for depth calculation)
738→ * @throws Error if folder not found, name collision, depth limit exceeded,
739→ *         or attempting to move folder into itself/descendant
740→ */
741→export async function moveFolder(params: {
742→  folderId: string;
743→  sourceFolderState: FolderNode;
744→  destFolderState: FolderNode;
745→  folders: Record<string, FolderNode>;
746→}): Promise<void> {
747→  // 1. Find folder in source
748→  const folder = params.sourceFolderState.children.find(
749→    (c) => c.type === 'folder' && c.id === params.folderId
750→  ) as FolderEntry | undefined;
751→
752→  if (!folder) throw new Error('Folder not found');
753→
754→  // 2. Check name collision in destination
755→  const nameExists = params.destFolderState.children.some((c) => c.name === folder.name);
756→  if (nameExists) throw new Error('An item with this name already exists in destination');
757→
758→  // 3. Check depth limit (20 levels max)
759→  const destDepth = getDepth(params.destFolderState.id, params.folders);
760→  const folderSubtreeDepth = calculateSubtreeDepth(params.folderId, params.folders);
761→  if (destDepth + 1 + folderSubtreeDepth > MAX_FOLDER_DEPTH) {
762→    throw new Error(`Cannot move: would exceed maximum folder depth of ${MAX_FOLDER_DEPTH}`);
763→  }
764→
765→  // 4. Prevent moving folder into itself or its descendants
766→  if (isDescendantOf(params.destFolderState.id, params.folderId, params.folders)) {
767→    throw new Error('Cannot move folder into itself or its subfolder');
768→  }
769→
770→  // 5. ADD to destination FIRST (add-before-remove pattern)
771→  const destChildren: FolderChild[] = [
772→    ...params.destFolderState.children,
773→    {
774→      ...folder,
775→      modifiedAt: Date.now(),
776→    },
777→  ];
778→
779→  await updateFolderMetadata({
780→    folderId: params.destFolderState.id,
781→    children: destChildren,
782→    folderKey: params.destFolderState.folderKey,
783→    ipnsPrivateKey: params.destFolderState.ipnsPrivateKey,
784→    ipnsName: params.destFolderState.ipnsName,
785→    sequenceNumber: params.destFolderState.sequenceNumber,
786→  });
787→
788→  // 6. REMOVE from source AFTER destination confirmed
789→  const sourceChildren = params.sourceFolderState.children.filter(
790→    (c) => !(c.type === 'folder' && c.id === params.folderId)
791→  );
792→
793→  await updateFolderMetadata({
794→    folderId: params.sourceFolderState.id,
795→    children: sourceChildren,
796→    folderKey: params.sourceFolderState.folderKey,
797→    ipnsPrivateKey: params.sourceFolderState.ipnsPrivateKey,
798→    ipnsName: params.sourceFolderState.ipnsName,
799→    sequenceNumber: params.sourceFolderState.sequenceNumber,
800→  });
801→}
802→
803→/**
804→ * Move a file from one folder to another (v2).
805→ *
806→ * Uses add-before-remove pattern to prevent data loss on failure.
807→ * FilePointer contains fileMetaIpnsName which stays the same across moves --
808→ * no file IPNS record changes needed, only folder metadata changes.
809→ *
810→ * @param params.fileId - ID of file to move
811→ * @param params.sourceFolderState - Current parent folder
812→ * @param params.destFolderState - Destination folder
813→ * @throws Error if file not found or name collision exists
814→ */
815→export async function moveFile(params: {
816→  fileId: string;
817→  sourceFolderState: FolderNode;
818→  destFolderState: FolderNode;
819→}): Promise<void> {
820→  // 1. Find file in source (v2: FilePointer)
821→  const file = params.sourceFolderState.children.find(
822→    (c) => c.type === 'file' && c.id === params.fileId
823→  ) as FilePointer | undefined;
824→
825→  if (!file) throw new Error('File not found');
826→
827→  // 2. Check name collision in destination
828→  const nameExists = params.destFolderState.children.some((c) => c.name === file.name);
829→  if (nameExists) throw new Error('An item with this name already exists in destination');
830→
831→  // 3. ADD to destination FIRST
832→  const destChildren: FolderChild[] = [
833→    ...params.destFolderState.children,
834→    {
835→      ...file,
836→      modifiedAt: Date.now(),
837→    },
838→  ];
839→
840→  await updateFolderMetadata({
841→    folderId: params.destFolderState.id,
842→    children: destChildren,
843→    folderKey: params.destFolderState.folderKey,
844→    ipnsPrivateKey: params.destFolderState.ipnsPrivateKey,
845→    ipnsName: params.destFolderState.ipnsName,
846→    sequenceNumber: params.destFolderState.sequenceNumber,
847→  });
848→
849→  // 4. REMOVE from source AFTER
850→  const sourceChildren = params.sourceFolderState.children.filter(
851→    (c) => !(c.type === 'file' && c.id === params.fileId)
852→  );
853→
854→  await updateFolderMetadata({
855→    folderId: params.sourceFolderState.id,
856→    children: sourceChildren,
857→    folderKey: params.sourceFolderState.folderKey,
858→    ipnsPrivateKey: params.sourceFolderState.ipnsPrivateKey,
859→    ipnsName: params.sourceFolderState.ipnsName,
860→    sequenceNumber: params.sourceFolderState.sequenceNumber,
861→  });
862→}
863→
864→/**
865→ * Rename a file within its parent folder (v2).
866→ *
867→ * Updates the FilePointer's name in the parent's metadata and publishes
868→ * an updated IPNS record for the parent folder.
869→ * File rename does NOT touch the file's own IPNS record -- only folder metadata.
870→ *
871→ * @param params.fileId - ID of file to rename
872→ * @param params.newName - New name for the file
873→ * @param params.parentFolderState - Parent folder containing this file
874→ * @throws Error if file not found or name collision exists
875→ */
876→export async function renameFile(params: {
877→  fileId: string;
878→  newName: string;
879→  parentFolderState: FolderNode;
880→}): Promise<void> {
881→  const children = [...params.parentFolderState.children];
882→  const fileIndex = children.findIndex((c) => c.type === 'file' && c.id === params.fileId);
883→
884→  if (fileIndex === -1) throw new Error('File not found');
885→
886→  // Check name collision
887→  const nameExists = children.some((c) => c.name === params.newName && c.id !== params.fileId);
888→  if (nameExists) throw new Error('An item with this name already exists');
889→
890→  const file = children[fileIndex] as FilePointer;
891→  children[fileIndex] = {
892→    ...file,
893→    name: params.newName,
894→    modifiedAt: Date.now(),
895→  };
896→
897→  await updateFolderMetadata({
898→    folderId: params.parentFolderState.id,
899→    children,
900→    folderKey: params.parentFolderState.folderKey,
901→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
902→    ipnsName: params.parentFolderState.ipnsName,
903→    sequenceNumber: params.parentFolderState.sequenceNumber,
904→  });
905→}
906→
907→/**
908→ * Fetch and decrypt folder metadata from IPFS.
909→ *
910→ * Used for sync operations when remote IPNS resolves to a different CID.
911→ * Fetches the encrypted metadata blob from IPFS and decrypts it with the folder key.
912→ * @param cid - IPFS CID of the encrypted metadata blob
913→ * @param folderKey - Decrypted AES-256 folder key
914→ * @returns Decrypted folder metadata (v2)
915→ * @throws Error if fetch or decryption fails
916→ */
917→export async function fetchAndDecryptMetadata(
918→  cid: string,
919→  folderKey: Uint8Array
920→): Promise<FolderMetadata> {
921→  // 1. Fetch encrypted metadata blob from IPFS
922→  const encryptedBytes = await fetchFromIpfs(cid);
923→
924→  // 2. Parse as JSON to get EncryptedFolderMetadata (contains iv and data fields)
925→  const encryptedJson = new TextDecoder().decode(encryptedBytes);
926→  const encrypted: EncryptedFolderMetadata = JSON.parse(encryptedJson);
927→
928→  // 3. Decrypt using folder key
929→  const metadata = await decryptFolderMetadata(encrypted, folderKey);
930→
931→  return metadata;
932→}
