  1→/**
  2→ * Folder Service - Folder CRUD operations with encryption
  3→ *
  4→ * Handles folder creation, loading, and metadata updates with
  5→ * client-side encryption using @cipherbox/crypto.
  6→ *
  7→ * Folders store slim FilePointer references. File metadata lives in per-file IPNS records.
  8→ */
  9→
 10→import {
 11→  generateEd25519Keypair,
 12→  deriveIpnsName,
 13→  generateRandomBytes,
 14→  wrapKey,
 15→  bytesToHex,
 16→  hexToBytes,
 17→  encryptFolderMetadata,
 18→  decryptFolderMetadata,
 19→  createIpnsRecord,
 20→  marshalIpnsRecord,
 21→  type FolderMetadata,
 22→  type EncryptedFolderMetadata,
 23→  type FolderEntry,
 24→  type FolderChild,
 25→  type FilePointer,
 26→} from '@cipherbox/crypto';
 27→import { addToIpfs, fetchFromIpfs } from '../lib/api/ipfs';
 28→import { createAndPublishIpnsRecord, resolveIpnsRecord } from './ipns.service';
 29→import { batchPublishIpnsRecords } from './ipns.service';
 30→import { useAuthStore } from '../stores/auth.store';
 31→import type { FolderNode } from '../stores/folder.store';
 32→import type { FileIpnsRecordPayload } from './file-metadata.service';
 33→
 34→/** Maximum folder nesting depth per FOLD-03 */
 35→const MAX_FOLDER_DEPTH = 20;
 36→
 37→/** Safe base64 encoding that avoids call stack overflow from spread operator */
 38→function uint8ToBase64(bytes: Uint8Array): string {
 39→  let binary = '';
 40→  for (let i = 0; i < bytes.length; i++) {
 41→    binary += String.fromCharCode(bytes[i]);
 42→  }
 43→  return btoa(binary);
 44→}
 45→
 46→/**
 47→ * Calculate the depth of a folder from root.
 48→ * Root folder has depth 0, immediate children have depth 1, etc.
 49→ *
 50→ * Used for enforcing FOLD-03 depth limit of 20.
 51→ *
 52→ * @param folderId - Folder ID to calculate depth for (null = root)
 53→ * @param folders - Current folder tree
 54→ * @returns Depth from root (0 for root)
 55→ */
 56→export function getDepth(folderId: string | null, folders: Record<string, FolderNode>): number {
 57→  if (folderId === null) return 0; // root is depth 0
 58→
 59→  let depth = 0;
 60→  let currentId: string | null = folderId;
 61→
 62→  while (currentId !== null) {
 63→    const folder: FolderNode | undefined = folders[currentId];
 64→    if (!folder) break;
 65→    depth++;
 66→    currentId = folder.parentId;
 67→  }
 68→
 69→  return depth;
 70→}
 71→
 72→/**
 73→ * Load a folder's metadata from IPNS.
 74→ *
 75→ * Resolves the folder's IPNS name to get the current metadata CID,
 76→ * fetches and decrypts the metadata, and returns a complete FolderNode.
 77→ * Returns v2 metadata with FolderChild[] children.
 78→ *
 79→ * IMPORTANT: Does NOT eagerly resolve per-file IPNS records during folder load.
 80→ * File metadata is lazy-loaded on download/preview (Pitfall 1 from research).
 81→ *
 82→ * @param folderId - Folder ID (null for root, or UUID)
 83→ * @param folderKey - Decrypted AES-256 key for this folder
 84→ * @param ipnsPrivateKey - Decrypted Ed25519 private key for IPNS
 85→ * @param ipnsName - IPNS name for this folder
 86→ * @param parentId - Parent folder ID (null for root)
 87→ * @param name - Display name for this folder
 88→ */
 89→export async function loadFolder(
 90→  folderId: string | null,
 91→  folderKey: Uint8Array,
 92→  ipnsPrivateKey: Uint8Array,
 93→  ipnsName: string,
 94→  parentId: string | null,
 95→  name: string
 96→): Promise<FolderNode> {
 97→  // 1. Resolve IPNS to get current metadata CID
 98→  const resolved = await resolveIpnsRecord(ipnsName);
 99→
100→  // 2. If IPNS resolution returns null, return unloaded folder so it can be retried
101→  //    This handles newly-created subfolders whose IPNS hasn't propagated yet
102→  if (!resolved) {
103→    console.warn(
104→      `IPNS resolution returned null for folder "${name}" (${ipnsName}). Marking as not loaded for retry.`
105→    );
106→    return {
107→      id: folderId ?? 'root',
108→      name,
109→      ipnsName,
110→      parentId,
111→      children: [],
112→      isLoaded: false,
113→      isLoading: false,
114→      sequenceNumber: 0n,
115→      folderKey,
116→      ipnsPrivateKey,
117→    };
118→  }
119→
120→  // 3. Fetch encrypted metadata from IPFS and decrypt with folderKey
121→  const metadata = await fetchAndDecryptMetadata(resolved.cid, folderKey);
122→
123→  // 4. Return complete FolderNode with decrypted children
124→  // FilePointer objects have fileMetaIpnsName instead of inline file data.
125→  return {
126→    id: folderId ?? 'root',
127→    name,
128→    ipnsName,
129→    parentId,
130→    children: metadata.children ?? [],
131→    isLoaded: true,
132→    isLoading: false,
133→    sequenceNumber: resolved.sequenceNumber,
134→    folderKey,
135→    ipnsPrivateKey,
136→  };
137→}
138→
139→/**
140→ * Create a new subfolder with generated keys.
141→ *
142→ * Generates new Ed25519 IPNS keypair and AES-256 folder key,
143→ * wraps them with the user's public key for storage.
144→ *
145→ * @param params.parentFolderId - Parent folder ID (null for root)
146→ * @param params.name - Folder name
147→ * @param params.userPublicKey - User's secp256k1 public key for ECIES wrapping
148→ * @param params.folders - Current folder tree (for depth checking)
149→ * @returns Created folder entry and decrypted keys
150→ * @throws Error if depth limit exceeded (FOLD-03)
151→ */
152→export async function createFolder(params: {
153→  parentFolderId: string | null;
154→  name: string;
155→  userPublicKey: Uint8Array;
156→  folders: Record<string, FolderNode>;
157→}): Promise<{
158→  folder: FolderEntry;
159→  ipnsPrivateKey: Uint8Array;
160→  folderKey: Uint8Array;
161→  encryptedIpnsPrivateKey?: string;
162→  keyEpoch?: number;
163→}> {
164→  // 1. Check depth limit (FOLD-03)
165→  const parentDepth = getDepth(params.parentFolderId, params.folders);
166→  if (parentDepth >= MAX_FOLDER_DEPTH) {
167→    throw new Error(`Cannot create folder: maximum depth of ${MAX_FOLDER_DEPTH} exceeded`);
168→  }
169→
170→  // 2. Generate Ed25519 keypair for folder IPNS
171→  const ipnsKeypair = await generateEd25519Keypair();
172→  const ipnsName = await deriveIpnsName(ipnsKeypair.publicKey);
173→
174→  // 3. Generate random AES-256 folder key
175→  const folderKey = generateRandomBytes(32);
176→
177→  // 4. Wrap keys with user's public key (ECIES encryption)
178→  // The 64-byte libp2p format private key (privateKey || publicKey) is wrapped
179→  const ipnsPrivateKeyEncrypted = bytesToHex(
180→    await wrapKey(ipnsKeypair.privateKey, params.userPublicKey)
181→  );
182→  const folderKeyEncrypted = bytesToHex(await wrapKey(folderKey, params.userPublicKey));
183→
184→  // 5. TEE-02: Encrypt IPNS private key with TEE public key for republishing
185→  let encryptedIpnsPrivateKey: string | undefined;
186→  let keyEpoch: number | undefined;
187→
188→  const teeKeys = useAuthStore.getState().teeKeys;
189→  if (teeKeys?.currentPublicKey) {
190→    const teePublicKey = hexToBytes(teeKeys.currentPublicKey);
191→    const encryptedKey = await wrapKey(ipnsKeypair.privateKey, teePublicKey);
192→    encryptedIpnsPrivateKey = bytesToHex(encryptedKey);
193→    keyEpoch = teeKeys.currentEpoch;
194→  }
195→
196→  // 6. Create folder entry for parent's metadata
197→  const now = Date.now();
198→  const folder: FolderEntry = {
199→    type: 'folder',
200→    id: crypto.randomUUID(),
201→    name: params.name,
202→    ipnsName,
203→    ipnsPrivateKeyEncrypted,
204→    folderKeyEncrypted,
205→    createdAt: now,
206→    modifiedAt: now,
207→  };
208→
209→  return {
210→    folder,
211→    ipnsPrivateKey: ipnsKeypair.privateKey,
212→    folderKey,
213→    encryptedIpnsPrivateKey,
214→    keyEpoch,
215→  };
216→}
217→
218→/**
219→ * Update folder metadata and publish to IPNS (v2 format).
220→ *
221→ * Encrypts the metadata with the folder key, uploads to IPFS,
222→ * and publishes an updated IPNS record pointing to the new CID.
223→ *
224→ * @param params.folderId - Folder being updated
225→ * @param params.children - New children array (FolderChild[])
226→ * @param params.folderKey - Decrypted AES-256 folder key
227→ * @param params.ipnsPrivateKey - Decrypted Ed25519 IPNS private key
228→ * @param params.ipnsName - IPNS name for this folder
229→ * @param params.sequenceNumber - Current sequence number
230→ * @param params.encryptedIpnsPrivateKey - TEE-wrapped key (first publish only)
231→ * @param params.keyEpoch - TEE key epoch (required with encryptedIpnsPrivateKey)
232→ * @returns New CID and sequence number
233→ */
234→export async function updateFolderMetadata(params: {
235→  folderId: string;
236→  children: FolderChild[];
237→  folderKey: Uint8Array;
238→  ipnsPrivateKey: Uint8Array;
239→  ipnsName: string;
240→  sequenceNumber: bigint;
241→  encryptedIpnsPrivateKey?: string;
242→  keyEpoch?: number;
243→}): Promise<{ cid: string; newSequenceNumber: bigint }> {
244→  // 1. Create v2 folder metadata
245→  const metadata: FolderMetadata = {
246→    version: 'v2',
247→    children: params.children,
248→  };
249→
250→  // 2. Encrypt metadata with folder key
251→  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
252→
253→  // 3. Upload to IPFS via backend relay
254→  const blob = new Blob([JSON.stringify(encrypted)], {
255→    type: 'application/json',
256→  });
257→  const { cid } = await addToIpfs(blob);
258→
259→  // 4. Publish IPNS record pointing to new CID
260→  const newSeq = params.sequenceNumber + 1n;
261→  await createAndPublishIpnsRecord({
262→    ipnsPrivateKey: params.ipnsPrivateKey,
263→    ipnsName: params.ipnsName,
264→    metadataCid: cid,
265→    sequenceNumber: newSeq,
266→    encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
267→    keyEpoch: params.keyEpoch,
268→  });
269→
270→  return { cid, newSequenceNumber: newSeq };
271→}
272→
273→/**
274→ * Build a folder IPNS record payload for batch publishing.
275→ *
276→ * Creates and signs an IPNS record locally without publishing.
277→ * Returns the payload that can be included in a batch publish call.
278→ *
279→ * @returns Folder IPNS record payload for batch publish
280→ */
281→async function buildFolderIpnsRecord(params: {
282→  children: FolderChild[];
283→  folderKey: Uint8Array;
284→  ipnsPrivateKey: Uint8Array;
285→  ipnsName: string;
286→  sequenceNumber: bigint;
287→  encryptedIpnsPrivateKey?: string;
288→  keyEpoch?: number;
289→}): Promise<{
290→  cid: string;
291→  record: FileIpnsRecordPayload & { recordType: 'folder' };
292→  newSequenceNumber: bigint;
293→}> {
294→  // 1. Create v2 folder metadata
295→  const metadata: FolderMetadata = {
296→    version: 'v2',
297→    children: params.children,
298→  };
299→
300→  // 2. Encrypt metadata with folder key
301→  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
302→
303→  // 3. Upload to IPFS
304→  const blob = new Blob([JSON.stringify(encrypted)], { type: 'application/json' });
305→  const { cid } = await addToIpfs(blob);
306→
307→  // 4. Create IPNS record
308→  const newSeq = params.sequenceNumber + 1n;
309→  const record = await createIpnsRecord(
310→    params.ipnsPrivateKey,
311→    `/ipfs/${cid}`,
312→    newSeq,
313→    24 * 60 * 60 * 1000
314→  );
315→  const recordBytes = marshalIpnsRecord(record);
316→  const recordBase64 = uint8ToBase64(recordBytes);
317→
318→  return {
319→    cid,
320→    record: {
321→      ipnsName: params.ipnsName,
322→      recordBase64,
323→      metadataCid: cid,
324→      encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
325→      keyEpoch: params.keyEpoch,
326→      recordType: 'folder',
327→    },
328→    newSequenceNumber: newSeq,
329→  };
330→}
331→
332→/**
333→ * Rename a folder within its parent.
334→ *
335→ * Updates the folder entry's name in the parent's metadata and publishes
336→ * an updated IPNS record for the parent folder.
337→ *
338→ * @param params.folderId - ID of folder to rename
339→ * @param params.newName - New name for the folder
340→ * @param params.parentFolderState - Parent folder containing this folder
341→ * @throws Error if folder not found or name collision exists
342→ */
343→export async function renameFolder(params: {
344→  folderId: string;
345→  newName: string;
346→  parentFolderState: FolderNode;
347→}): Promise<void> {
348→  // 1. Find folder entry in parent's children
349→  const children = [...params.parentFolderState.children];
350→  const folderIndex = children.findIndex((c) => c.type === 'folder' && c.id === params.folderId);
351→
352→  if (folderIndex === -1) throw new Error('Folder not found');
353→
354→  // 2. Check for name collision
355→  const nameExists = children.some((c) => c.name === params.newName && c.id !== params.folderId);
356→  if (nameExists) throw new Error('An item with this name already exists');
357→
358→  // 3. Update name and modifiedAt
359→  const folder = children[folderIndex] as FolderEntry;
360→  children[folderIndex] = {
361→    ...folder,
362→    name: params.newName,
363→    modifiedAt: Date.now(),
364→  };
365→
366→  // 4. Update parent folder metadata and publish
367→  await updateFolderMetadata({
368→    folderId: params.parentFolderState.id,
369→    children,
370→    folderKey: params.parentFolderState.folderKey,
371→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
372→    ipnsName: params.parentFolderState.ipnsName,
373→    sequenceNumber: params.parentFolderState.sequenceNumber,
374→  });
375→}
376→
377→/**
378→ * Delete a folder and all its contents recursively.
379→ *
380→ * For v2 metadata: collects fileMetaIpnsName values from FilePointer children
381→ * (not CIDs, since CIDs live in per-file IPNS records, not folder metadata).
382→ * Removes the folder from the parent's metadata, publishes the update.
383→ * Returns the collected file IPNS names so the caller can resolve them for
384→ * CID unpinning and TEE unenrollment.
385→ *
386→ * @param params.folderId - ID of folder to delete
387→ * @param params.parentFolderState - Parent folder containing this folder
388→ * @param params.getFolderState - Function to get folder state by ID (for recursion)
389→ * @param params.unpinCid - Function to unpin a CID from IPFS
390→ * @throws Error if folder not found
391→ */
392→export async function deleteFolder(params: {
393→  folderId: string;
394→  parentFolderState: FolderNode;
395→  getFolderState: (id: string) => FolderNode | undefined;
396→  unpinCid: (cid: string) => Promise<void>;
397→}): Promise<string[]> {
398→  // 1. Find folder in parent's children
399→  const children = [...params.parentFolderState.children];
400→  const folderIndex = children.findIndex((c) => c.type === 'folder' && c.id === params.folderId);
401→
402→  if (folderIndex === -1) throw new Error('Folder not found');
403→
404→  // 2. Recursively collect file IPNS names for cleanup
405→  // In v2, file children are FilePointers with fileMetaIpnsName (no inline CID).
406→  // The caller must resolve each fileMetaIpnsName to get the CID for unpinning.
407→  const fileIpnsNames: string[] = [];
408→  const collectFileIpnsNames = (folderId: string) => {
409→    const folder = params.getFolderState(folderId);
410→    if (!folder) return;
411→
412→    for (const child of folder.children) {
413→      if (child.type === 'file') {
414→        const filePointer = child as FilePointer;
415→        if (filePointer.fileMetaIpnsName) {
416→          fileIpnsNames.push(filePointer.fileMetaIpnsName);
417→        }
418→      } else if (child.type === 'folder') {
419→        collectFileIpnsNames(child.id);
420→      }
421→    }
422→  };
423→
424→  collectFileIpnsNames(params.folderId);
425→
426→  // 3. Remove folder from parent's children
427→  children.splice(folderIndex, 1);
428→
429→  // 4. Update parent folder metadata and publish
430→  await updateFolderMetadata({
431→    folderId: params.parentFolderState.id,
432→    children,
433→    folderKey: params.parentFolderState.folderKey,
434→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
435→    ipnsName: params.parentFolderState.ipnsName,
436→    sequenceNumber: params.parentFolderState.sequenceNumber,
437→  });
438→
439→  // 5. File CID unpinning and TEE unenrollment deferred to caller
440→  // The caller (useFolder hook) resolves fileMetaIpnsName -> CID for unpinning.
441→  // TODO: Phase 14 should add batch unenrollment for orphaned file IPNS records.
442→
443→  return fileIpnsNames;
444→}
445→
446→/**
447→ * Delete a file from its parent folder (v2).
448→ *
449→ * Removes the FilePointer from the parent's metadata, publishes the update.
450→ * Returns the fileMetaIpnsName so the caller can resolve it for CID unpinning
451→ * and TEE unenrollment.
452→ *
453→ * Note: This handles folder metadata update. File CID unpinning is handled
454→ * by the caller after resolving the file metadata.
455→ *
456→ * @param params.fileId - ID of file to delete
457→ * @param params.parentFolderState - Parent folder containing this file
458→ * @returns fileMetaIpnsName for caller to handle cleanup
459→ * @throws Error if file not found
460→ */
461→export async function deleteFileFromFolder(params: {
462→  fileId: string;
463→  parentFolderState: FolderNode;
464→}): Promise<{ fileMetaIpnsName: string | undefined }> {
465→  // 1. Find file in parent's children
466→  const children = [...params.parentFolderState.children];
467→  const fileIndex = children.findIndex((c) => c.type === 'file' && c.id === params.fileId);
468→
469→  if (fileIndex === -1) throw new Error('File not found');
470→
471→  // 2. Get fileMetaIpnsName for cleanup
472→  const filePointer = children[fileIndex] as FilePointer;
473→  const fileMetaIpnsName = filePointer.fileMetaIpnsName;
474→
475→  // 3. Remove file from parent's children
476→  children.splice(fileIndex, 1);
477→
478→  // 4. Update parent folder metadata and publish
479→  await updateFolderMetadata({
480→    folderId: params.parentFolderState.id,
481→    children,
482→    folderKey: params.parentFolderState.folderKey,
483→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
484→    ipnsName: params.parentFolderState.ipnsName,
485→    sequenceNumber: params.parentFolderState.sequenceNumber,
486→  });
487→
488→  // 5. TEE unenrollment: no API endpoint available yet.
489→  // TODO: Phase 14 should expose unenrollIpns via REST API.
490→  if (fileMetaIpnsName) {
491→    console.warn(
492→      `File IPNS record ${fileMetaIpnsName} orphaned after deletion. TEE unenrollment deferred to Phase 14.`
493→    );
494→  }
495→
496→  return { fileMetaIpnsName };
497→}
498→
499→/**
500→ * Add a file to a folder after successful upload (v2).
501→ *
502→ * Creates a FilePointer from the file's IPNS record and adds it to the folder's
503→ * children array. Publishes both the file IPNS record and the updated folder
504→ * metadata via a single batch API call.
505→ *
506→ * @param params.parentFolderState - Parent folder to add file to
507→ * @param params.fileId - Pre-generated file UUID
508→ * @param params.name - Original file name
509→ * @param params.fileIpnsRecord - File IPNS record payload from createFileMetadata
510→ * @returns Created file pointer and new sequence number
511→ * @throws Error if name collision exists
512→ */
513→export async function addFileToFolder(params: {
514→  parentFolderState: FolderNode;
515→  fileId: string;
516→  name: string;
517→  fileIpnsRecord: FileIpnsRecordPayload;
518→}): Promise<{ filePointer: FilePointer; newSequenceNumber: bigint }> {
519→  // 1. Check for name collision
520→  const nameExists = params.parentFolderState.children.some((c) => c.name === params.name);
521→  if (nameExists) {
522→    throw new Error('A file with this name already exists');
523→  }
524→
525→  // 2. Create FilePointer (slim reference to per-file IPNS record)
526→  const now = Date.now();
527→  const filePointer: FilePointer = {
528→    type: 'file',
529→    id: params.fileId,
530→    name: params.name,
531→    fileMetaIpnsName: params.fileIpnsRecord.ipnsName,
532→    createdAt: now,
533→    modifiedAt: now,
534→  };
535→
536→  // 3. Add FilePointer to parent's children
537→  const children: FolderChild[] = [...params.parentFolderState.children, filePointer];
538→
539→  // 4. Build folder IPNS record for batch publish
540→  const folderResult = await buildFolderIpnsRecord({
541→    children,
542→    folderKey: params.parentFolderState.folderKey,
543→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
544→    ipnsName: params.parentFolderState.ipnsName,
545→    sequenceNumber: params.parentFolderState.sequenceNumber,
546→  });
547→
548→  // 5. Batch publish: file IPNS record + folder IPNS record
549→  const publishResult = await batchPublishIpnsRecords([
550→    { ...params.fileIpnsRecord, recordType: 'file' },
551→    folderResult.record,
552→  ]);
553→
554→  if (publishResult.totalFailed > 0) {
555→    console.error(
556→      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
557→    );
558→    throw new Error('Failed to publish one or more IPNS records');
559→  }
560→
561→  return { filePointer, newSequenceNumber: folderResult.newSequenceNumber };
562→}
563→
564→/**
565→ * Add multiple files to a folder after successful upload (v2 batch).
566→ *
567→ * Creates FilePointer objects for all files, checks for name collisions,
568→ * then publishes all N file IPNS records + 1 folder IPNS record via a
569→ * single batch API call.
570→ *
571→ * @param params.parentFolderState - Parent folder to add files to
572→ * @param params.files - Array of file data with pre-created IPNS records
573→ * @returns Created file pointers and new sequence number
574→ * @throws Error if any name collision exists
575→ */
576→export async function addFilesToFolder(params: {
577→  parentFolderState: FolderNode;
578→  files: Array<{
579→    fileId: string;
580→    name: string;
581→    fileIpnsRecord: FileIpnsRecordPayload;
582→  }>;
583→}): Promise<{ filePointers: FilePointer[]; newSequenceNumber: bigint }> {
584→  // 1. Build a set of existing child names for collision detection
585→  const existingNames = new Set(params.parentFolderState.children.map((c) => c.name));
586→
587→  // 2. Create FilePointer for each file, checking collisions
588→  const filePointers: FilePointer[] = [];
589→  const now = Date.now();
590→
591→  for (const file of params.files) {
592→    if (existingNames.has(file.name)) {
593→      throw new Error(`A file with name "${file.name}" already exists`);
594→    }
595→    existingNames.add(file.name);
596→
597→    const filePointer: FilePointer = {
598→      type: 'file',
599→      id: file.fileId,
600→      name: file.name,
601→      fileMetaIpnsName: file.fileIpnsRecord.ipnsName,
602→      createdAt: now,
603→      modifiedAt: now,
604→    };
605→    filePointers.push(filePointer);
606→  }
607→
608→  // 3. Build updated children array
609→  const children: FolderChild[] = [...params.parentFolderState.children, ...filePointers];
610→
611→  // 4. Build folder IPNS record
612→  const folderResult = await buildFolderIpnsRecord({
613→    children,
614→    folderKey: params.parentFolderState.folderKey,
615→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
616→    ipnsName: params.parentFolderState.ipnsName,
617→    sequenceNumber: params.parentFolderState.sequenceNumber,
618→  });
619→
620→  // 5. Batch publish: all N file IPNS records + 1 folder IPNS record (single API call)
621→  const allRecords = [
622→    ...params.files.map((f) => ({ ...f.fileIpnsRecord, recordType: 'file' as const })),
623→    folderResult.record,
624→  ];
625→  const publishResult = await batchPublishIpnsRecords(allRecords);
626→
627→  if (publishResult.totalFailed > 0) {
628→    console.error(
629→      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
630→    );
631→    throw new Error('Failed to publish one or more IPNS records');
632→  }
633→
634→  return { filePointers, newSequenceNumber: folderResult.newSequenceNumber };
635→}
636→
637→/**
638→ * Replace file content in v2 metadata (content update).
639→ *
640→ * THIS IS THE PRIMARY BENEFIT OF PER-FILE IPNS: content updates publish
641→ * ONLY the file's IPNS record. The folder metadata is NOT touched because
642→ * the FilePointer still points to the same fileMetaIpnsName, which now
643→ * resolves to the updated content.
644→ *
645→ * @param params.fileId - ID of file to update
646→ * @param params.fileIpnsRecord - Updated file IPNS record from updateFileMetadata
647→ * @param params.parentFolderState - Parent folder (used only to find the FilePointer)
648→ * @returns Void -- no folder metadata update needed
649→ * @throws Error if file not found
650→ */
651→export async function replaceFileInFolder(params: {
652→  fileId: string;
653→  fileIpnsRecord: FileIpnsRecordPayload;
654→  parentFolderState: FolderNode;
655→}): Promise<void> {
656→  // 1. Verify file exists in parent's children
657→  const fileExists = params.parentFolderState.children.some(
658→    (c) => c.type === 'file' && c.id === params.fileId
659→  );
660→
661→  if (!fileExists) throw new Error('File not found');
662→
663→  // 2. Publish ONLY the file IPNS record (folder metadata untouched!)
664→  // This is the key optimization: content update does NOT modify folder metadata.
665→  const publishResult = await batchPublishIpnsRecords([
666→    { ...params.fileIpnsRecord, recordType: 'file' },
667→  ]);
668→
669→  if (publishResult.totalFailed > 0) {
670→    throw new Error('Failed to publish file IPNS record');
671→  }
672→}
673→
674→/**
675→ * Calculate the maximum depth of a folder's subtree.
676→ *
677→ * Used to check if moving a folder would exceed the depth limit.
678→ *
679→ * @param folderId - Folder ID to calculate subtree depth for
680→ * @param folders - Current folder tree
681→ * @returns Maximum depth in the subtree (0 if folder has no children)
682→ */
683→export function calculateSubtreeDepth(
684→  folderId: string,
685→  folders: Record<string, FolderNode>
686→): number {
687→  const folder = folders[folderId];
688→  if (!folder) return 0;
689→
690→  let maxChildDepth = 0;
691→  for (const child of folder.children) {
692→    if (child.type === 'folder') {
693→      const childDepth = 1 + calculateSubtreeDepth(child.id, folders);
694→      maxChildDepth = Math.max(maxChildDepth, childDepth);
695→    }
696→  }
697→
698→  return maxChildDepth;
699→}
700→
701→/**
702→ * Check if a folder is a descendant of a potential ancestor.
703→ *
704→ * Used to prevent moving a folder into itself or its descendants.
705→ *
706→ * @param folderId - Folder to check
707→ * @param potentialAncestorId - Potential ancestor folder ID
708→ * @param folders - Current folder tree
709→ * @returns true if folderId is a descendant of potentialAncestorId
710→ */
711→export function isDescendantOf(
712→  folderId: string,
713→  potentialAncestorId: string,
714→  folders: Record<string, FolderNode>
715→): boolean {
716→  let currentId: string | null = folderId;
717→
718→  while (currentId !== null) {
719→    if (currentId === potentialAncestorId) return true;
720→    const currentFolder: FolderNode | undefined = folders[currentId];
721→    if (!currentFolder) break;
722→    currentId = currentFolder.parentId;
723→  }
724→
725→  return false;
726→}
727→
728→/**
729→ * Move a folder from one parent to another.
730→ *
731→ * Uses add-before-remove pattern to prevent data loss on failure.
732→ * Validates depth limit and prevents moving folder into itself.
733→ *
734→ * @param params.folderId - ID of folder to move
735→ * @param params.sourceFolderState - Current parent folder
736→ * @param params.destFolderState - Destination parent folder
737→ * @param params.folders - Full folder tree (for depth calculation)
738→ * @throws Error if folder not found, name collision, depth limit exceeded,
739→ *         or attempting to move folder into itself/descendant
740→ */
741→export async function moveFolder(params: {
742→  folderId: string;
743→  sourceFolderState: FolderNode;
744→  destFolderState: FolderNode;
745→  folders: Record<string, FolderNode>;
746→}): Promise<void> {
747→  // 1. Find folder in source
748→  const folder = params.sourceFolderState.children.find(
749→    (c) => c.type === 'folder' && c.id === params.folderId
750→  ) as FolderEntry | undefined;
751→
752→  if (!folder) throw new Error('Folder not found');
753→
754→  // 2. Check name collision in destination
755→  const nameExists = params.destFolderState.children.some((c) => c.name === folder.name);
756→  if (nameExists) throw new Error('An item with this name already exists in destination');
757→
758→  // 3. Check depth limit (20 levels max)
759→  const destDepth = getDepth(params.destFolderState.id, params.folders);
760→  const folderSubtreeDepth = calculateSubtreeDepth(params.folderId, params.folders);
761→  if (destDepth + 1 + folderSubtreeDepth > MAX_FOLDER_DEPTH) {
762→    throw new Error(`Cannot move: would exceed maximum folder depth of ${MAX_FOLDER_DEPTH}`);
763→  }
764→
765→  // 4. Prevent moving folder into itself or its descendants
766→  if (isDescendantOf(params.destFolderState.id, params.folderId, params.folders)) {
767→    throw new Error('Cannot move folder into itself or its subfolder');
768→  }
769→
770→  // 5. ADD to destination FIRST (add-before-remove pattern)
771→  const destChildren: FolderChild[] = [
772→    ...params.destFolderState.children,
773→    {
774→      ...folder,
775→      modifiedAt: Date.now(),
776→    },
777→  ];
778→
779→  await updateFolderMetadata({
780→    folderId: params.destFolderState.id,
781→    children: destChildren,
782→    folderKey: params.destFolderState.folderKey,
783→    ipnsPrivateKey: params.destFolderState.ipnsPrivateKey,
784→    ipnsName: params.destFolderState.ipnsName,
785→    sequenceNumber: params.destFolderState.sequenceNumber,
786→  });
787→
788→  // 6. REMOVE from source AFTER destination confirmed
789→  const sourceChildren = params.sourceFolderState.children.filter(
790→    (c) => !(c.type === 'folder' && c.id === params.folderId)
791→  );
792→
793→  await updateFolderMetadata({
794→    folderId: params.sourceFolderState.id,
795→    children: sourceChildren,
796→    folderKey: params.sourceFolderState.folderKey,
797→    ipnsPrivateKey: params.sourceFolderState.ipnsPrivateKey,
798→    ipnsName: params.sourceFolderState.ipnsName,
799→    sequenceNumber: params.sourceFolderState.sequenceNumber,
800→  });
801→}
802→
803→/**
804→ * Move a file from one folder to another (v2).
805→ *
806→ * Uses add-before-remove pattern to prevent data loss on failure.
807→ * FilePointer contains fileMetaIpnsName which stays the same across moves --
808→ * no file IPNS record changes needed, only folder metadata changes.
809→ *
810→ * @param params.fileId - ID of file to move
811→ * @param params.sourceFolderState - Current parent folder
812→ * @param params.destFolderState - Destination folder
813→ * @throws Error if file not found or name collision exists
814→ */
815→export async function moveFile(params: {
816→  fileId: string;
817→  sourceFolderState: FolderNode;
818→  destFolderState: FolderNode;
819→}): Promise<void> {
820→  // 1. Find file in source (v2: FilePointer)
821→  const file = params.sourceFolderState.children.find(
822→    (c) => c.type === 'file' && c.id === params.fileId
823→  ) as FilePointer | undefined;
824→
825→  if (!file) throw new Error('File not found');
826→
827→  // 2. Check name collision in destination
828→  const nameExists = params.destFolderState.children.some((c) => c.name === file.name);
829→  if (nameExists) throw new Error('An item with this name already exists in destination');
830→
831→  // 3. ADD to destination FIRST
832→  const destChildren: FolderChild[] = [
833→    ...params.destFolderState.children,
834→    {
835→      ...file,
836→      modifiedAt: Date.now(),
837→    },
838→  ];
839→
840→  await updateFolderMetadata({
841→    folderId: params.destFolderState.id,
842→    children: destChildren,
843→    folderKey: params.destFolderState.folderKey,
844→    ipnsPrivateKey: params.destFolderState.ipnsPrivateKey,
845→    ipnsName: params.destFolderState.ipnsName,
846→    sequenceNumber: params.destFolderState.sequenceNumber,
847→  });
848→
849→  // 4. REMOVE from source AFTER
850→  const sourceChildren = params.sourceFolderState.children.filter(
851→    (c) => !(c.type === 'file' && c.id === params.fileId)
852→  );
853→
854→  await updateFolderMetadata({
855→    folderId: params.sourceFolderState.id,
856→    children: sourceChildren,
857→    folderKey: params.sourceFolderState.folderKey,
858→    ipnsPrivateKey: params.sourceFolderState.ipnsPrivateKey,
859→    ipnsName: params.sourceFolderState.ipnsName,
860→    sequenceNumber: params.sourceFolderState.sequenceNumber,
861→  });
862→}
863→
864→/**
865→ * Rename a file within its parent folder (v2).
866→ *
867→ * Updates the FilePointer's name in the parent's metadata and publishes
868→ * an updated IPNS record for the parent folder.
869→ * File rename does NOT touch the file's own IPNS record -- only folder metadata.
870→ *
871→ * @param params.fileId - ID of file to rename
872→ * @param params.newName - New name for the file
873→ * @param params.parentFolderState - Parent folder containing this file
874→ * @throws Error if file not found or name collision exists
875→ */
876→export async function renameFile(params: {
877→  fileId: string;
878→  newName: string;
879→  parentFolderState: FolderNode;
880→}): Promise<void> {
881→  const children = [...params.parentFolderState.children];
882→  const fileIndex = children.findIndex((c) => c.type === 'file' && c.id === params.fileId);
883→
884→  if (fileIndex === -1) throw new Error('File not found');
885→
886→  // Check name collision
887→  const nameExists = children.some((c) => c.name === params.newName && c.id !== params.fileId);
888→  if (nameExists) throw new Error('An item with this name already exists');
889→
890→  const file = children[fileIndex] as FilePointer;
891→  children[fileIndex] = {
892→    ...file,
893→    name: params.newName,
894→    modifiedAt: Date.now(),
895→  };
896→
897→  await updateFolderMetadata({
898→    folderId: params.parentFolderState.id,
899→    children,
900→    folderKey: params.parentFolderState.folderKey,
901→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
902→    ipnsName: params.parentFolderState.ipnsName,
903→    sequenceNumber: params.parentFolderState.sequenceNumber,
904→  });
905→}
906→
907→/**
908→ * Fetch and decrypt folder metadata from IPFS.
909→ *
910→ * Used for sync operations when remote IPNS resolves to a different CID.
911→ * Fetches the encrypted metadata blob from IPFS and decrypts it with the folder key.
912→ * @param cid - IPFS CID of the encrypted metadata blob
913→ * @param folderKey - Decrypted AES-256 folder key
914→ * @returns Decrypted folder metadata (v2)
915→ * @throws Error if fetch or decryption fails
916→ */
917→export async function fetchAndDecryptMetadata(
918→  cid: string,
919→  folderKey: Uint8Array
920→): Promise<FolderMetadata> {
921→  // 1. Fetch encrypted metadata blob from IPFS
922→  const encryptedBytes = await fetchFromIpfs(cid);
923→
924→  // 2. Parse as JSON to get EncryptedFolderMetadata (contains iv and data fields)
925→  const encryptedJson = new TextDecoder().decode(encryptedBytes);
926→  const encrypted: EncryptedFolderMetadata = JSON.parse(encryptedJson);
927→
928→  // 3. Decrypt using folder key
929→  const metadata = await decryptFolderMetadata(encrypted, folderKey);
930→
931→  return metadata;
932→}
933→

