 38→/**
 39→ * Application initialization sequence.
 40→ */
 41→async function init(): Promise<void> {
 42→  console.log('CipherBox Desktop initializing...');
 43→
 44→  const appDiv = document.getElementById('app');
 45→
 46→  // Show loading state
 47→  if (appDiv) {
 48→    appDiv.innerHTML = loadingHtml('Initializing CipherBox...');
 49→  }
 50→
 51→  // Step 1: Check for dev-key mode (debug builds)
 52→  const devKey: string | null = await invoke('get_dev_key');
 53→  if (devKey) {
 54→    if (appDiv) {
 55→      appDiv.innerHTML = loadingHtml('Dev mode: authenticating with provided key...');
 56→    }
 57→    try {
 58→      await handleDevKeyAuth(devKey);
 59→      handleAuthSuccess(appDiv);
 60→      return;
 61→    } catch (err) {
 62→      console.error('Dev-key auth failed:', err);
 63→      if (appDiv) {
 64→        appDiv.innerHTML = errorHtml(
 65→          err instanceof Error ? err.message : 'Dev-key authentication failed'
 66→        );
 67→      }
 68→      return;
 69→    }
 70→  }
 71→
 72→  // Step 2: Try silent refresh from Keychain
 73→  let silentRefreshOk = false;
 74→  try {
 75→    const refreshed: boolean = await invoke('try_silent_refresh');
 76→    if (refreshed) {
 77→      console.log('API session refreshed from Keychain');
 78→      silentRefreshOk = true;
 79→    }
 80→  } catch (err) {
 81→    console.warn('Silent refresh failed:', err);
 82→    // Not an error -- just means we need full login
 83→  }
 84→
 85→  // Step 3: Initialize Core Kit
 86→  if (appDiv) {
 87→    appDiv.innerHTML = loadingHtml('Connecting to CipherBox...');
 88→  }
 89→
 90→  try {
 91→    await initCoreKit();
 92→  } catch (err) {
 93→    console.error('Core Kit initialization failed:', err);
 94→    if (appDiv) {
 95→      appDiv.innerHTML = errorHtml(
 96→        err instanceof Error ? err.message : 'Failed to initialize authentication'
 97→      );
 98→    }
 99→    return;
100→  }
101→
102→  // Step 3b: If Core Kit restored a previous session, try to use it
103→  if (getCoreKitStatus() === 'LOGGED_IN') {
104→    if (silentRefreshOk) {
105→      // Both Core Kit session and API tokens are valid — restore directly
106→      try {
107→        if (appDiv) {
108→          appDiv.innerHTML = loadingHtml('Restoring session...');
109→        }
110→        await restoreSession();
111→        handleAuthSuccess(appDiv);
112→        return;
113→      } catch (err) {
114→        console.warn('Session restore failed, falling back to login:', err);
115→      }
116→    }
117→    // Core Kit has a session but API tokens are stale or restore failed —
118→    // logout Core Kit to get a clean state for fresh login
119→    console.log('Clearing stale Core Kit session before login');
120→    try {
121→      await logout();
122→    } catch {
123→      // Best-effort cleanup
124→    }
125→  }
126→
127→  // Step 4: Show login form
128→  if (appDiv) {
129→    renderLoginForm(appDiv);
130→  }
131→}
