441→      mfaStatus.style.color = '#ef4444';
442→      mfaStatus.textContent = err instanceof Error ? err.message : 'Recovery failed';
443→    }
444→  });
445→
446→  // --- Device approval path ---
447→  document.getElementById('approval-btn')?.addEventListener('click', async () => {
448→    document.getElementById('recovery-form')!.style.display = 'none';
