463→        if (!activeRequestId) return;
464→        try {
465→          const status = await pollApprovalStatus(activeRequestId);
466→          if (status === 'approved') {
467→            if (pollInterval) clearInterval(pollInterval);
468→            pollInterval = null;
469→            activeRequestId = null;
