474→            activeRequestId = null;
475→            statusEl.style.color = '#ef4444';
476→            statusEl.textContent = 'Approval was rejected.';
477→          } else if (status === 'expired') {
478→            if (pollInterval) clearInterval(pollInterval);
479→            pollInterval = null;
480→            activeRequestId = null;
481→            statusEl.style.color = '#ef4444';
482→            statusEl.textContent = 'Approval request expired.';
483→          }
484→        } catch {
485→          // Continue polling on transient errors
486→        }
487→      }, 3000);
488→    } catch (err) {
489→      statusEl.style.color = '#ef4444';
490→      statusEl.textContent = 'Failed to request approval';
491→      console.error('[MFA] Approval request error:', err);
492→    }
493→  });
494→
495→  // Cancel approval button
496→  document.getElementById('cancel-approval-btn')?.addEventListener('click', async () => {
497→    if (pollInterval) {
498→      clearInterval(pollInterval);
499→      pollInterval = null;
500→    }
501→    if (activeRequestId) {
502→      await cancelApprovalRequest(activeRequestId);
503→      activeRequestId = null;
504→    }
505→    document.getElementById('approval-waiting')!.style.display = 'none';
506→    mfaStatus.textContent = '';
507→  });
508→
509→  // Back to login button
510→  document.getElementById('mfa-logout-btn')?.addEventListener('click', async () => {
511→    // Clean up any active approval
512→    if (pollInterval) {
513→      clearInterval(pollInterval);
514→      pollInterval = null;
515→    }
516→    if (activeRequestId) {
517→      await cancelApprovalRequest(activeRequestId);
518→      activeRequestId = null;
519→    }
520→    try {
521→      await logout();
522→    } catch (err) {
523→      console.warn('Logout during MFA failed:', err);
524→    }
525→    location.reload();
526→  });
527→}
