553→/**
554→ * Handle dev-key authentication (debug builds only).
555→ *
556→ * Uses the backend's test-login endpoint to get tokens and a deterministic
557→ * keypair, then calls handle_test_login_complete (skips /auth/login POST).
558→ *
559→ * The private key comes from the test-login response (server-generated),
560→ * NOT from the CLI --dev-key argument. This ensures the keypair matches
561→ * the user record so vault ECIES operations succeed.
562→ */
563→async function handleDevKeyAuth(_devKeyHex: string): Promise<void> {
564→  const testLoginSecret = import.meta.env.VITE_TEST_LOGIN_SECRET;
565→  if (!testLoginSecret) {
566→    throw new Error(
567→      'VITE_TEST_LOGIN_SECRET is not set in .env. ' +
568→        "Dev-key mode requires a test-login secret matching the API's TEST_LOGIN_SECRET."
569→    );
570→  }
571→
572→  const resp = await fetch(`${API_BASE}/auth/test-login`, {
573→    method: 'POST',
574→    headers: { 'Content-Type': 'application/json' },
575→    body: JSON.stringify({
576→      email: 'dev-key@cipherbox.local',
577→      secret: testLoginSecret,
578→    }),
579→  });
580→
581→  if (!resp.ok) {
582→    const body = await resp.text().catch(() => '');
583→    throw new Error(`Test-login failed (${resp.status}): ${body}`);
584→  }
585→
586→  const data = await resp.json();
587→
588→  // Call handle_test_login_complete with the server-issued tokens and keypair.
589→  // Uses accessToken (not idToken) and privateKeyHex from test-login response
590→  // (not the CLI dev key) so the keypair matches the user record.
591→  await invoke('handle_test_login_complete', {
592→    accessToken: data.accessToken,
593→    refreshToken: data.refreshToken,
594→    privateKeyHex: data.privateKeyHex,
595→    isNewUser: data.isNewUser,
596→  });
597→}
