 107→  if (TEXT_EXTENSIONS.has(lower)) return true;
 108→  // Handle well-known extensionless filenames like Dockerfile, Makefile
 109→  if (TEXT_FILENAMES.has(lower)) return true;
 110→  const lastDot = lower.lastIndexOf('.');
 111→  if (lastDot === -1) return false;
 112→  return TEXT_EXTENSIONS.has(lower.slice(lastDot));
 113→}
 114→
 115→/** Extensions recognized as previewable image files. */
 116→const IMAGE_EXTENSIONS = new Set([
 117→  '.png',
 118→  '.jpg',
 119→  '.jpeg',
 120→  '.gif',
 121→  '.webp',
 122→  '.svg',
 123→  '.bmp',
 124→  '.ico',
 125→  '.avif',
 126→]);
 127→
 128→/**
 129→ * Check if a filename has a previewable image extension.
 130→ */
 131→function isImageFile(name: string): boolean {
 132→  const lower = name.toLowerCase();
 133→  const lastDot = lower.lastIndexOf('.');
 134→  if (lastDot === -1) return false;
 135→  return IMAGE_EXTENSIONS.has(lower.slice(lastDot));
 136→}
 137→
 138→/** Extensions recognized as PDF files. */
 139→const PDF_EXTENSIONS = new Set(['.pdf']);
 140→
 141→/** Extensions recognized as playable audio files. */
 142→const AUDIO_EXTENSIONS = new Set(['.mp3', '.wav', '.ogg', '.m4a', '.flac']);
 143→
 144→/** Extensions recognized as playable video files. */
 145→const VIDEO_EXTENSIONS = new Set(['.mp4', '.webm', '.mov', '.mkv']);
 146→
 147→/**
 148→ * Check if a filename has a PDF extension.
 149→ */
 150→function isPdfFile(name: string): boolean {
 151→  const lower = name.toLowerCase();
 152→  const lastDot = lower.lastIndexOf('.');
 153→  if (lastDot === -1) return false;
 154→  return PDF_EXTENSIONS.has(lower.slice(lastDot));
 155→}
 156→
 157→/**
 158→ * Check if a filename has a playable audio extension.
 159→ */
 160→function isAudioFile(name: string): boolean {
 161→  const lower = name.toLowerCase();
 162→  const lastDot = lower.lastIndexOf('.');
 163→  if (lastDot === -1) return false;
 164→  return AUDIO_EXTENSIONS.has(lower.slice(lastDot));
 165→}
 166→
 167→/**
 168→ * Check if a filename has a playable video extension.
 169→ */
 170→function isVideoFile(name: string): boolean {
 171→  const lower = name.toLowerCase();
 172→  const lastDot = lower.lastIndexOf('.');
 173→  if (lastDot === -1) return false;
 174→  return VIDEO_EXTENSIONS.has(lower.slice(lastDot));
 175→}
 176→
 177→/**
 178→ * Check if a filename is any previewable type (image, PDF, audio, video).
 179→ */
 180→function isPreviewableFile(name: string): boolean {
 181→  return isImageFile(name) || isPdfFile(name) || isAudioFile(name) || isVideoFile(name);
 182→}
 183→
 184→/**
 185→ * Dialog state for rename/delete operations.
 186→ */
 187→type DialogState = {
 188→  open: boolean;
 189→  item: FolderChild | null;
 190→};
 191→
 192→/**
 193→ * Main file browser container component.
 194→ *
 195→ * Manages the file list area with in-place navigation.
 196→ * Selection state, context menu, and file/folder actions.
 197→ *
 198→ * Navigation:
 199→ * - Double-click folders to navigate into them
 200→ * - Click [..] PARENT_DIR row to navigate up
 201→ * - Browser history integration via URL routing
 202→ *
 203→ * Actions:
 204→ * - Download: Downloads file from IPFS, decrypts, and triggers browser download
 205→ * - Rename: Opens dialog to rename file/folder
 206→ * - Delete: Shows confirmation, then removes from folder metadata
 207→ * - Move: Drag-drop to folder rows in list
 208→ *
 209→ * @example
 210→ * ```tsx
 211→ * function FilesPage() {
 212→ *   return (
 213→ *     <main className="app-main">
 214→ *       <FileBrowser />
 215→ *     </main>
 216→ *   );
 217→ * }
 218→ * ```
 219→ */
 220→
 221→export function FileBrowser() {
 222→  // Navigation state from hook
 223→  const { currentFolderId, currentFolder, breadcrumbs, isLoading, navigateTo, navigateUp } =
 224→    useFolderNavigation();
 225→
 226→  // Folder operations
 227→  const {
 228→    createFolder,
 229→    renameItem,
 230→    moveItem,
 231→    moveItems,
 232→    deleteItem,
 233→    deleteItems,
 234→    isLoading: isOperating,
 235→  } = useFolder();
 236→
 237→  // File download
 238→  const { downloadFromIpns, isDownloading } = useFileDownload();
 239→
 240→  // External file drop upload
 241→  const { handleFileDrop } = useDropUpload();
 242→
 243→  // Context menu state
 244→  const contextMenu = useContextMenu();
 245→
 246→  // Vault and folder stores for sync
 247→  const { rootIpnsName } = useVaultStore();
 248→  const initialSyncComplete = useSyncStore((state) => state.initialSyncComplete);
 249→  const syncStatus = useSyncStore((state) => state.status);
 250→
 251→  // Sync callback - compare remote sequence with local, refresh if different
 252→  const handleSync = useCallback(async () => {
 253→    if (!rootIpnsName) return;
 254→
 255→    // Resolve root folder IPNS to get remote CID and sequence number
 256→    // Note: resolveIpnsRecord returns null when the record definitively
 257→    // doesn't exist (backend 404 / success:false). Transient failures
 258→    // (network errors, 5xx) throw instead, which useSyncPolling catches.
 259→    const resolved = await resolveIpnsRecord(rootIpnsName);
 260→    if (!resolved) {
 261→      // No IPNS record found. This is expected for vaults that have
 262→      // never published (empty vault, no uploads yet). Return normally
 263→      // so useSyncPolling marks initial sync complete and shows the
 264→      // empty file browser instead of retrying forever.
 265→      return;
 266→    }
 267→
 268→    // Read fresh state from store — avoid stale closure from render cycle.
 269→    // On initial load, the root folder may not be in the closure's `folders`
 270→    // yet because useFolderNavigation's useEffect sets it in the same commit.
 271→    const rootFolder = useFolderStore.getState().folders['root'];
 272→    if (!rootFolder) return;
 273→
 274→    // Compare sequence numbers - if remote > local, we need to refresh
 275→    // Sequence number comparison is more reliable than CID since we
 276→    // don't cache the local CID, and sequence always increments
 277→    if (resolved.sequenceNumber <= rootFolder.sequenceNumber) {
 278→      // No changes, already up to date
 279→      return;
 280→    }
 281→
 282→    // Remote has newer version - fetch and decrypt new metadata
 283→    try {
 284→      const metadata = await fetchAndDecryptMetadata(resolved.cid, rootFolder.folderKey);
 285→
 286→      // Update folder store with new children
 287→      // Per CONTEXT.md: last write wins, instant refresh (no toast/prompt)
 288→      useFolderStore.getState().updateFolderChildren('root', metadata.children);
 289→      useFolderStore.getState().updateFolderSequence('root', resolved.sequenceNumber);
 290→    } catch (err) {
 291→      console.error('Sync refresh failed:', err);
 292→      // During initial sync, propagate so useSyncPolling keeps the
 293→      // loading UI visible and retries instead of showing empty state
 294→      if (!useSyncStore.getState().initialSyncComplete) {
 295→        throw err;
 296→      }
 297→    }
 298→  }, [rootIpnsName]);
 299→
 300→  // Start sync polling (30s interval, pauses when backgrounded/offline)
 301→  useSyncPolling(handleSync);
 302→
 303→  // Device registry polling (60s interval, independent from vault sync)
 304→  useDeviceRegistrySync();
 305→
 306→  // External drag state — tracks when files from OS are being dragged over
 307→  const [isDraggingExternal, setIsDraggingExternal] = useState(false);
 308→  const dragCounterRef = useRef(0);
 309→
 310→  /**
 311→   * Detect external file drag entering the content area.
 312→   * Uses a counter to handle nested dragenter/dragleave events correctly.
 313→   */
 314→  const handleContentDragEnter = useCallback((e: DragEvent) => {
 315→    if (isExternalFileDrag(e.dataTransfer)) {
 316→      dragCounterRef.current += 1;
 317→      if (dragCounterRef.current === 1) {
 318→        setIsDraggingExternal(true);
 319→      }
 320→    }
 321→  }, []);
 322→
 323→  const handleContentDragOver = useCallback((e: DragEvent) => {
 324→    if (isExternalFileDrag(e.dataTransfer)) {
 325→      e.preventDefault();
 326→      e.dataTransfer.dropEffect = 'copy';
 327→    }
 328→  }, []);
 329→
 330→  const handleContentDragLeave = useCallback((e: DragEvent) => {
 331→    if (isExternalFileDrag(e.dataTransfer)) {
 332→      dragCounterRef.current -= 1;
 333→      if (dragCounterRef.current <= 0) {
 334→        dragCounterRef.current = 0;
 335→        setIsDraggingExternal(false);
 336→      }
 337→    }
 338→  }, []);
 339→
 340→  // Reset drag state if user abandons drag outside the window (e.g. drops on desktop
 341→  // or presses Escape). Without this the counter/overlay can get stuck.
 342→  useEffect(() => {
 343→    const resetDragState = () => {
 344→      dragCounterRef.current = 0;
 345→      setIsDraggingExternal(false);
 346→    };
 347→    const handleWindowDrop = (e: Event) => {
 348→      e.preventDefault();
 349→      resetDragState();
 350→    };
 351→    const handleWindowDragLeave = (e: globalThis.DragEvent) => {
 352→      // relatedTarget is null when the drag leaves the browser window entirely
 353→      if (!e.relatedTarget) {
 354→        resetDragState();
 355→      }
 356→    };
 357→    window.addEventListener('dragend', resetDragState);
 358→    window.addEventListener('drop', handleWindowDrop);
 359→    window.addEventListener('dragleave', handleWindowDragLeave as EventListener);
 360→    return () => {
 361→      window.removeEventListener('dragend', resetDragState);
 362→      window.removeEventListener('drop', handleWindowDrop);
 363→      window.removeEventListener('dragleave', handleWindowDragLeave as EventListener);
 364→    };
 365→  }, []);
 366→
 367→  /**
 368→   * Catch-all drop handler for the content area.
 369→   * If files from OS are dropped on general area (not intercepted by a child),
 370→   * upload them to the current folder.
 371→   */
 372→  const handleContentDrop = useCallback(
 373→    (e: DragEvent) => {
 374→      dragCounterRef.current = 0;
 375→      setIsDraggingExternal(false);
 376→
 377→      // Only handle external file drops
 378→      if (!e.dataTransfer.files || e.dataTransfer.files.length === 0) return;
 379→      if (!isExternalFileDrag(e.dataTransfer)) return;
 380→
 381→      e.preventDefault();
 382→      const files = Array.from(e.dataTransfer.files);
 383→      handleFileDrop(files, currentFolderId);
 384→    },
 385→    [handleFileDrop, currentFolderId]
 386→  );
 387→
 388→  /**
 389→   * Handle external file drops targeting a specific folder.
 390→   * Called by FileListItem (folder rows) and Breadcrumbs.
 391→   */
 392→  const handleExternalFileDrop = useCallback(
 393→    (files: File[], targetFolderId: string) => {
 394→      dragCounterRef.current = 0;
 395→      setIsDraggingExternal(false);
 396→      handleFileDrop(files, targetFolderId);
 397→    },
 398→    [handleFileDrop]
 399→  );
 400→
 401→  // Get current folder's children (needed early for selection logic)
 402→  const children = currentFolder?.children ?? [];
 403→  const hasChildren = children.length > 0;
 404→
 405→  // Multi-selection state
 406→  const [selectedIds, setSelectedIds] = useState<Set<string>>(new Set());
 407→  const lastSelectedIdRef = useRef<string | null>(null);
 408→
 409→  // Prune stale selectedIds when children change (sync refresh, upload, external delete)
 410→  const childIds = useMemo(() => new Set(children.map((c) => c.id)), [children]);
 411→  useEffect(() => {
 412→    setSelectedIds((prev) => {
 413→      if (prev.size === 0) return prev;
 414→      const pruned = new Set([...prev].filter((id) => childIds.has(id)));
 415→      if (pruned.size === prev.size) return prev;
 416→      return pruned;
 417→    });
 418→  }, [childIds]);
 419→
 420→  // Compute selected items from IDs for action bar and batch operations
 421→  const selectedItems = useMemo(
 422→    () => children.filter((c) => selectedIds.has(c.id)),
 423→    [children, selectedIds]
 424→  );
 425→  const multiSelectActive = selectedIds.size > 0;
 426→
 427→  // Dialog states
 428→  const [confirmDialog, setConfirmDialog] = useState<DialogState>({ open: false, item: null });
 429→  const [renameDialog, setRenameDialog] = useState<DialogState>({ open: false, item: null });
 430→  const [moveDialog, setMoveDialog] = useState<DialogState>({ open: false, item: null });
 431→  const [detailsDialog, setDetailsDialog] = useState<DialogState>({ open: false, item: null });
 432→  const [editorDialog, setEditorDialog] = useState<DialogState>({ open: false, item: null });
 433→  const [imagePreviewDialog, setImagePreviewDialog] = useState<DialogState>({
 434→    open: false,
 435→    item: null,
 436→  });
 437→  const [pdfPreviewDialog, setPdfPreviewDialog] = useState<DialogState>({
 438→    open: false,
 439→    item: null,
 440→  });
 441→  const [audioPlayerDialog, setAudioPlayerDialog] = useState<DialogState>({
 442→    open: false,
 443→    item: null,
 444→  });
 445→  const [videoPlayerDialog, setVideoPlayerDialog] = useState<DialogState>({
 446→    open: false,
 447→    item: null,
 448→  });
 449→  const [createFolderDialogOpen, setCreateFolderDialogOpen] = useState(false);
 450→
 451→  // Clear selection when navigating to a new folder
 452→  const handleNavigate = useCallback(
 453→    (folderId: string) => {
 454→      setSelectedIds(new Set());
 455→      lastSelectedIdRef.current = null;
 456→      navigateTo(folderId);
 457→    },
 458→    [navigateTo]
 459→  );
 460→
 461→  /**
 462→   * Handle item selection with modifier keys.
 463→   * - Plain click: single select (clears others)
 464→   * - Ctrl/Cmd+click: toggle selection
 465→   * - Shift+click: range select from last clicked to current
 466→   */
 467→  const handleSelect = useCallback(
 468→    (itemId: string, event: { ctrlKey: boolean; shiftKey: boolean; metaKey: boolean }) => {
 469→      const isCtrl = event.ctrlKey || event.metaKey;
 470→      const isShift = event.shiftKey;
 471→
 472→      if (isShift && lastSelectedIdRef.current) {
 473→        // Range select: select all items between lastSelected and current
 474→        // Use sorted children order so the range respects visual order
 475→        const sortedChildren = [...children].sort((a, b) => {
 476→          if (a.type === 'folder' && b.type !== 'folder') return -1;
 477→          if (a.type !== 'folder' && b.type === 'folder') return 1;
 478→          return a.name.localeCompare(b.name, undefined, { sensitivity: 'base' });
 479→        });
 480→
 481→        const ids = sortedChildren.map((c) => c.id);
 482→        const startIdx = ids.indexOf(lastSelectedIdRef.current);
 483→        const endIdx = ids.indexOf(itemId);
 484→
 485→        if (startIdx !== -1 && endIdx !== -1) {
 486→          const rangeStart = Math.min(startIdx, endIdx);
 487→          const rangeEnd = Math.max(startIdx, endIdx);
 488→          const rangeIds = ids.slice(rangeStart, rangeEnd + 1);
 489→
 490→          setSelectedIds((prev) => {
 491→            const next = new Set(prev);
 492→            for (const id of rangeIds) {
 493→              next.add(id);
 494→            }
 495→            return next;
 496→          });
 497→        }
 498→      } else if (isCtrl) {
 499→        // Toggle: add or remove from selection
 500→        setSelectedIds((prev) => {
 501→          const next = new Set(prev);
 502→          if (next.has(itemId)) {
 503→            next.delete(itemId);
 504→          } else {
 505→            next.add(itemId);
 506→          }
 507→          return next;
 508→        });
 509→        lastSelectedIdRef.current = itemId;
 510→      } else {
 511→        // Plain click: single select
 512→        setSelectedIds(new Set([itemId]));
 513→        lastSelectedIdRef.current = itemId;
 514→      }
 515→    },
 516→    [children]
 517→  );
 518→
 519→  // Select/deselect all items
 520→  const handleSelectAll = useCallback(() => {
 521→    if (selectedIds.size === children.length) {
 522→      // All selected -> deselect all
 523→      setSelectedIds(new Set());
 524→      lastSelectedIdRef.current = null;
 525→    } else {
 526→      // Select all
 527→      setSelectedIds(new Set(children.map((c) => c.id)));
 528→    }
 529→  }, [children, selectedIds.size]);
 530→
 531→  // Clear selection
 532→  const clearSelection = useCallback(() => {
 533→    setSelectedIds(new Set());
 534→    lastSelectedIdRef.current = null;
 535→  }, []);
 536→
 537→  // Context menu handler - show context menu
 538→  // If right-clicked item is not in selection, select only that item
 539→  const handleContextMenu = useCallback(
 540→    (event: MouseEvent, item: FolderChild) => {
 541→      if (!selectedIds.has(item.id)) {
 542→        setSelectedIds(new Set([item.id]));
 543→        lastSelectedIdRef.current = item.id;
 544→      }
 545→      contextMenu.show(event, item);
 546→    },
 547→    [contextMenu, selectedIds]
 548→  );
 549→
 550→  // Drag start handler
 551→  const handleDragStart = useCallback((_event: DragEvent, _item: FolderChild) => {
 552→    // Drag data is set by FileListItem component
 553→  }, []);
 554→
 555→  // Drop on folder handler (supports single and multi-item drops)
 556→  const handleDropOnFolder = useCallback(
 557→    async (
 558→      items: Array<{ id: string; type: 'file' | 'folder' }>,
 559→      sourceParentId: string,
 560→      destFolderId: string
 561→    ) => {
 562→      try {
 563→        if (items.length === 1) {
 564→          await moveItem(items[0].id, items[0].type, sourceParentId, destFolderId);
 565→        } else {
 566→          await moveItems(items, sourceParentId, destFolderId);
 567→          clearSelection();
 568→        }
 569→      } catch (err) {
 570→        console.error('Move failed:', err);
 571→      }
 572→    },
 573→    [moveItem, moveItems, clearSelection]
 574→  );
 575→
 576→  // Download action handler (v2: resolves file IPNS for CID/key)
 577→  const handleDownload = useCallback(async () => {
 578→    const item = contextMenu.item;
 579→    if (!item || !isFilePointer(item)) return;
 580→
 581→    const folderKey = currentFolder?.folderKey;
 582→    if (!folderKey) return;
 583→
 584→    try {
 585→      await downloadFromIpns({
 586→        fileMetaIpnsName: item.fileMetaIpnsName,
 587→        folderKey,
 588→        fileName: item.name,
 589→      });
 590→    } catch (err) {
 591→      console.error('Download failed:', err);
 592→    }
 593→  }, [contextMenu.item, downloadFromIpns, currentFolder?.folderKey]);
 594→
 595→  // Open rename dialog
 596→  const handleRenameClick = useCallback(() => {
 597→    if (contextMenu.item) {
 598→      setRenameDialog({ open: true, item: contextMenu.item });
 599→    }
 600→  }, [contextMenu.item]);
 601→
 602→  // Open delete confirmation dialog
 603→  const handleDeleteClick = useCallback(() => {
 604→    if (contextMenu.item) {
 605→      setConfirmDialog({ open: true, item: contextMenu.item });
 606→    }
 607→  }, [contextMenu.item]);
 608→
 609→  // Open move dialog
 610→  const handleMoveClick = useCallback(() => {
 611→    if (contextMenu.item) {
 612→      setMoveDialog({ open: true, item: contextMenu.item });
 613→    }
 614→  }, [contextMenu.item]);
 615→
 616→  // Open details dialog
 617→  const handleDetailsClick = useCallback(() => {
 618→    if (contextMenu.item) {
 619→      setDetailsDialog({ open: true, item: contextMenu.item });
 620→    }
 621→  }, [contextMenu.item]);
 622→
 623→  // Open text editor dialog
 624→  const handleEditClick = useCallback(() => {
 625→    if (contextMenu.item) {
 626→      setEditorDialog({ open: true, item: contextMenu.item });
 627→    }
 628→  }, [contextMenu.item]);
 629→
 630→  // Open preview dialog (routes to correct dialog based on file type)
 631→  const handlePreviewClick = useCallback(() => {
 632→    const item = contextMenu.item;
 633→    if (!item || !isFilePointer(item)) return;
 634→    const name = item.name;
 635→    if (isImageFile(name)) {
 636→      setImagePreviewDialog({ open: true, item });
 637→    } else if (isPdfFile(name)) {
 638→      setPdfPreviewDialog({ open: true, item });
 639→    } else if (isAudioFile(name)) {
 640→      setAudioPlayerDialog({ open: true, item });
 641→    } else if (isVideoFile(name)) {
 642→      setVideoPlayerDialog({ open: true, item });
 643→    }
 644→  }, [contextMenu.item]);
 645→
 646→  // --- Batch action handlers ---
 647→
 648→  // Batch delete state: stores multiple items for batch confirmation
 649→  const [batchDeleteDialog, setBatchDeleteDialog] = useState<{
 650→    open: boolean;
 651→    items: FolderChild[];
 652→  }>({ open: false, items: [] });
 653→
 654→  // Batch move state: stores items for batch move dialog
 655→  const [batchMoveDialog, setBatchMoveDialog] = useState<{
 656→    open: boolean;
 657→    items: FolderChild[];
 658→  }>({ open: false, items: [] });
 659→
 660→  // Open batch delete confirmation
 661→  const handleBatchDeleteClick = useCallback(() => {
 662→    if (selectedItems.length === 0) return;
 663→    setBatchDeleteDialog({ open: true, items: [...selectedItems] });
 664→  }, [selectedItems]);
 665→
 666→  // Open batch move dialog (uses first selected item for MoveDialog, moves all)
 667→  const handleBatchMoveClick = useCallback(() => {
 668→    if (selectedItems.length === 0) return;
 669→    setBatchMoveDialog({ open: true, items: [...selectedItems] });
 670→  }, [selectedItems]);
 671→
 672→  // Batch download: download all selected files sequentially (v2: IPNS-based)
 673→  const handleBatchDownload = useCallback(async () => {
 674→    const files = selectedItems.filter(isFilePointer);
 675→    if (files.length === 0) return;
 676→
 677→    const folderKey = currentFolder?.folderKey;
 678→    if (!folderKey) return;
 679→
 680→    for (const file of files) {
 681→      try {
 682→        await downloadFromIpns({
 683→          fileMetaIpnsName: file.fileMetaIpnsName,
 684→          folderKey,
 685→          fileName: file.name,
 686→        });
 687→      } catch (err) {
 688→        console.error(`Download failed for ${file.name}:`, err);
 689→      }
 690→    }
 691→  }, [selectedItems, downloadFromIpns, currentFolder?.folderKey]);
 692→
 693→  // Confirm batch delete - single IPNS publish for entire batch
 694→  const handleBatchDeleteConfirm = useCallback(async () => {
 695→    const items = batchDeleteDialog.items;
 696→    if (items.length === 0) return;
 697→
 698→    try {
 699→      await deleteItems(
 700→        items.map((i) => ({ id: i.id, type: i.type })),
 701→        currentFolderId
 702→      );
 703→      setBatchDeleteDialog({ open: false, items: [] });
 704→      clearSelection();
 705→    } catch (err) {
 706→      console.error('Batch delete failed:', err);
 707→    }
 708→  }, [batchDeleteDialog.items, deleteItems, currentFolderId, clearSelection]);
 709→
 710→  // Confirm batch move - single IPNS publish for source + destination
 711→  const handleBatchMoveConfirm = useCallback(
 712→    async (destinationFolderId: string) => {
 713→      const items = batchMoveDialog.items;
 714→      if (items.length === 0) return;
 715→
 716→      try {
 717→        await moveItems(
 718→          items.map((i) => ({ id: i.id, type: i.type })),
 719→          currentFolderId,
 720→          destinationFolderId
 721→        );
 722→        setBatchMoveDialog({ open: false, items: [] });
 723→        clearSelection();
 724→      } catch (err) {
 725→        console.error('Batch move failed:', err);
 726→      }
 727→    },
 728→    [batchMoveDialog.items, moveItems, currentFolderId, clearSelection]
 729→  );
 730→
 731→  // Close batch dialogs
 732→  const closeBatchDeleteDialog = useCallback(() => {
 733→    setBatchDeleteDialog({ open: false, items: [] });
 734→  }, []);
 735→
 736→  const closeBatchMoveDialog = useCallback(() => {
 737→    setBatchMoveDialog({ open: false, items: [] });
 738→  }, []);
 739→
 740→  // Confirm rename
 741→  const handleRenameConfirm = useCallback(
 742→    async (newName: string) => {
 743→      const item = renameDialog.item;
 744→      if (!item) return;
 745→
 746→      try {
 747→        await renameItem(item.id, item.type, newName, currentFolderId);
 748→        setRenameDialog({ open: false, item: null });
 749→      } catch (err) {
 750→        console.error('Rename failed:', err);
 751→      }
 752→    },
 753→    [renameDialog.item, renameItem, currentFolderId]
 754→  );
