  1→import { Fragment, useState, useCallback, type DragEvent, type KeyboardEvent } from 'react';
  2→import type { Breadcrumb } from '../../hooks/useFolderNavigation';
  3→import { isExternalFileDrag } from '../../hooks/useDropUpload';
  4→
  5→type BreadcrumbsProps = {
  6→  /** Breadcrumb trail from root to current folder */
  7→  breadcrumbs: Breadcrumb[];
  8→  /** Callback to navigate to a folder */
  9→  onNavigate: (folderId: string) => void;
 10→  /** @deprecated Callback to navigate up - use onNavigate with parent ID instead */
 11→  onNavigateUp?: () => void;
 12→  /** Callback when items are dropped onto a breadcrumb segment */
 13→  onDrop?: (
 14→    items: Array<{ id: string; type: 'file' | 'folder' }>,
 15→    sourceParentId: string,
 16→    destFolderId: string
 17→  ) => void;
 18→  /** Callback when external files are dropped onto a breadcrumb segment */
 19→  onExternalFileDrop?: (files: File[], destFolderId: string) => void;
 20→};
 21→
 22→/**
 23→ * Interactive breadcrumb navigation component.
 24→ *
 25→ * Features:
 26→ * - Clickable segments to navigate to any parent folder
 27→ * - Drop targets on each segment for quick moves to parent folders
 28→ * - Terminal-style path format: ~/root/documents/projects
 29→ *
 30→ * @example
 31→ * ```tsx
 32→ * function FileBrowser() {
 33→ *   const { breadcrumbs, navigateTo, navigateUp } = useFolderNavigation();
 34→ *   const { moveItem } = useFolder();
 35→ *
 36→ *   const handleDrop = (sourceId, sourceType, sourceParentId, destFolderId) => {
 37→ *     moveItem(sourceId, sourceType, sourceParentId, destFolderId);
 38→ *   };
 39→ *
 40→ *   return (
 41→ *     <Breadcrumbs
 42→ *       breadcrumbs={breadcrumbs}
 43→ *       onNavigate={navigateTo}
 44→ *       onNavigateUp={navigateUp}
 45→ *       onDrop={handleDrop}
 46→ *     />
 47→ *   );
 48→ * }
 49→ * ```
 50→ */
 51→export function Breadcrumbs({
 52→  breadcrumbs,
 53→  onNavigate,
 54→  onDrop,
 55→  onExternalFileDrop,
 56→}: BreadcrumbsProps) {
 57→  // Track which breadcrumb is being dragged over
 58→  const [dragOverId, setDragOverId] = useState<string | null>(null);
 59→
 60→  /**
 61→   * Handle click on breadcrumb segment.
 62→   */
 63→  const handleClick = useCallback(
 64→    (folderId: string) => {
 65→      onNavigate(folderId);
 66→    },
 67→    [onNavigate]
 68→  );
 69→
 70→  /**
 71→   * Handle keyboard navigation.
 72→   */
 73→  const handleKeyDown = useCallback(
 74→    (event: KeyboardEvent, folderId: string) => {
 75→      if (event.key === 'Enter' || event.key === ' ') {
 76→        event.preventDefault();
 77→        onNavigate(folderId);
 78→      }
 79→    },
 80→    [onNavigate]
 81→  );
 82→
 83→  /**
 84→   * Handle drag over breadcrumb segment.
 85→   * Accepts both internal drags (move) and external file drags (upload).
 86→   */
 87→  const handleDragOver = useCallback(
 88→    (e: DragEvent, folderId: string) => {
 89→      if (!onDrop && !onExternalFileDrop) return;
 90→      e.preventDefault();
 91→      e.stopPropagation();
 92→
 93→      e.dataTransfer.dropEffect = isExternalFileDrag(e.dataTransfer) ? 'copy' : 'move';
 94→      setDragOverId(folderId);
 95→    },
 96→    [onDrop, onExternalFileDrop]
 97→  );
 98→
 99→  /**
100→   * Handle drag leave breadcrumb segment.
101→   */
102→  const handleDragLeave = useCallback((e: DragEvent) => {
103→    e.preventDefault();
104→    e.stopPropagation();
105→    setDragOverId(null);
106→  }, []);
107→
108→  /**
109→   * Handle drop on breadcrumb segment.
110→   * Routes external files to upload, internal items to move.
111→   */
112→  const handleDrop = useCallback(
113→    (e: DragEvent, destFolderId: string) => {
114→      e.preventDefault();
115→      e.stopPropagation();
116→      setDragOverId(null);
117→
118→      // Read JSON once — empty string means external file drag from OS
119→      const jsonData = e.dataTransfer.getData('application/json');
120→
121→      if (!jsonData) {
122→        // External file drop
123→        if (e.dataTransfer.files.length > 0 && onExternalFileDrop) {
124→          const files = Array.from(e.dataTransfer.files);
125→          onExternalFileDrop(files, destFolderId);
126→        }
127→        return;
128→      }
129→
130→      // Internal move operation
131→      if (!onDrop) return;
132→
133→      try {
134→        const parsed = JSON.parse(jsonData) as {
135→          items: Array<{ id: string; type: 'file' | 'folder' }>;
136→          parentId: string;
137→        };
138→
139→        // Filter out items that are the dest folder or already in it
140→        const validItems = parsed.items.filter(
141→          (i) => i.id !== destFolderId && parsed.parentId !== destFolderId
142→        );
143→        if (validItems.length === 0) return;
144→
145→        onDrop(validItems, parsed.parentId, destFolderId);
146→      } catch {
147→        // Invalid drag data, ignore
148→      }
149→    },
150→    [onDrop, onExternalFileDrop]
151→  );
152→
153→  // Handle empty breadcrumbs
154→  if (breadcrumbs.length === 0) {
155→    return (
156→      <nav className="breadcrumb-nav" aria-label="Current location" data-testid="breadcrumbs">
157→        <span className="breadcrumb-prefix">~</span>
158→        <span className="breadcrumb-separator">/</span>
159→        <span className="breadcrumb-text">root</span>
160→      </nav>
161→    );
162→  }
163→
164→  return (
165→    <nav className="breadcrumb-nav" aria-label="Current location" data-testid="breadcrumbs">
166→      <span className="breadcrumb-prefix">~</span>
167→      <span className="breadcrumb-separator">/</span>
168→      {breadcrumbs.map((crumb, index) => {
169→        const isLast = index === breadcrumbs.length - 1;
170→        const isDragOver = dragOverId === crumb.id;
171→
172→        return (
173→          <Fragment key={crumb.id}>
174→            <button
175→              type="button"
176→              className={`breadcrumb-item ${isDragOver ? 'breadcrumb-item--drag-over' : ''} ${isLast ? 'breadcrumb-item--current' : ''}`}
177→              onClick={() => handleClick(crumb.id)}
178→              onKeyDown={(e) => handleKeyDown(e, crumb.id)}
179→              onDragOver={
180→                onDrop || onExternalFileDrop ? (e) => handleDragOver(e, crumb.id) : undefined
181→              }
182→              onDragLeave={onDrop || onExternalFileDrop ? handleDragLeave : undefined}
183→              onDrop={onDrop || onExternalFileDrop ? (e) => handleDrop(e, crumb.id) : undefined}
184→              aria-current={isLast ? 'page' : undefined}
185→              data-folder-id={crumb.id}
186→            >
187→              {crumb.name.toLowerCase()}
188→            </button>
189→            {!isLast && <span className="breadcrumb-separator">/</span>}
190→          </Fragment>
191→        );
192→      })}
193→    </nav>
194→  );
195→}
