  1→import { useCallback, useEffect, useRef } from 'react';
  2→import { useInterval } from './useInterval';
  3→import { useVisibility } from './useVisibility';
  4→import { useOnlineStatus } from './useOnlineStatus';
  5→import { useSyncStore } from '../stores/sync.store';
  6→import { useVaultStore } from '../stores/vault.store';
  7→
  8→const SYNC_INTERVAL_MS = 30000; // 30 seconds per CONTEXT.md
  9→
 10→/**
 11→ * Orchestrates IPNS polling for multi-device sync.
 12→ *
 13→ * Behavior per CONTEXT.md:
 14→ * - Polls every 30s when tab is visible and online
 15→ * - Pauses polling when tab is backgrounded (saves battery)
 16→ * - Polls immediately when tab regains focus (per RESEARCH.md recommendation)
 17→ * - Polls immediately when coming back online
 18→ * - Updates sync store with status (syncing/success/error)
 19→ *
 20→ * @param onSync - Callback to execute sync logic (resolve IPNS, compare, refresh)
 21→ */
 22→export function useSyncPolling(onSync: () => Promise<void>): void {
 23→  const isVisible = useVisibility();
 24→  const isOnline = useOnlineStatus();
 25→  const { startSync, syncSuccess, syncFailure, completeInitialSync, setOnline } = useSyncStore();
 26→  const { rootIpnsName, isNewVault } = useVaultStore();
 27→
 28→  // Track previous states for edge detection
 29→  const prevOnline = useRef(isOnline);
 30→  const prevVisible = useRef(isVisible);
 31→
 32→  // Guard against concurrent sync runs - multiple triggers can occur (interval + visibility + reconnect)
 33→  const isSyncingRef = useRef(false);
 34→
 35→  // Track whether initial sync has been attempted
 36→  const initialSyncFired = useRef(false);
 37→
 38→  // For new vaults, skip the syncing state — there's nothing to sync
 39→  useEffect(() => {
 40→    if (isNewVault && !useSyncStore.getState().initialSyncComplete) {
 41→      completeInitialSync();
 42→    }
 43→  }, [isNewVault, completeInitialSync]);
 44→
 45→  // Keep sync store's isOnline in sync
 46→  useEffect(() => {
 47→    setOnline(isOnline);
 48→  }, [isOnline, setOnline]);
 49→
 50→  // Wrapped sync callback that updates store state with concurrent execution guard
 51→  const doSync = useCallback(async () => {
 52→    if (!rootIpnsName || !isOnline) return;
 53→
 54→    // Skip if already syncing to prevent race conditions
 55→    if (isSyncingRef.current) return;
 56→    isSyncingRef.current = true;
 57→
 58→    startSync();
 59→    try {
 60→      await onSync();
 61→      syncSuccess();
 62→      // Mark initial sync complete only on success (IPNS resolved)
 63→      if (!useSyncStore.getState().initialSyncComplete) {
 64→        completeInitialSync();
 65→      }
 66→    } catch (error) {
 67→      const message = error instanceof Error ? error.message : 'Sync failed';
 68→      syncFailure(message);
 69→      // Don't mark initial sync complete on failure — will retry
 70→    } finally {
 71→      isSyncingRef.current = false;
 72→    }
 73→  }, [rootIpnsName, isOnline, onSync, startSync, syncSuccess, syncFailure, completeInitialSync]);
 74→
 75→  // Immediate sync on first mount — don't wait for the 30s interval
 76→  // Skip for new vaults: there's no IPNS record to resolve yet
 77→  useEffect(() => {
 78→    if (rootIpnsName && isOnline && !isNewVault && !initialSyncFired.current) {
 79→      initialSyncFired.current = true;
 80→      doSync();
 81→    }
 82→  }, [rootIpnsName, isOnline, isNewVault, doSync]);
 83→
 84→  // Determine polling delay: null pauses, number runs
 85→  // Per CONTEXT.md: pause when backgrounded (Claude's discretion chose pause)
 86→  const pollDelay = isVisible && isOnline ? SYNC_INTERVAL_MS : null;
 87→
 88→  // Regular interval polling
 89→  useInterval(doSync, pollDelay);
 90→
 91→  // Immediate sync on visibility regain (per RESEARCH.md recommendation)
 92→  useEffect(() => {
 93→    if (isVisible && !prevVisible.current && isOnline) {
 94→      doSync();
 95→    }
 96→    prevVisible.current = isVisible;
 97→  }, [isVisible, isOnline, doSync]);
 98→
 99→  // Immediate sync on reconnect (per CONTEXT.md)
100→  useEffect(() => {
101→    if (isOnline && !prevOnline.current) {
102→      doSync();
103→    }
104→    prevOnline.current = isOnline;
105→  }, [isOnline, doSync]);
106→}
