  1→/**
  2→ * Folder Service - Folder CRUD operations with encryption
  3→ *
  4→ * Handles folder creation, loading, and metadata updates with
  5→ * client-side encryption using @cipherbox/crypto.
  6→ *
  7→ * Folders store slim FilePointer references. File metadata lives in per-file IPNS records.
  8→ */
  9→
 10→import {
 11→  generateEd25519Keypair,
 12→  deriveIpnsName,
 13→  generateRandomBytes,
 14→  wrapKey,
 15→  bytesToHex,
 16→  hexToBytes,
 17→  encryptFolderMetadata,
 18→  decryptFolderMetadata,
 19→  createIpnsRecord,
 20→  marshalIpnsRecord,
 21→  type FolderMetadata,
 22→  type EncryptedFolderMetadata,
 23→  type FolderEntry,
 24→  type FolderChild,
 25→  type FilePointer,
 26→} from '@cipherbox/crypto';
 27→import { addToIpfs, fetchFromIpfs } from '../lib/api/ipfs';
 28→import { createAndPublishIpnsRecord, resolveIpnsRecord } from './ipns.service';
 29→import { batchPublishIpnsRecords } from './ipns.service';
 30→import { useAuthStore } from '../stores/auth.store';
 31→import type { FolderNode } from '../stores/folder.store';
 32→import type { FileIpnsRecordPayload } from './file-metadata.service';
 33→
 34→/** Maximum folder nesting depth per FOLD-03 */
 35→const MAX_FOLDER_DEPTH = 20;
 36→
 37→/** Safe base64 encoding that avoids call stack overflow from spread operator */
 38→function uint8ToBase64(bytes: Uint8Array): string {
 39→  let binary = '';
 40→  for (let i = 0; i < bytes.length; i++) {
 41→    binary += String.fromCharCode(bytes[i]);
 42→  }
 43→  return btoa(binary);
 44→}
 45→
 46→/**
 47→ * Calculate the depth of a folder from root.
 48→ * Root folder has depth 0, immediate children have depth 1, etc.
 49→ *
 50→ * Used for enforcing FOLD-03 depth limit of 20.
 51→ *
 52→ * @param folderId - Folder ID to calculate depth for (null = root)
 53→ * @param folders - Current folder tree
 54→ * @returns Depth from root (0 for root)
 55→ */
 56→export function getDepth(folderId: string | null, folders: Record<string, FolderNode>): number {
 57→  if (folderId === null) return 0; // root is depth 0
 58→
 59→  let depth = 0;
 60→  let currentId: string | null = folderId;
 61→
 62→  while (currentId !== null) {
 63→    const folder: FolderNode | undefined = folders[currentId];
 64→    if (!folder) break;
 65→    depth++;
 66→    currentId = folder.parentId;
 67→  }
 68→
 69→  return depth;
 70→}
 71→
 72→/**
 73→ * Load a folder's metadata from IPNS.
 74→ *
 75→ * Resolves the folder's IPNS name to get the current metadata CID,
 76→ * fetches and decrypts the metadata, and returns a complete FolderNode.
 77→ * Returns v2 metadata with FolderChild[] children.
 78→ *
 79→ * IMPORTANT: Does NOT eagerly resolve per-file IPNS records during folder load.
 80→ * File metadata is lazy-loaded on download/preview (Pitfall 1 from research).
 81→ *
 82→ * @param folderId - Folder ID (null for root, or UUID)
 83→ * @param folderKey - Decrypted AES-256 key for this folder
 84→ * @param ipnsPrivateKey - Decrypted Ed25519 private key for IPNS
 85→ * @param ipnsName - IPNS name for this folder
 86→ * @param parentId - Parent folder ID (null for root)
 87→ * @param name - Display name for this folder
 88→ */
 89→export async function loadFolder(
 90→  folderId: string | null,
 91→  folderKey: Uint8Array,
 92→  ipnsPrivateKey: Uint8Array,
 93→  ipnsName: string,
 94→  parentId: string | null,
 95→  name: string
 96→): Promise<FolderNode> {
 97→  // 1. Resolve IPNS to get current metadata CID
 98→  const resolved = await resolveIpnsRecord(ipnsName);
 99→
100→  // 2. If IPNS resolution returns null, return unloaded folder so it can be retried
101→  //    This handles newly-created subfolders whose IPNS hasn't propagated yet
102→  if (!resolved) {
103→    console.warn(
104→      `IPNS resolution returned null for folder "${name}" (${ipnsName}). Marking as not loaded for retry.`
105→    );
106→    return {
107→      id: folderId ?? 'root',
108→      name,
109→      ipnsName,
110→      parentId,
111→      children: [],
112→      isLoaded: false,
113→      isLoading: false,
114→      sequenceNumber: 0n,
115→      folderKey,
116→      ipnsPrivateKey,
117→    };
118→  }
119→
120→  // 3. Fetch encrypted metadata from IPFS and decrypt with folderKey
121→  const metadata = await fetchAndDecryptMetadata(resolved.cid, folderKey);
122→
123→  // 4. Return complete FolderNode with decrypted children
124→  // FilePointer objects have fileMetaIpnsName instead of inline file data.
125→  return {
126→    id: folderId ?? 'root',
127→    name,
128→    ipnsName,
129→    parentId,
130→    children: metadata.children ?? [],
131→    isLoaded: true,
132→    isLoading: false,
133→    sequenceNumber: resolved.sequenceNumber,
134→    folderKey,
135→    ipnsPrivateKey,
136→  };
137→}
138→
139→/**
140→ * Create a new subfolder with generated keys.
141→ *
142→ * Generates new Ed25519 IPNS keypair and AES-256 folder key,
143→ * wraps them with the user's public key for storage.
144→ *
145→ * @param params.parentFolderId - Parent folder ID (null for root)
146→ * @param params.name - Folder name
147→ * @param params.userPublicKey - User's secp256k1 public key for ECIES wrapping
148→ * @param params.folders - Current folder tree (for depth checking)
149→ * @returns Created folder entry and decrypted keys
150→ * @throws Error if depth limit exceeded (FOLD-03)
151→ */
152→export async function createFolder(params: {
153→  parentFolderId: string | null;
154→  name: string;
155→  userPublicKey: Uint8Array;
156→  folders: Record<string, FolderNode>;
157→}): Promise<{
158→  folder: FolderEntry;
159→  ipnsPrivateKey: Uint8Array;
160→  folderKey: Uint8Array;
161→  encryptedIpnsPrivateKey?: string;
162→  keyEpoch?: number;
163→}> {
164→  // 1. Check depth limit (FOLD-03)
165→  const parentDepth = getDepth(params.parentFolderId, params.folders);
166→  if (parentDepth >= MAX_FOLDER_DEPTH) {
167→    throw new Error(`Cannot create folder: maximum depth of ${MAX_FOLDER_DEPTH} exceeded`);
168→  }
169→
170→  // 2. Generate Ed25519 keypair for folder IPNS
171→  const ipnsKeypair = await generateEd25519Keypair();
172→  const ipnsName = await deriveIpnsName(ipnsKeypair.publicKey);
173→
174→  // 3. Generate random AES-256 folder key
175→  const folderKey = generateRandomBytes(32);
176→
177→  // 4. Wrap keys with user's public key (ECIES encryption)
178→  // The 64-byte libp2p format private key (privateKey || publicKey) is wrapped
179→  const ipnsPrivateKeyEncrypted = bytesToHex(
180→    await wrapKey(ipnsKeypair.privateKey, params.userPublicKey)
181→  );
182→  const folderKeyEncrypted = bytesToHex(await wrapKey(folderKey, params.userPublicKey));
183→
184→  // 5. TEE-02: Encrypt IPNS private key with TEE public key for republishing
185→  let encryptedIpnsPrivateKey: string | undefined;
186→  let keyEpoch: number | undefined;
187→
188→  const teeKeys = useAuthStore.getState().teeKeys;
189→  if (teeKeys?.currentPublicKey) {
190→    const teePublicKey = hexToBytes(teeKeys.currentPublicKey);
191→    const encryptedKey = await wrapKey(ipnsKeypair.privateKey, teePublicKey);
192→    encryptedIpnsPrivateKey = bytesToHex(encryptedKey);
193→    keyEpoch = teeKeys.currentEpoch;
194→  }
195→
196→  // 6. Create folder entry for parent's metadata
197→  const now = Date.now();
198→  const folder: FolderEntry = {
199→    type: 'folder',
200→    id: crypto.randomUUID(),
201→    name: params.name,
202→    ipnsName,
203→    ipnsPrivateKeyEncrypted,
204→    folderKeyEncrypted,
205→    createdAt: now,
206→    modifiedAt: now,
207→  };
208→
209→  return {
210→    folder,
211→    ipnsPrivateKey: ipnsKeypair.privateKey,
212→    folderKey,
213→    encryptedIpnsPrivateKey,
214→    keyEpoch,
215→  };
216→}
217→
218→/**
219→ * Update folder metadata and publish to IPNS (v2 format).
220→ *
221→ * Encrypts the metadata with the folder key, uploads to IPFS,
222→ * and publishes an updated IPNS record pointing to the new CID.
223→ *
224→ * @param params.folderId - Folder being updated
225→ * @param params.children - New children array (FolderChild[])
226→ * @param params.folderKey - Decrypted AES-256 folder key
227→ * @param params.ipnsPrivateKey - Decrypted Ed25519 IPNS private key
228→ * @param params.ipnsName - IPNS name for this folder
229→ * @param params.sequenceNumber - Current sequence number
230→ * @param params.encryptedIpnsPrivateKey - TEE-wrapped key (first publish only)
231→ * @param params.keyEpoch - TEE key epoch (required with encryptedIpnsPrivateKey)
232→ * @returns New CID and sequence number
233→ */
234→export async function updateFolderMetadata(params: {
235→  folderId: string;
236→  children: FolderChild[];
237→  folderKey: Uint8Array;
238→  ipnsPrivateKey: Uint8Array;
239→  ipnsName: string;
240→  sequenceNumber: bigint;
241→  encryptedIpnsPrivateKey?: string;
242→  keyEpoch?: number;
243→}): Promise<{ cid: string; newSequenceNumber: bigint }> {
244→  // 1. Create v2 folder metadata
245→  const metadata: FolderMetadata = {
246→    version: 'v2',
247→    children: params.children,
248→  };
249→
250→  // 2. Encrypt metadata with folder key
251→  const encrypted = await encryptFolderMetadata(metadata, params.folderKey);
252→
253→  // 3. Upload to IPFS via backend relay
254→  const blob = new Blob([JSON.stringify(encrypted)], {
255→    type: 'application/json',
256→  });
257→  const { cid } = await addToIpfs(blob);
258→
259→  // 4. Publish IPNS record pointing to new CID
260→  const newSeq = params.sequenceNumber + 1n;
261→  await createAndPublishIpnsRecord({
262→    ipnsPrivateKey: params.ipnsPrivateKey,
263→    ipnsName: params.ipnsName,
264→    metadataCid: cid,
265→    sequenceNumber: newSeq,
266→    encryptedIpnsPrivateKey: params.encryptedIpnsPrivateKey,
267→    keyEpoch: params.keyEpoch,
268→  });
269→
270→  return { cid, newSequenceNumber: newSeq };
271→}
