377→/**
378→ * Delete a folder and all its contents recursively.
379→ *
380→ * For v2 metadata: collects fileMetaIpnsName values from FilePointer children
381→ * (not CIDs, since CIDs live in per-file IPNS records, not folder metadata).
382→ * Removes the folder from the parent's metadata, publishes the update.
383→ * Returns the collected file IPNS names so the caller can resolve them for
384→ * CID unpinning and TEE unenrollment.
385→ *
386→ * @param params.folderId - ID of folder to delete
387→ * @param params.parentFolderState - Parent folder containing this folder
388→ * @param params.getFolderState - Function to get folder state by ID (for recursion)
389→ * @param params.unpinCid - Function to unpin a CID from IPFS
390→ * @throws Error if folder not found
391→ */
392→export async function deleteFolder(params: {
393→  folderId: string;
394→  parentFolderState: FolderNode;
395→  getFolderState: (id: string) => FolderNode | undefined;
396→  unpinCid: (cid: string) => Promise<void>;
397→}): Promise<string[]> {
398→  // 1. Find folder in parent's children
399→  const children = [...params.parentFolderState.children];
400→  const folderIndex = children.findIndex((c) => c.type === 'folder' && c.id === params.folderId);
401→
402→  if (folderIndex === -1) throw new Error('Folder not found');
403→
404→  // 2. Recursively collect file IPNS names for cleanup
405→  // In v2, file children are FilePointers with fileMetaIpnsName (no inline CID).
406→  // The caller must resolve each fileMetaIpnsName to get the CID for unpinning.
407→  const fileIpnsNames: string[] = [];
408→  const collectFileIpnsNames = (folderId: string) => {
409→    const folder = params.getFolderState(folderId);
410→    if (!folder) return;
411→
412→    for (const child of folder.children) {
413→      if (child.type === 'file') {
414→        const filePointer = child as FilePointer;
415→        if (filePointer.fileMetaIpnsName) {
416→          fileIpnsNames.push(filePointer.fileMetaIpnsName);
417→        }
418→      } else if (child.type === 'folder') {
419→        collectFileIpnsNames(child.id);
420→      }
421→    }
422→  };
423→
424→  collectFileIpnsNames(params.folderId);
425→
426→  // 3. Remove folder from parent's children
427→  children.splice(folderIndex, 1);
428→
429→  // 4. Update parent folder metadata and publish
430→  await updateFolderMetadata({
431→    folderId: params.parentFolderState.id,
432→    children,
433→    folderKey: params.parentFolderState.folderKey,
434→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
435→    ipnsName: params.parentFolderState.ipnsName,
436→    sequenceNumber: params.parentFolderState.sequenceNumber,
437→  });
438→
439→  // 5. File CID unpinning and TEE unenrollment deferred to caller
440→  // The caller (useFolder hook) resolves fileMetaIpnsName -> CID for unpinning.
441→  // TODO: Phase 14 should add batch unenrollment for orphaned file IPNS records.
442→
443→  return fileIpnsNames;
444→}
