446→/**
447→ * Delete a file from its parent folder (v2).
448→ *
449→ * Removes the FilePointer from the parent's metadata, publishes the update.
450→ * Returns the fileMetaIpnsName so the caller can resolve it for CID unpinning
451→ * and TEE unenrollment.
452→ *
453→ * Note: This handles folder metadata update. File CID unpinning is handled
454→ * by the caller after resolving the file metadata.
455→ *
456→ * @param params.fileId - ID of file to delete
457→ * @param params.parentFolderState - Parent folder containing this file
458→ * @returns fileMetaIpnsName for caller to handle cleanup
459→ * @throws Error if file not found
460→ */
461→export async function deleteFileFromFolder(params: {
462→  fileId: string;
463→  parentFolderState: FolderNode;
464→}): Promise<{ fileMetaIpnsName: string | undefined }> {
465→  // 1. Find file in parent's children
466→  const children = [...params.parentFolderState.children];
467→  const fileIndex = children.findIndex((c) => c.type === 'file' && c.id === params.fileId);
468→
469→  if (fileIndex === -1) throw new Error('File not found');
470→
471→  // 2. Get fileMetaIpnsName for cleanup
472→  const filePointer = children[fileIndex] as FilePointer;
473→  const fileMetaIpnsName = filePointer.fileMetaIpnsName;
474→
475→  // 3. Remove file from parent's children
476→  children.splice(fileIndex, 1);
477→
478→  // 4. Update parent folder metadata and publish
479→  await updateFolderMetadata({
480→    folderId: params.parentFolderState.id,
481→    children,
482→    folderKey: params.parentFolderState.folderKey,
483→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
484→    ipnsName: params.parentFolderState.ipnsName,
485→    sequenceNumber: params.parentFolderState.sequenceNumber,
486→  });
487→
488→  // 5. TEE unenrollment: no API endpoint available yet.
489→  // TODO: Phase 14 should expose unenrollIpns via REST API.
490→  if (fileMetaIpnsName) {
491→    console.warn(
492→      `File IPNS record ${fileMetaIpnsName} orphaned after deletion. TEE unenrollment deferred to Phase 14.`
493→    );
494→  }
495→
496→  return { fileMetaIpnsName };
497→}
