564→/**
565→ * Add multiple files to a folder after successful upload (v2 batch).
566→ *
567→ * Creates FilePointer objects for all files, checks for name collisions,
568→ * then publishes all N file IPNS records + 1 folder IPNS record via a
569→ * single batch API call.
570→ *
571→ * @param params.parentFolderState - Parent folder to add files to
572→ * @param params.files - Array of file data with pre-created IPNS records
573→ * @returns Created file pointers and new sequence number
574→ * @throws Error if any name collision exists
575→ */
576→export async function addFilesToFolder(params: {
577→  parentFolderState: FolderNode;
578→  files: Array<{
579→    fileId: string;
580→    name: string;
581→    fileIpnsRecord: FileIpnsRecordPayload;
582→  }>;
583→}): Promise<{ filePointers: FilePointer[]; newSequenceNumber: bigint }> {
584→  // 1. Build a set of existing child names for collision detection
585→  const existingNames = new Set(params.parentFolderState.children.map((c) => c.name));
586→
587→  // 2. Create FilePointer for each file, checking collisions
588→  const filePointers: FilePointer[] = [];
589→  const now = Date.now();
590→
591→  for (const file of params.files) {
592→    if (existingNames.has(file.name)) {
593→      throw new Error(`A file with name "${file.name}" already exists`);
594→    }
595→    existingNames.add(file.name);
596→
597→    const filePointer: FilePointer = {
598→      type: 'file',
599→      id: file.fileId,
600→      name: file.name,
601→      fileMetaIpnsName: file.fileIpnsRecord.ipnsName,
602→      createdAt: now,
603→      modifiedAt: now,
604→    };
605→    filePointers.push(filePointer);
606→  }
607→
608→  // 3. Build updated children array
609→  const children: FolderChild[] = [...params.parentFolderState.children, ...filePointers];
610→
611→  // 4. Build folder IPNS record
612→  const folderResult = await buildFolderIpnsRecord({
613→    children,
614→    folderKey: params.parentFolderState.folderKey,
615→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
616→    ipnsName: params.parentFolderState.ipnsName,
617→    sequenceNumber: params.parentFolderState.sequenceNumber,
618→  });
619→
620→  // 5. Batch publish: all N file IPNS records + 1 folder IPNS record (single API call)
621→  const allRecords = [
622→    ...params.files.map((f) => ({ ...f.fileIpnsRecord, recordType: 'file' as const })),
623→    folderResult.record,
624→  ];
625→  const publishResult = await batchPublishIpnsRecords(allRecords);
626→
627→  if (publishResult.totalFailed > 0) {
628→    console.error(
629→      `Batch publish partial failure: ${publishResult.totalFailed}/${publishResult.totalFailed + publishResult.totalSucceeded} records failed`
630→    );
631→    throw new Error('Failed to publish one or more IPNS records');
632→  }
633→
634→  return { filePointers, newSequenceNumber: folderResult.newSequenceNumber };
635→}
