864→/**
865→ * Rename a file within its parent folder (v2).
866→ *
867→ * Updates the FilePointer's name in the parent's metadata and publishes
868→ * an updated IPNS record for the parent folder.
869→ * File rename does NOT touch the file's own IPNS record -- only folder metadata.
870→ *
871→ * @param params.fileId - ID of file to rename
872→ * @param params.newName - New name for the file
873→ * @param params.parentFolderState - Parent folder containing this file
874→ * @throws Error if file not found or name collision exists
875→ */
876→export async function renameFile(params: {
877→  fileId: string;
878→  newName: string;
879→  parentFolderState: FolderNode;
880→}): Promise<void> {
881→  const children = [...params.parentFolderState.children];
882→  const fileIndex = children.findIndex((c) => c.type === 'file' && c.id === params.fileId);
883→
884→  if (fileIndex === -1) throw new Error('File not found');
885→
886→  // Check name collision
887→  const nameExists = children.some((c) => c.name === params.newName && c.id !== params.fileId);
888→  if (nameExists) throw new Error('An item with this name already exists');
889→
890→  const file = children[fileIndex] as FilePointer;
891→  children[fileIndex] = {
892→    ...file,
893→    name: params.newName,
894→    modifiedAt: Date.now(),
895→  };
896→
897→  await updateFolderMetadata({
898→    folderId: params.parentFolderState.id,
899→    children,
900→    folderKey: params.parentFolderState.folderKey,
901→    ipnsPrivateKey: params.parentFolderState.ipnsPrivateKey,
902→    ipnsName: params.parentFolderState.ipnsName,
903→    sequenceNumber: params.parentFolderState.sequenceNumber,
904→  });
905→}
