  19→/** Maximum folder nesting depth per FOLD-03 */
  20→const MAX_FOLDER_DEPTH = 20;
  21→
  22→/**
  23→ * State for folder operations.
  24→ */
  25→type FolderOperationState = {
  26→  isLoading: boolean;
  27→  error: string | null;
  28→};
  29→
  30→/**
  31→ * Get the root folder state from vault and folder stores.
  32→ * Root folder uses vault keys directly.
  33→ */
  34→function getRootFolderState(
  35→  vaultStore: ReturnType<typeof useVaultStore.getState>,
  36→  folders: Record<string, FolderNode>
  37→): FolderNode | null {
  38→  if (!vaultStore.rootFolderKey || !vaultStore.rootIpnsKeypair || !vaultStore.rootIpnsName) {
  39→    return null;
  40→  }
  41→
  42→  // If we have an explicit root folder in the tree, use it
  43→  const existingRoot = folders['root'];
  44→  if (existingRoot) return existingRoot;
  45→
  46→  // Otherwise construct from vault state
  47→  return {
  48→    id: 'root',
  49→    name: 'My Vault',
  50→    ipnsName: vaultStore.rootIpnsName,
  51→    parentId: null,
  52→    children: [],
  53→    isLoaded: false,
  54→    isLoading: false,
  55→    sequenceNumber: 0n,
  56→    folderKey: vaultStore.rootFolderKey,
  57→    ipnsPrivateKey: vaultStore.rootIpnsKeypair.privateKey,
  58→  };
  59→}
  60→
  61→/**
  62→ * Calculate folder depth by walking up the tree.
  63→ */
  64→function calculateFolderDepth(folderId: string, folders: Record<string, FolderNode>): number {
  65→  return folderService.getDepth(folderId, folders);
  66→}
