 477→    async (itemId: string, itemType: 'file' | 'folder', parentId: string): Promise<void> => {
 478→      setState({ isLoading: true, error: null });
 479→      try {
 480→        const folders = useFolderStore.getState().folders;
 481→        const vault = useVaultStore.getState();
 482→
 483→        // Get parent folder state
 484→        const parentFolder =
 485→          parentId === 'root' ? getRootFolderState(vault, folders) : folders[parentId];
 486→
 487→        if (!parentFolder) throw new Error('Parent folder not found');
 488→
 489→        if (itemType === 'folder') {
