 520→  /**
 521→   * Delete multiple files/folders in a single IPNS publish.
 522→   *
 523→   * Removes all items from parent's children array, collects CIDs to unpin,
 524→   * then publishes metadata and IPNS once for the entire batch.
 525→   *
 526→   * @param items - Array of { id, type } to delete
 527→   * @param parentId - Parent folder ID (all items must share the same parent)
 528→   */
 529→  const handleDeleteItems = useCallback(
 530→    async (
 531→      items: Array<{ id: string; type: 'file' | 'folder' }>,
 532→      parentId: string
 533→    ): Promise<void> => {
 534→      setState({ isLoading: true, error: null });
 535→      try {
 536→        const folders = useFolderStore.getState().folders;
 537→        const vault = useVaultStore.getState();
 538→
 539→        const parentFolder =
 540→          parentId === 'root' ? getRootFolderState(vault, folders) : folders[parentId];
 541→
 542→        if (!parentFolder) throw new Error('Parent folder not found');
 543→
 544→        const itemIds = new Set(items.map((i) => i.id));
 545→
 546→        // Collect nested folder IDs to remove from store
 547→        // In v2, file children are FilePointers (no inline CID). File IPNS/TEE
 548→        // enrollments will expire naturally (24h IPNS lifetime). Phase 14 adds cleanup.
 549→        const folderIdsToRemove: string[] = [];
 550→
 551→        const collectFolderIds = (folderId: string) => {
 552→          folderIdsToRemove.push(folderId);
 553→          const folder = folders[folderId];
 554→          if (!folder) return;
 555→          for (const child of folder.children) {
 556→            if (child.type === 'folder') {
 557→              collectFolderIds(child.id);
 558→            }
 559→          }
 560→        };
 561→
 562→        for (const item of items) {
 563→          if (item.type === 'folder') {
 564→            collectFolderIds(item.id);
 565→          }
 566→        }
 567→
 568→        // Remove all items from parent's children in one pass
 569→        const updatedChildren = parentFolder.children.filter((c) => !itemIds.has(c.id));
 570→
 571→        // Single IPNS publish for the entire batch
 572→        await folderService.updateFolderMetadata({
 573→          folderId: parentFolder.id,
 574→          children: updatedChildren,
 575→          folderKey: parentFolder.folderKey,
 576→          ipnsPrivateKey: parentFolder.ipnsPrivateKey,
 577→          ipnsName: parentFolder.ipnsName,
 578→          sequenceNumber: parentFolder.sequenceNumber,
 579→        });
 580→
 581→        // Update local state — remove all nested folders from store
 582→        const store = useFolderStore.getState();
