 759→  /**
 760→   * Update a file's content in-place (v2: re-encrypt, update file IPNS, folder untouched).
 761→   *
 762→   * Old CIDs are preserved as version history (VER-01). Only pruned versions
 763→   * (excess beyond max 10) have their CIDs unpinned.
 764→   *
 765→   * @param parentId - Parent folder ID ('root' or folder UUID)
 766→   * @param fileData - New file data after re-encryption
 767→   * @returns Resolves when the update is complete
 768→   */
 769→  const handleUpdateFile = useCallback(
 770→    async (
 771→      parentId: string,
 772→      fileData: {
 773→        fileId: string;
 774→        newCid: string;
 775→        newFileKeyEncrypted: string;
 776→        newFileIv: string;
 777→        newSize: number;
 778→        forceVersion?: boolean; // true for web re-upload, false/undefined for text editor
 779→      }
 780→    ): Promise<void> => {
 781→      setState({ isLoading: true, error: null });
 782→      try {
 783→        const folders = useFolderStore.getState().folders;
 784→        const vault = useVaultStore.getState();
 785→        const auth = useAuthStore.getState();
 786→
 787→        if (!auth.vaultKeypair) {
 788→          throw new Error('No ECIES keypair available - please log in again');
 789→        }
 790→
 791→        // Get parent folder state
 792→        const parentFolder =
 793→          parentId === 'root' ? getRootFolderState(vault, folders) : folders[parentId];
 794→
 795→        if (!parentFolder) {
 796→          throw new Error('Parent folder not found or vault not initialized');
 797→        }
 798→
 799→        // 1. Find the FilePointer in parent's children
 800→        const filePointer = parentFolder.children.find(
 801→          (c) => c.type === 'file' && c.id === fileData.fileId
 802→        ) as FilePointer | undefined;
 803→
 804→        if (!filePointer) {
 805→          throw new Error('File not found in folder');
 806→        }
 807→
 808→        // 2. Resolve current file metadata from IPNS
 809→        const { metadata: currentMetadata } = await resolveFileMetadata(
 810→          filePointer.fileMetaIpnsName,
 811→          parentFolder.folderKey
 812→        );
 813→
 814→        // 3. Determine whether to create a version entry
 815→        const createVersion = shouldCreateVersion(currentMetadata, fileData.forceVersion ?? false);
 816→
 817→        // 4. Update file metadata and publish new IPNS record
 818→        const { ipnsRecord, prunedCids } = await updateFileMetadata({
 819→          fileId: fileData.fileId,
 820→          folderKey: parentFolder.folderKey,
 821→          userPrivateKey: auth.vaultKeypair.privateKey,
 822→          currentMetadata,
 823→          updates: {
 824→            cid: fileData.newCid,
 825→            fileKeyEncrypted: fileData.newFileKeyEncrypted,
 826→            fileIv: fileData.newFileIv,
 827→            size: fileData.newSize,
 828→          },
 829→          createVersion,
 830→        });
 831→
 832→        // 5. Publish only the file IPNS record (folder metadata untouched!)
 833→        await folderService.replaceFileInFolder({
 834→          fileId: fileData.fileId,
 835→          fileIpnsRecord: ipnsRecord,
 836→          parentFolderState: parentFolder,
 837→        });
 838→
 839→        // 6. Update local state -- only touch modifiedAt on the FilePointer
 840→        const updatedChildren = parentFolder.children.map((child) => {
 841→          if (child.type === 'file' && child.id === fileData.fileId) {
 842→            return { ...child, modifiedAt: Date.now() };
 843→          }
 844→          return child;
 845→        });
 846→
 847→        const store = useFolderStore.getState();
 848→        store.updateFolderChildren(parentId, updatedChildren);
 849→
 850→        // 7. Only unpin CIDs of pruned versions (excess beyond max 10)
 851→        // Old CIDs stay pinned as version history (VER-01)
 852→        for (const prunedCid of prunedCids) {
 853→          unpinFromIpfs(prunedCid).catch(() => {});
 854→        }
 855→
 856→        // Refresh quota
 857→        useQuotaStore.getState().fetchQuota();
 858→
 859→        setState({ isLoading: false, error: null });
 860→      } catch (err) {
 861→        const error = err instanceof Error ? err.message : 'Failed to update file';
 862→        setState({ isLoading: false, error });
 863→        throw err;
 864→      }
 865→    },
 866→    []
 867→  );
