  1→import { type Page, type Locator } from '@playwright/test';
  2→
  3→/**
  4→ * Page object for Breadcrumbs component interactions.
  5→ *
  6→ * Interactive breadcrumb navigation with clickable segments
  7→ * and drag-drop support for quick moves to parent folders.
  8→ *
  9→ * Uses semantic selectors for maintainability.
 10→ */
 11→export class BreadcrumbsPage {
 12→  constructor(private readonly page: Page) {}
 13→
 14→  /**
 15→   * Get the breadcrumbs container.
 16→   */
 17→  container(): Locator {
 18→    return this.page.locator('.breadcrumb-nav');
 19→  }
 20→
 21→  /**
 22→   * Get a breadcrumb item by folder name.
 23→   */
 24→  getBreadcrumbItem(name: string): Locator {
 25→    return this.container().locator('.breadcrumb-item', {
 26→      hasText: new RegExp(`^${name}$`, 'i'),
 27→    });
 28→  }
 29→
 30→  /**
 31→   * Get all breadcrumb items.
 32→   */
 33→  breadcrumbItems(): Locator {
 34→    return this.container().locator('.breadcrumb-item');
 35→  }
 36→
 37→  /**
 38→   * Check if the breadcrumbs component is visible.
 39→   */
 40→  async isVisible(): Promise<boolean> {
 41→    return await this.container().isVisible();
 42→  }
 43→
 44→  /**
 45→   * Click a breadcrumb segment to navigate.
 46→   */
 47→  async clickBreadcrumb(name: string): Promise<void> {
 48→    await this.getBreadcrumbItem(name).click();
 49→  }
 50→
 51→  /**
 52→   * Get all visible breadcrumb names in order.
 53→   */
 54→  async getVisibleBreadcrumbs(): Promise<string[]> {
 55→    const items = this.breadcrumbItems();
 56→    const count = await items.count();
 57→    const names: string[] = [];
 58→
 59→    for (let i = 0; i < count; i++) {
 60→      const text = await items.nth(i).textContent();
 61→      if (text) {
 62→        names.push(text.trim());
 63→      }
 64→    }
 65→
 66→    return names;
 67→  }
 68→
 69→  /**
 70→   * Get the current path as a string (e.g., "~/my vault/documents").
 71→   * Reads the full nav content.
 72→   */
 73→  async getPathString(): Promise<string> {
 74→    const text = await this.container().textContent();
 75→    return text?.trim() ?? '';
 76→  }
 77→
 78→  /**
 79→   * Check if the current path contains a specific folder name.
 80→   */
 81→  async pathContains(folderName: string): Promise<boolean> {
 82→    const path = await this.getPathString();
 83→    return path.toLowerCase().includes(folderName.toLowerCase());
 84→  }
 85→
 86→  /**
 87→   * Check if at root folder.
 88→   * Root shows only "my vault" segment after ~/
 89→   */
 90→  async isAtRoot(): Promise<boolean> {
 91→    const breadcrumbs = await this.getVisibleBreadcrumbs();
 92→    return breadcrumbs.length === 1 && breadcrumbs[0].toLowerCase() === 'my vault';
 93→  }
 94→
 95→  /**
 96→   * Get the current folder name (last breadcrumb segment).
 97→   */
 98→  async getCurrentFolderName(): Promise<string> {
 99→    const breadcrumbs = await this.getVisibleBreadcrumbs();
100→    return breadcrumbs[breadcrumbs.length - 1] || 'my vault';
101→  }
102→
103→  /**
104→   * Wait for path to contain a specific folder name.
105→   * Useful after navigation.
106→   */
107→  async waitForPathToContain(folderName: string, options?: { timeout?: number }): Promise<void> {
108→    await this.container()
109→      .locator('.breadcrumb-item', { hasText: new RegExp(folderName, 'i') })
110→      .waitFor({ state: 'visible', ...options });
111→  }
112→
113→  /**
114→   * Wait for the breadcrumbs to show only root.
115→   */
116→  async waitForRoot(options?: { timeout?: number }): Promise<void> {
117→    // Wait for "my vault" to be the only breadcrumb (root level)
118→    await this.page
119→      .locator('.breadcrumb-nav')
120→      .filter({ hasText: /my vault/i })
121→      .waitFor({ state: 'visible', ...options });
122→  }
123→
124→  /**
125→   * Drag an item to a breadcrumb segment.
126→   * This performs a drag-and-drop from a source locator to a breadcrumb.
127→   *
128→   * @param sourceLocator - The locator for the item to drag
129→   * @param breadcrumbName - The name of the target breadcrumb
130→   */
131→  async dragItemToBreadcrumb(sourceLocator: Locator, breadcrumbName: string): Promise<void> {
132→    const target = this.getBreadcrumbItem(breadcrumbName);
133→    await sourceLocator.dragTo(target);
134→  }
135→}
