threads = pr['reviewThreads']['nodes']
unresolved = [t for t in threads if not t['isResolved']]
print(f'Total threads: {len(threads)}, Unresolved: {len(unresolved)}')
for t in unresolved:
    author = t['comments']['nodes'][0]['author']['login']
    body = t['comments']['nodes'][0]['body'][:100]
    print(f'  [{author}] {body}')
