  1→import { useCallback, useEffect, useRef, useState } from 'react';
  2→import { authApi } from '../../lib/api/auth';
  3→
  4→interface EmailLoginFormProps {
  5→  onLogin: (email: string, otp: string) => Promise<void>;
  6→  disabled?: boolean;
  7→}
  8→
  9→type FormStep = 'email' | 'otp';
 10→
 11→const OTP_LENGTH = 6;
 12→const RESEND_COOLDOWN_SECONDS = 60;
 13→
 14→/**
 15→ * Email + OTP two-step login form.
 16→ * Step 1: Email input + [SEND OTP] button
 17→ * Step 2: OTP input (6 digits) + [VERIFY] button + [RESEND] link
 18→ *
 19→ * Calls the CipherBox identity provider to send OTP,
 20→ * then calls onLogin(email, otp) for Core Kit auth.
 21→ */
 22→export function EmailLoginForm({ onLogin, disabled }: EmailLoginFormProps) {
 23→  const [step, setStep] = useState<FormStep>('email');
 24→  const [email, setEmail] = useState('');
 25→  const [otp, setOtp] = useState('');
 26→  const [loading, setLoading] = useState(false);
 27→  const [error, setError] = useState<string | null>(null);
 28→  const [resendCooldown, setResendCooldown] = useState(0);
 29→
 30→  const otpInputRef = useRef<HTMLInputElement>(null);
 31→  const cooldownTimerRef = useRef<ReturnType<typeof setInterval> | null>(null);
 32→
 33→  // Auto-focus OTP input when transitioning to step 2
 34→  useEffect(() => {
 35→    if (step !== 'otp') return;
 36→
 37→    // Small delay for DOM update
 38→    const timer = setTimeout(() => {
 39→      otpInputRef.current?.focus();
 40→    }, 50);
 41→    return () => clearTimeout(timer);
 42→  }, [step]);
 43→
 44→  // Cleanup cooldown timer on unmount
 45→  useEffect(() => {
 46→    return () => {
 47→      if (cooldownTimerRef.current) {
 48→        clearInterval(cooldownTimerRef.current);
 49→      }
 50→    };
 51→  }, []);
 52→
 53→  const startResendCooldown = useCallback(() => {
 54→    setResendCooldown(RESEND_COOLDOWN_SECONDS);
 55→
 56→    if (cooldownTimerRef.current) {
 57→      clearInterval(cooldownTimerRef.current);
 58→    }
 59→
 60→    cooldownTimerRef.current = setInterval(() => {
 61→      setResendCooldown((prev) => {
 62→        if (prev <= 1) {
 63→          if (cooldownTimerRef.current) {
 64→            clearInterval(cooldownTimerRef.current);
 65→            cooldownTimerRef.current = null;
 66→          }
 67→          return 0;
 68→        }
 69→        return prev - 1;
 70→      });
 71→    }, 1000);
 72→  }, []);
 73→
 74→  const handleSendOtp = async (e: React.FormEvent) => {
 75→    e.preventDefault();
 76→    if (!email.trim() || disabled || loading) return;
 77→
 78→    setLoading(true);
 79→    setError(null);
 80→
 81→    try {
 82→      await authApi.identityEmailSendOtp(email.trim().toLowerCase());
 83→      setStep('otp');
 84→      startResendCooldown();
 85→    } catch (err) {
 86→      const message = err instanceof Error ? err.message : 'Failed to send verification code';
 87→      // Check for rate limit error
 88→      if ((err as { response?: { status?: number } })?.response?.status === 429) {
 89→        setError('Too many attempts. Please wait before trying again.');
 90→      } else {
 91→        setError(message);
 92→      }
 93→    } finally {
 94→      setLoading(false);
 95→    }
 96→  };
 97→
 98→  const handleVerifyOtp = async (e: React.FormEvent) => {
 99→    e.preventDefault();
100→    if (otp.length !== OTP_LENGTH || disabled || loading) return;
101→
102→    setLoading(true);
103→    setError(null);
104→
105→    try {
106→      await onLogin(email.trim().toLowerCase(), otp);
107→    } catch (err) {
108→      const message = err instanceof Error ? err.message : 'Verification failed';
109→      // Check for invalid OTP response
110→      if ((err as { response?: { status?: number } })?.response?.status === 401) {
111→        setError('Invalid code. Please check and try again.');
112→      } else {
113→        setError(message);
114→      }
115→      // Clear OTP on error so user can re-enter
116→      setOtp('');
117→      otpInputRef.current?.focus();
118→    } finally {
119→      setLoading(false);
120→    }
121→  };
122→
123→  const handleResend = async () => {
124→    if (resendCooldown > 0 || loading) return;
125→
126→    setLoading(true);
127→    setError(null);
128→
129→    try {
130→      await authApi.identityEmailSendOtp(email.trim().toLowerCase());
131→      startResendCooldown();
132→      setOtp('');
133→      otpInputRef.current?.focus();
134→    } catch {
135→      setError('Failed to resend code. Please try again.');
136→    } finally {
137→      setLoading(false);
138→    }
139→  };
140→
141→  const handleBackToEmail = () => {
142→    setStep('email');
143→    setOtp('');
144→    setError(null);
145→  };
146→
147→  const handleOtpChange = (e: React.ChangeEvent<HTMLInputElement>) => {
148→    const value = e.target.value.replace(/\D/g, '').slice(0, OTP_LENGTH);
149→    setOtp(value);
150→  };
151→
152→  const loadingText = step === 'email' ? 'sending code...' : 'verifying code...';
153→
154→  return (
155→    <div className="email-login-form">
156→      {step === 'email' ? (
157→        <form onSubmit={handleSendOtp} className="email-login-step">
158→          <label htmlFor="login-email" className="sr-only">
159→            Email address
160→          </label>
161→          <input
162→            id="login-email"
163→            data-testid="email-input"
164→            type="email"
165→            className="email-login-input"
166→            placeholder="enter email address"
167→            value={email}
168→            onChange={(e) => setEmail(e.target.value)}
169→            disabled={disabled || loading}
170→            required
171→            autoComplete="email"
172→            aria-describedby={error ? 'email-login-error' : undefined}
173→          />
174→          <button
175→            type="submit"
176→            data-testid="send-otp-button"
177→            className={['email-login-submit', loading ? 'email-login-submit--loading' : '']
178→              .filter(Boolean)
179→              .join(' ')}
180→            disabled={disabled || loading || !email.trim()}
181→            aria-busy={loading}
182→          >
183→            {loading ? loadingText : '[SEND OTP]'}
184→          </button>
185→        </form>
186→      ) : (
187→        <form onSubmit={handleVerifyOtp} className="email-login-step">
188→          <div className="otp-header">
189→            <button
190→              type="button"
191→              className="otp-back-btn"
192→              onClick={handleBackToEmail}
193→              disabled={loading}
194→              aria-label="Go back to email input"
195→            >
196→              {'<-'}
197→            </button>
198→            <span className="otp-email-display">{email}</span>
199→          </div>
200→          <label htmlFor="login-otp" className="sr-only">
201→            Verification code
202→          </label>
203→          <input
204→            id="login-otp"
205→            data-testid="otp-input"
206→            ref={otpInputRef}
207→            type="text"
208→            inputMode="numeric"
209→            pattern="[0-9]*"
210→            className="otp-input"
211→            placeholder="enter 6-digit code"
212→            value={otp}
213→            onChange={handleOtpChange}
214→            disabled={disabled || loading}
215→            required
216→            maxLength={OTP_LENGTH}
217→            autoComplete="one-time-code"
218→            aria-describedby={error ? 'email-login-error' : undefined}
219→          />
220→          <button
221→            type="submit"
222→            data-testid="verify-button"
223→            className={['email-login-submit', loading ? 'email-login-submit--loading' : '']
224→              .filter(Boolean)
225→              .join(' ')}
226→            disabled={disabled || loading || otp.length !== OTP_LENGTH}
227→            aria-busy={loading}
228→          >
229→            {loading ? loadingText : '[VERIFY]'}
230→          </button>
231→          <button
232→            type="button"
233→            className="otp-resend-btn"
234→            onClick={handleResend}
235→            disabled={resendCooldown > 0 || loading}
236→            aria-label={
237→              resendCooldown > 0
238→                ? `Resend code available in ${resendCooldown} seconds`
239→                : 'Resend verification code'
240→            }
241→          >
242→            {resendCooldown > 0 ? `[resend in ${resendCooldown}s]` : '[resend code]'}
243→          </button>
244→        </form>
245→      )}
246→
247→      {error && (
248→        <div id="email-login-error" className="login-error" role="alert" aria-live="polite">
249→          {error}
250→        </div>
251→      )}
252→    </div>
253→  );
254→}
