  1→import { useCallback, useEffect, useState } from 'react';
  2→
  3→/** Minimal type declarations for Google Identity Services (GIS) library */
  4→declare const google: {
  5→  accounts: {
  6→    id: {
  7→      initialize: (config: {
  8→        client_id: string;
  9→        callback: (response: { credential: string }) => void;
 10→        auto_select: boolean;
 11→      }) => void;
 12→      prompt: (
 13→        momentListener?: (notification: {
 14→          isNotDisplayed: () => boolean;
 15→          isSkippedMoment: () => boolean;
 16→        }) => void
 17→      ) => void;
 18→    };
 19→  };
 20→};
 21→
 22→interface GoogleLoginButtonProps {
 23→  onLogin: (googleIdToken: string) => Promise<void>;
 24→  disabled?: boolean;
 25→}
 26→
 27→/**
 28→ * Google OAuth login button using Google Identity Services (GIS) library.
 29→ * Loads the GIS script dynamically, initializes with VITE_GOOGLE_CLIENT_ID,
 30→ * and triggers the Google One Tap / popup flow on click.
 31→ *
 32→ * If VITE_GOOGLE_CLIENT_ID is not set, renders in a disabled state.
 33→ */
 34→export function GoogleLoginButton({ onLogin, disabled }: GoogleLoginButtonProps) {
 35→  const [gisLoaded, setGisLoaded] = useState(false);
 36→  const [ready, setReady] = useState(false);
 37→  const [loading, setLoading] = useState(false);
 38→  const [error, setError] = useState<string | null>(null);
 39→
 40→  const clientId = import.meta.env.VITE_GOOGLE_CLIENT_ID as string | undefined;
 41→
 42→  // Handle credential response from Google
 43→  const handleCredentialResponse = useCallback(
 44→    async (response: { credential: string }) => {
 45→      try {
 46→        await onLogin(response.credential);
 47→      } catch (err) {
 48→        setError(err instanceof Error ? err.message : 'Google login failed');
 49→      } finally {
 50→        setLoading(false);
 51→      }
 52→    },
 53→    [onLogin]
 54→  );
 55→
 56→  // Load GIS script dynamically on mount
 57→  useEffect(() => {
 58→    if (!clientId) return;
 59→
 60→    // Check if already loaded
 61→    if (typeof google !== 'undefined' && google?.accounts?.id) {
 62→      setGisLoaded(true);
 63→      return;
 64→    }
 65→
 66→    let cancelled = false;
 67→
 68→    const script = document.createElement('script');
 69→    script.src = 'https://accounts.google.com/gsi/client';
 70→    script.async = true;
 71→    script.onload = () => {
 72→      if (!cancelled) setGisLoaded(true);
 73→    };
 74→    script.onerror = () => {
 75→      if (!cancelled) setError('Failed to load Google authentication');
 76→    };
 77→    document.body.appendChild(script);
 78→
 79→    return () => {
 80→      cancelled = true;
 81→      if (script.parentNode) {
 82→        document.body.removeChild(script);
 83→      }
 84→    };
 85→  }, [clientId]);
 86→
 87→  // Initialize GIS once script loads
 88→  useEffect(() => {
 89→    if (!gisLoaded || !clientId) return;
 90→
 91→    try {
 92→      google.accounts.id.initialize({
 93→        client_id: clientId,
 94→        callback: handleCredentialResponse,
 95→        auto_select: false,
 96→      });
 97→      setReady(true);
 98→    } catch {
 99→      setError('Failed to initialize Google authentication');
100→    }
101→  }, [gisLoaded, clientId, handleCredentialResponse]);
102→
103→  const handleClick = () => {
104→    if (!ready || disabled || loading) return;
105→
106→    setLoading(true);
107→    setError(null);
108→
109→    // Safety timeout: if Google prompt doesn't resolve within 60s
110→    // (e.g., user closes popup without selecting), reset loading state
111→    const timeoutId = setTimeout(() => {
112→      setLoading(false);
113→    }, 60000);
114→
115→    const originalHandleCredential = handleCredentialResponse;
116→    const wrappedHandler = async (response: { credential: string }) => {
117→      clearTimeout(timeoutId);
118→      await originalHandleCredential(response);
119→    };
120→
121→    // Re-initialize with wrapped handler to clear timeout on success
122→    google.accounts.id.initialize({
123→      client_id: clientId!,
124→      callback: wrappedHandler,
125→      auto_select: false,
126→    });
127→
128→    google.accounts.id.prompt((notification) => {
129→      if (notification.isNotDisplayed() || notification.isSkippedMoment()) {
130→        clearTimeout(timeoutId);
131→        setError('Google popup was blocked. Please allow popups for this site.');
132→        setLoading(false);
133→      }
134→    });
135→  };
136→
137→  const isDisabled = disabled || loading || !clientId || (!ready && !!clientId);
138→
139→  const buttonText = () => {
140→    if (!clientId) return '[GOOGLE - NOT CONFIGURED]';
141→    if (loading) return 'authenticating with google...';
142→    return '[GOOGLE]';
143→  };
144→
145→  return (
146→    <div className="google-login-wrapper">
147→      <button
148→        type="button"
149→        data-testid="google-login-button"
150→        className={[
151→          'google-login-btn',
152→          loading ? 'google-login-btn--loading' : '',
153→          !clientId ? 'google-login-btn--not-configured' : '',
154→        ]
155→          .filter(Boolean)
156→          .join(' ')}
157→        onClick={handleClick}
158→        disabled={isDisabled}
159→        aria-label="Sign in with Google"
160→        aria-busy={loading}
161→      >
162→        {buttonText()}
163→      </button>
164→      {error && (
165→        <div className="login-error" role="alert" aria-live="polite">
166→          {error}
167→        </div>
168→      )}
169→    </div>
170→  );
171→}
