 3→/**
 4→ * Logout button that disconnects Web3Auth and clears session.
 5→ * Per 02-CONTEXT.md: Immediate logout on click, no confirmation.
 6→ */
 7→export function LogoutButton() {
 8→  const { logout, isLoading } = useAuth();
 9→
10→  const handleClick = async () => {
11→    try {
12→      await logout();
13→    } catch {
14→      // Error is already logged in useAuth
15→      // User will be redirected regardless
16→    }
17→  };
18→
19→  return (
20→    <button onClick={handleClick} disabled={isLoading} className="logout-link">
21→      {isLoading ? 'logging out...' : 'logout'}
22→    </button>
23→  );
24→}
