 1→import { createContext, useContext, useEffect, useState, useCallback, type ReactNode } from 'react';
 2→import { getCoreKit, initCoreKit, COREKIT_STATUS } from './core-kit';
 3→import type { Web3AuthMPCCoreKit } from './core-kit';
 4→
 5→interface CoreKitContextValue {
 6→  /** The Core Kit singleton instance (null until first render) */
 7→  coreKit: Web3AuthMPCCoreKit | null;
 8→  /** Current SDK status from the COREKIT_STATUS state machine */
 9→  status: COREKIT_STATUS;
10→  /** True when status is anything other than NOT_INITIALIZED */
11→  isInitialized: boolean;
12→  /** True when status is LOGGED_IN (user fully authenticated) */
13→  isLoggedIn: boolean;
14→  /** True when status is REQUIRED_SHARE (MFA challenge needed -- Phase 12.4) */
15→  isRequiredShare: boolean;
16→  /** Initialization error, if any */
17→  error: Error | null;
18→  /** Re-run initialization (e.g., retry after error) */
19→  reinitialize: () => Promise<void>;
20→}
21→
22→const CoreKitContext = createContext<CoreKitContextValue | null>(null);
23→
24→/**
25→ * React context provider for MPC Core Kit.
26→ *
27→ * Initializes Core Kit on mount (calls init() which checks for existing sessions).
28→ * Exposes the singleton instance and COREKIT_STATUS to child components.
29→ * Mounted at the top of the component tree in main.tsx.
30→ */
31→export function CoreKitProvider({ children }: { children: ReactNode }) {
32→  const [status, setStatus] = useState<COREKIT_STATUS>(COREKIT_STATUS.NOT_INITIALIZED);
33→  const [coreKit, setCoreKit] = useState<Web3AuthMPCCoreKit | null>(null);
34→  const [error, setError] = useState<Error | null>(null);
35→
36→  const initialize = useCallback(async () => {
37→    try {
38→      setError(null);
39→      const ck = getCoreKit();
40→      setCoreKit(ck);
41→      const resultStatus = await initCoreKit();
42→      setStatus(resultStatus);
43→    } catch (err) {
44→      console.error('[CoreKit] Initialization failed:', err);
45→      setError(err instanceof Error ? err : new Error(String(err)));
46→    }
47→  }, []);
48→
49→  useEffect(() => {
50→    initialize();
51→  }, [initialize]);
52→
53→  const value: CoreKitContextValue = {
54→    coreKit,
55→    status,
56→    isInitialized: status !== COREKIT_STATUS.NOT_INITIALIZED,
57→    isLoggedIn: status === COREKIT_STATUS.LOGGED_IN,
58→    isRequiredShare: status === COREKIT_STATUS.REQUIRED_SHARE,
59→    error,
60→    reinitialize: initialize,
61→  };
62→
63→  return <CoreKitContext.Provider value={value}>{children}</CoreKitContext.Provider>;
64→}
65→
66→/**
67→ * Hook to access Core Kit instance and status from React components.
68→ * Must be used within a CoreKitProvider.
69→ */
70→export function useCoreKit(): CoreKitContextValue {
71→  const context = useContext(CoreKitContext);
72→  if (!context) {
73→    throw new Error('useCoreKit must be used within CoreKitProvider');
74→  }
75→  return context;
76→}
