  1→import { useCallback, useEffect, useRef, useState } from 'react';
  2→import { useNavigate } from 'react-router-dom';
  3→import { useCoreKitAuth } from '../lib/web3auth/hooks';
  4→import { authApi } from '../lib/api/auth';
  5→import { vaultApi } from '../lib/api/vault';
  6→import { useAuthStore } from '../stores/auth.store';
  7→import { useVaultStore } from '../stores/vault.store';
  8→import { useFolderStore } from '../stores/folder.store';
  9→import { useSyncStore } from '../stores/sync.store';
 10→import { useDeviceRegistryStore } from '../stores/device-registry.store';
 11→import { useMfaStore } from '../stores/mfa.store';
 12→import {
 13→  initializeVault,
 14→  encryptVaultKeys,
 15→  decryptVaultKeys,
 16→  deriveIpnsName,
 17→  hexToBytes,
 18→  bytesToHex,
 19→} from '@cipherbox/crypto';
 20→import { getOrCreateDeviceIdentity } from '../lib/device/identity';
 21→import { detectDeviceInfo } from '../lib/device/info';
 22→import { initializeOrSyncRegistry } from '../services/device-registry.service';
 23→
 24→export function useAuth() {
 25→  const navigate = useNavigate();
 26→  const {
 27→    isLoggedIn: coreKitLoggedIn,
 28→    isInitialized: coreKitInitialized,
 29→    isRequiredShare,
 30→    loginWithGoogle: coreKitLoginGoogle,
 31→    loginWithEmailOtp: coreKitLoginEmail,
 32→    loginWithWallet: coreKitLoginWallet,
 33→    getVaultKeypair,
 34→    getPublicKeyHex,
 35→    logout: coreKitLogout,
 36→  } = useCoreKitAuth();
 37→  const {
 38→    accessToken,
 39→    isAuthenticated,
 40→    lastAuthMethod,
 41→    userEmail,
 42→    setAccessToken,
 43→    setLastAuthMethod,
 44→    setUserEmail,
 45→    setVaultKeypair,
 46→    logout: clearAuthState,
 47→  } = useAuthStore();
 48→
 49→  const [isLoggingIn, setIsLoggingIn] = useState(false);
 50→  const [isLoggingOut, setIsLoggingOut] = useState(false);
 51→  const restoringRef = useRef(false);
 52→
 53→  // Pending auth state for REQUIRED_SHARE flow
 54→  // Stored so completeRequiredShare() can resume after factor input
 55→  const [pendingCipherboxJwt, setPendingCipherboxJwt] = useState<string | null>(null);
 56→  const [pendingAuthMethod, setPendingAuthMethod] = useState<string | null>(null);
 57→
 58→  const isLoading = !coreKitInitialized || isLoggingIn || isLoggingOut;
 59→
 60→  // Get vault store actions
 61→  const setVaultKeys = useVaultStore((state) => state.setVaultKeys);
 62→
 63→  /**
 64→   * Initialize vault for new users or load existing vault keys.
 65→   * Called after successful backend authentication.
 66→   * Uses Core Kit's getVaultKeypair() instead of PnP provider.
 67→   */
 68→  const initializeOrLoadVault = useCallback(async (): Promise<void> => {
 69→    // Get user's secp256k1 keypair from Core Kit TSS export
 70→    const userKeypair = await getVaultKeypair();
 71→
 72→    if (!userKeypair) {
 73→      throw new Error('Failed to get vault keypair from Core Kit');
 74→    }
 75→
 76→    // Store keypair in auth store for crypto operations
 77→    setVaultKeypair(userKeypair);
 78→
 79→    try {
 80→      // Try to fetch existing vault
 81→      const existingVault = await vaultApi.getVault();
 82→
 83→      // Store TEE keys if available
 84→      if (existingVault.teeKeys) {
 85→        useAuthStore.getState().setTeeKeys(existingVault.teeKeys);
 86→      }
 87→
 88→      // Vault exists - decrypt keys and load into store
 89→      // decryptVaultKeys derives the IPNS public key internally from the decrypted private key
 90→      const decryptedVault = await decryptVaultKeys(
 91→        {
 92→          encryptedRootFolderKey: hexToBytes(existingVault.encryptedRootFolderKey),
 93→          encryptedIpnsPrivateKey: hexToBytes(existingVault.encryptedRootIpnsPrivateKey),
 94→        },
 95→        userKeypair.privateKey
 96→      );
 97→
 98→      setVaultKeys({
 99→        rootFolderKey: decryptedVault.rootFolderKey,
100→        rootIpnsKeypair: decryptedVault.rootIpnsKeypair,
101→        rootIpnsName: existingVault.rootIpnsName,
102→        vaultId: existingVault.id,
103→      });
104→    } catch (error) {
105→      const is404 = (error as { response?: { status?: number } })?.response?.status === 404;
106→
107→      if (is404) {
108→        // New user - initialize vault
109→        console.log(
110→          '[Auth] New user on Core Kit. If migrating from PnP, vault re-initialization may be needed.'
111→        );
112→        const newVault = await initializeVault(userKeypair.privateKey);
113→        const encryptedVault = await encryptVaultKeys(newVault, userKeypair.publicKey);
114→        const rootIpnsName = await deriveIpnsName(newVault.rootIpnsKeypair.publicKey);
115→
116→        const storedVault = await vaultApi.initVault({
117→          ownerPublicKey: bytesToHex(userKeypair.publicKey),
118→          encryptedRootFolderKey: bytesToHex(encryptedVault.encryptedRootFolderKey),
119→          encryptedRootIpnsPrivateKey: bytesToHex(encryptedVault.encryptedIpnsPrivateKey),
120→          rootIpnsName,
121→        });
122→
123→        if (storedVault.teeKeys) {
124→          useAuthStore.getState().setTeeKeys(storedVault.teeKeys);
125→        }
126→
127→        setVaultKeys({
128→          rootFolderKey: newVault.rootFolderKey,
129→          rootIpnsKeypair: newVault.rootIpnsKeypair,
130→          rootIpnsName,
131→          vaultId: storedVault.id,
132→          isNewVault: true,
133→        });
134→      } else {
135→        console.error('[useAuth] Failed to load vault:', error);
136→        throw error;
137→      }
138→    }
139→
140→    // Non-blocking device registry initialization (fire-and-forget)
141→    // Placed after vault load so registry failures never block login
142→    void (async () => {
143→      try {
144→        const deviceKeypair = await getOrCreateDeviceIdentity();
145→        const deviceInfo = detectDeviceInfo();
146→        const result = await initializeOrSyncRegistry({
147→          userPrivateKey: userKeypair.privateKey,
148→          userPublicKey: userKeypair.publicKey,
149→          deviceKeypair,
150→          deviceInfo: { ...deviceInfo, ipHash: '' },
151→        });
152→        if (result) {
153→          useDeviceRegistryStore
154→            .getState()
155→            .setRegistry(result.registry, result.ipnsName, deviceKeypair.deviceId);
156→        }
157→      } catch (error) {
158→        console.error('[Auth] Device registry init failed (non-blocking):', error);
159→      }
160→    })();
161→  }, [getVaultKeypair, setVaultKeypair, setVaultKeys]);
162→
163→  /**
164→   * Complete backend authentication after Core Kit login.
165→   * Sends the CipherBox-issued JWT (which we used for Core Kit loginWithJWT)
166→   * to the backend with loginType 'corekit'. The backend verifies it against
167→   * its own JWKS since CipherBox is the identity provider.
168→   */
169→  const completeBackendAuth = useCallback(
170→    async (authMethod: string, cipherboxJwt: string): Promise<void> => {
171→      // 1. Get real publicKey from Core Kit TSS export
172→      const publicKey = await getPublicKeyHex();
173→      if (!publicKey) {
174→        throw new Error('Failed to get publicKey from Core Kit');
175→      }
176→
177→      // publicKey available for debugging via DevTools if needed
178→
179→      // 2. Authenticate with CipherBox backend
180→      // Backend verifies our CipherBox JWT and resolves placeholder publicKey
181→      const response = await authApi.login({
182→        idToken: cipherboxJwt,
183→        publicKey,
184→        loginType: 'corekit',
185→      });
186→
187→      // 3. Store access token (refresh token in HTTP-only cookie)
188→      setAccessToken(response.accessToken);
189→
190→      // 4. Remember auth method for UX
191→      setLastAuthMethod(authMethod);
192→
193→      // 5. Initialize or load vault
194→      await initializeOrLoadVault();
195→    },
196→    [getPublicKeyHex, setAccessToken, setLastAuthMethod, initializeOrLoadVault]
197→  );
198→
199→  /**
200→   * Complete the login flow after REQUIRED_SHARE is resolved.
201→   * Called after inputFactorKey() succeeds (from recovery phrase or cross-device approval).
202→   * Uses the stored pendingCipherboxJwt to call completeBackendAuth with the REAL publicKey
203→   * (since Core Kit is now LOGGED_IN), then navigates to /files.
204→   */
205→  const completeRequiredShare = useCallback(async (): Promise<void> => {
206→    const jwt = pendingCipherboxJwt;
207→    const method = pendingAuthMethod;
208→
209→    if (!jwt || !method) {
210→      throw new Error('No pending auth info for REQUIRED_SHARE completion');
211→    }
212→
213→    // Core Kit should now be LOGGED_IN after inputFactorKey()
214→    // Complete backend auth with the REAL publicKey (replaces the placeholder session)
215→    await completeBackendAuth(method, jwt);
216→
217→    // Clear pending state
218→    setPendingCipherboxJwt(null);
219→    setPendingAuthMethod(null);
220→
221→    // Navigate to files
222→    navigate('/files');
223→  }, [pendingCipherboxJwt, pendingAuthMethod, completeBackendAuth, navigate]);
224→
225→  /**
226→   * Login with Google OAuth token.
227→   * Flow: Google idToken -> CipherBox backend -> CipherBox JWT ->
228→   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
229→   */
230→  const loginWithGoogle = useCallback(
231→    async (googleIdToken: string): Promise<void> => {
232→      if (isLoggingIn) return;
233→      setIsLoggingIn(true);
234→      try {
235→        // 1. Core Kit login via CipherBox identity provider
236→        const {
237→          cipherboxJwt,
238→          email,
239→          userId,
240→          status: coreKitStatus,
241→        } = await coreKitLoginGoogle(googleIdToken);
242→
243→        if (coreKitStatus === 'required_share') {
244→          // MFA enabled but device factor missing.
245→          // Store pending auth info for later completion after factor input.
246→          setPendingCipherboxJwt(cipherboxJwt);
247→          setPendingAuthMethod('google');
248→
249→          // Obtain temporary backend access token so the new device can
250→          // call bulletin board API endpoints (device-approval/*).
251→          // Uses placeholder publicKey since Core Kit is in REQUIRED_SHARE
252→          // state and we can't export the TSS key yet.
253→          const tempLoginResponse = await authApi.login({
254→            idToken: cipherboxJwt,
255→            publicKey: `pending-core-kit-${userId}`,
256→            loginType: 'corekit',
257→          });
258→          setAccessToken(tempLoginResponse.accessToken);
259→
260→          if (email) {
261→            setUserEmail(email);
262→          }
263→
264→          // Do NOT call completeBackendAuth() or navigate('/files')
265→          // The component tree will see isRequiredShare === true and
266→          // render recovery/approval UI.
267→          return;
268→        }
269→
270→        // Normal path: Core Kit logged in, proceed as before
271→        // 2. Complete backend auth + vault init
272→        await completeBackendAuth('google', cipherboxJwt);
273→
274→        // 3. Store email for UI display (returned from identity endpoint)
275→        if (email) {
276→          setUserEmail(email);
277→        }
278→
279→        // 4. Navigate to files
280→        navigate('/files');
281→      } catch (error) {
282→        console.error('[useAuth] Google login failed:', error);
283→        throw error;
284→      } finally {
285→        setIsLoggingIn(false);
286→      }
287→    },
288→    [isLoggingIn, coreKitLoginGoogle, completeBackendAuth, setAccessToken, setUserEmail, navigate]
289→  );
290→
291→  /**
292→   * Login with Email OTP.
293→   * Flow: email+otp -> CipherBox backend -> CipherBox JWT ->
294→   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
295→   */
296→  const loginWithEmail = useCallback(
297→    async (email: string, otp: string): Promise<void> => {
298→      if (isLoggingIn) return;
299→      setIsLoggingIn(true);
300→      try {
301→        // 1. Core Kit login via CipherBox identity provider
302→        const { cipherboxJwt, userId, status: coreKitStatus } = await coreKitLoginEmail(email, otp);
303→
304→        if (coreKitStatus === 'required_share') {
305→          setPendingCipherboxJwt(cipherboxJwt);
306→          setPendingAuthMethod('email');
307→
308→          const tempLoginResponse = await authApi.login({
309→            idToken: cipherboxJwt,
310→            publicKey: `pending-core-kit-${userId}`,
311→            loginType: 'corekit',
312→          });
313→          setAccessToken(tempLoginResponse.accessToken);
314→          setUserEmail(email);
315→          return;
316→        }
317→
318→        // Normal path
319→        await completeBackendAuth('email', cipherboxJwt);
320→        setUserEmail(email);
321→        navigate('/files');
322→      } catch (error) {
323→        console.error('[useAuth] Email login failed:', error);
324→        throw error;
325→      } finally {
326→        setIsLoggingIn(false);
327→      }
328→    },
329→    [isLoggingIn, coreKitLoginEmail, completeBackendAuth, setAccessToken, setUserEmail, navigate]
330→  );
331→
332→  /**
333→   * Login with Wallet (SIWE).
334→   * Flow: Wallet connects + signs SIWE message -> backend verifies ->
335→   * CipherBox JWT -> Core Kit loginWithJWT -> backend /auth/login (corekit type)
336→   */
337→  const loginWithWallet = useCallback(
338→    async (cipherboxJwt: string, userId: string): Promise<void> => {
339→      if (isLoggingIn) return;
340→      setIsLoggingIn(true);
341→      try {
342→        // 1. Core Kit login via CipherBox identity provider
343→        const { status: coreKitStatus } = await coreKitLoginWallet(cipherboxJwt, userId);
344→
345→        if (coreKitStatus === 'required_share') {
346→          setPendingCipherboxJwt(cipherboxJwt);
347→          setPendingAuthMethod('wallet');
348→
349→          const tempLoginResponse = await authApi.login({
350→            idToken: cipherboxJwt,
351→            publicKey: `pending-core-kit-${userId}`,
352→            loginType: 'corekit',
353→          });
354→          setAccessToken(tempLoginResponse.accessToken);
355→          return;
356→        }
357→
358→        // Normal path
359→        await completeBackendAuth('wallet', cipherboxJwt);
360→        navigate('/files');
361→      } catch (error) {
362→        console.error('[useAuth] Wallet login failed:', error);
363→        throw error;
364→      } finally {
365→        setIsLoggingIn(false);
366→      }
367→    },
368→    [isLoggingIn, coreKitLoginWallet, completeBackendAuth, setAccessToken, navigate]
369→  );
370→
371→  /**
372→   * Legacy login() for backward compatibility with AuthButton.
373→   * With Core Kit, login requires specifying the auth method.
374→   * This will be replaced by the new Login UI in Plan 04.
375→   */
376→  const login = useCallback(async () => {
377→    // Plan 04 will build the new login UI with method selection.
378→    // For now, this is a no-op placeholder that AuthButton calls.
379→    console.warn(
380→      '[useAuth] login() called without method -- ' +
381→        'use loginWithGoogle() or loginWithEmail() instead. ' +
382→        'Plan 04 will provide the new Login UI.'
383→    );
384→  }, []);
385→
386→  // Complete logout: Backend -> Core Kit -> Clear state
387→  const logout = useCallback(async () => {
388→    if (isLoggingOut) return;
389→
390→    setIsLoggingOut(true);
391→    try {
392→      // 1. Call backend logout (clears cookie)
393→      if (accessToken) {
394→        try {
395→          await authApi.logout();
396→        } catch {
397→          // Ignore errors -- we'll clear state anyway
398→        }
399→      }
400→
401→      // 2. Logout Core Kit (clears session from localStorage)
402→      await coreKitLogout();
403→
404→      // 3. Clear local state
405→      // [SECURITY: HIGH-02] Clear vault and folder stores
406→      // BEFORE auth state to zero crypto keys from memory
407→      useFolderStore.getState().clearFolders();
408→      useVaultStore.getState().clearVaultKeys();
409→      useSyncStore.getState().reset();
410→      useDeviceRegistryStore.getState().clearRegistry();
411→      useMfaStore.getState().reset();
412→      clearAuthState();
413→
414→      // 4. Clear pending REQUIRED_SHARE state
415→      setPendingCipherboxJwt(null);
416→      setPendingAuthMethod(null);
417→
418→      // 5. Navigate to login
419→      navigate('/');
420→    } catch (error) {
421→      console.error('[useAuth] Logout failed:', error);
422→      // Still clear state even if backend fails
423→      useFolderStore.getState().clearFolders();
424→      useVaultStore.getState().clearVaultKeys();
425→      useSyncStore.getState().reset();
426→      useDeviceRegistryStore.getState().clearRegistry();
427→      useMfaStore.getState().reset();
428→      clearAuthState();
429→      setPendingCipherboxJwt(null);
430→      setPendingAuthMethod(null);
431→      navigate('/');
432→    } finally {
433→      setIsLoggingOut(false);
434→    }
435→  }, [accessToken, coreKitLogout, clearAuthState, navigate, isLoggingOut]);
436→
437→  // Session restoration: if Core Kit restores a session from localStorage
438→  // on init, we have LOGGED_IN status without going through login flow.
439→  // Complete backend auth + vault loading.
440→  useEffect(() => {
441→    const restoreSession = async () => {
442→      // Only restore if Core Kit has a session but we don't have
443→      // a backend access token yet
444→      if (coreKitLoggedIn && !isAuthenticated && !isLoggingIn && !restoringRef.current) {
445→        restoringRef.current = true;
446→        setIsLoggingIn(true);
447→        try {
448→          // Try to refresh using the HTTP-only cookie first
449→          const response = await authApi.refresh();
450→          setAccessToken(response.accessToken);
451→
452→          // Restore email from backend if available
453→          if (response.email) {
454→            setUserEmail(response.email);
455→          }
456→
457→          // Load vault keys from Core Kit keypair
458→          await initializeOrLoadVault();
459→        } catch {
460→          // No valid backend session -- user needs to re-login
461→          // Core Kit session exists but backend cookie expired
462→          // Clear Core Kit session to avoid inconsistent state
463→          try {
464→            await coreKitLogout();
465→          } catch {
466→            // Ignore logout errors during cleanup
467→          }
468→        } finally {
469→          restoringRef.current = false;
470→          setIsLoggingIn(false);
471→        }
472→      }
473→    };
474→    restoreSession();
475→  }, [
476→    coreKitLoggedIn,
477→    isAuthenticated,
478→    isLoggingIn,
479→    setAccessToken,
480→    setUserEmail,
481→    initializeOrLoadVault,
482→    coreKitLogout,
483→  ]);
484→
485→  return {
486→    isLoading,
487→    isAuthenticated,
488→    isRequiredShare,
489→    lastAuthMethod,
490→    userEmail,
491→    pendingAuthMethod,
492→    login,
493→    loginWithGoogle,
494→    loginWithEmail,
495→    loginWithWallet,
496→    completeRequiredShare,
497→    logout,
498→  };
499→}
