  1→import { useCallback, useEffect, useRef, useState } from 'react';
  2→import { useDeviceApproval, type ApprovalStatus } from '../../hooks/useDeviceApproval';
  3→
  4→type DeviceWaitingScreenProps = {
  5→  onRecoveryFallback: () => void;
  6→  onApprovalComplete: () => void;
  7→};
  8→
  9→const APPROVAL_TTL_MS = 5 * 60 * 1000; // 5 minutes
 10→const COUNTDOWN_WARNING_MS = 3 * 60 * 1000; // 3 minutes remaining
 11→
 12→/**
 13→ * Full-screen waiting component shown when a new device is in
 14→ * REQUIRED_SHARE state. Creates a bulletin board approval request
 15→ * and polls for a response from an existing device.
 16→ *
 17→ * Displays a spinner, countdown timer, and a fallback link to
 18→ * use a recovery phrase instead.
 19→ */
 20→export function DeviceWaitingScreen({
 21→  onRecoveryFallback,
 22→  onApprovalComplete,
 23→}: DeviceWaitingScreenProps) {
 24→  const { requestApproval, cancelRequest, approvalStatus, approvalError } = useDeviceApproval();
 25→
 26→  const [countdown, setCountdown] = useState(APPROVAL_TTL_MS);
 27→  const startTimeRef = useRef<number>(Date.now());
 28→  const countdownRef = useRef<ReturnType<typeof setInterval> | null>(null);
 29→
 30→  // Start the approval request on mount
 31→  useEffect(() => {
 32→    startTimeRef.current = Date.now();
 33→    requestApproval();
 34→
 35→    // Countdown timer
 36→    countdownRef.current = setInterval(() => {
 37→      const elapsed = Date.now() - startTimeRef.current;
 38→      const remaining = Math.max(0, APPROVAL_TTL_MS - elapsed);
 39→      setCountdown(remaining);
 40→    }, 1000);
 41→
 42→    return () => {
 43→      if (countdownRef.current) {
 44→        clearInterval(countdownRef.current);
 45→      }
 46→      // Cancel request on unmount
 47→      cancelRequest();
 48→    };
 49→  }, []);
 50→
 51→  // Auto-complete when approval succeeds
 52→  useEffect(() => {
 53→    if (approvalStatus === 'approved') {
 54→      onApprovalComplete();
 55→    }
 56→  }, [approvalStatus, onApprovalComplete]);
 57→
 58→  const handleRetry = useCallback(() => {
 59→    startTimeRef.current = Date.now();
 60→    setCountdown(APPROVAL_TTL_MS);
 61→    requestApproval();
 62→  }, [requestApproval]);
 63→
 64→  const minutes = Math.floor(countdown / 60000);
 65→  const seconds = Math.floor((countdown % 60000) / 1000);
 66→  const timeDisplay = `${minutes}:${seconds.toString().padStart(2, '0')}`;
 67→  const isWarning = countdown <= COUNTDOWN_WARNING_MS && countdown > 0;
 68→
 69→  return (
 70→    <div className="device-waiting">
 71→      <div className="device-waiting-card">
 72→        <h2 className="device-waiting-title">{'// waiting for device approval'}</h2>
 73→
 74→        {renderContent(approvalStatus, {
 75→          timeDisplay,
 76→          isWarning,
 77→          countdown,
 78→          approvalError,
 79→          onRecoveryFallback,
 80→          handleRetry,
 81→        })}
 82→      </div>
 83→    </div>
 84→  );
 85→}
 86→
 87→function renderContent(
 88→  status: ApprovalStatus,
 89→  props: {
 90→    timeDisplay: string;
 91→    isWarning: boolean;
 92→    countdown: number;
 93→    approvalError: string | null;
 94→    onRecoveryFallback: () => void;
 95→    handleRetry: () => void;
 96→  }
 97→) {
 98→  const { timeDisplay, isWarning, countdown, approvalError, onRecoveryFallback, handleRetry } =
 99→    props;
100→
101→  if (status === 'denied') {
102→    return (
103→      <div className="device-waiting-content">
104→        <div className="device-waiting-status-message device-waiting-denied">
105→          Request was denied by another device.
106→        </div>
107→        <div className="device-waiting-actions">
108→          <button type="button" className="device-waiting-btn" onClick={handleRetry}>
109→            --retry
110→          </button>
111→          <button type="button" className="device-waiting-link" onClick={onRecoveryFallback}>
112→            use recovery phrase instead
113→          </button>
114→        </div>
115→      </div>
116→    );
117→  }
118→
119→  if (status === 'expired') {
120→    return (
121→      <div className="device-waiting-content">
122→        <div className="device-waiting-status-message device-waiting-expired">
123→          Request expired. No device responded within the time limit.
124→        </div>
125→        <div className="device-waiting-actions">
126→          <button type="button" className="device-waiting-btn" onClick={handleRetry}>
127→            --retry
128→          </button>
129→          <button type="button" className="device-waiting-link" onClick={onRecoveryFallback}>
130→            use recovery phrase instead
131→          </button>
132→        </div>
133→      </div>
134→    );
135→  }
136→
137→  if (status === 'error') {
138→    return (
139→      <div className="device-waiting-content">
140→        <div className="device-waiting-status-message device-waiting-error">
141→          {approvalError || 'An error occurred.'}
142→        </div>
143→        <div className="device-waiting-actions">
144→          <button type="button" className="device-waiting-btn" onClick={handleRetry}>
145→            --retry
146→          </button>
147→          <button type="button" className="device-waiting-link" onClick={onRecoveryFallback}>
148→            use recovery phrase instead
149→          </button>
150→        </div>
151→      </div>
152→    );
153→  }
154→
155→  if (status === 'completing') {
156→    return (
157→      <div className="device-waiting-content">
158→        <span className="device-waiting-spinner" aria-hidden="true" />
159→        <p className="device-waiting-text">Approval received. Completing login...</p>
160→      </div>
161→    );
162→  }
163→
164→  // Default: requesting/pending
165→  return (
166→    <div className="device-waiting-content">
167→      <span className="device-waiting-spinner" aria-hidden="true" />
168→      <p className="device-waiting-text">
169→        Request sent to your other devices. Open CipherBox on an authorized device to approve this
170→        login.
171→      </p>
172→      <div
173→        className={`device-waiting-countdown ${isWarning ? 'warning' : ''}`}
174→        aria-label={`Time remaining: ${timeDisplay}`}
175→      >
176→        {countdown > 0 ? timeDisplay : 'expired'}
177→      </div>
178→      <button type="button" className="device-waiting-link" onClick={onRecoveryFallback}>
179→        use recovery phrase instead
180→      </button>
181→    </div>
182→  );
183→}
