  1→import { useCallback, useState } from 'react';
  2→import { useMfa } from '../../hooks/useMfa';
  3→import { useDeviceApproval } from '../../hooks/useDeviceApproval';
  4→
  5→type RecoveryInputProps = {
  6→  onRecoveryComplete: () => void;
  7→  onBack: () => void;
  8→};
  9→
 10→/**
 11→ * Recovery phrase input form for REQUIRED_SHARE state.
 12→ * Accepts a 24-word BIP39 mnemonic to recover account access
 13→ * on a new device.
 14→ *
 15→ * On successful recovery:
 16→ * 1. Cancels any pending approval request (Pitfall 5)
 17→ * 2. Creates a device factor for the new device
 18→ * 3. Calls onRecoveryComplete to resume login
 19→ */
 20→export function RecoveryInput({ onRecoveryComplete, onBack }: RecoveryInputProps) {
 21→  const { recoverWithMnemonic } = useMfa();
 22→  const { cancelRequest } = useDeviceApproval();
 23→
 24→  const [phrase, setPhrase] = useState('');
 25→  const [isRecovering, setIsRecovering] = useState(false);
 26→  const [error, setError] = useState<string | null>(null);
 27→
 28→  const handleRecover = useCallback(async () => {
 29→    const trimmed = phrase.trim().toLowerCase();
 30→    const words = trimmed.split(/\s+/);
 31→
 32→    if (words.length !== 24) {
 33→      setError(`Expected 24 words, got ${words.length}.`);
 34→      return;
 35→    }
 36→
 37→    setIsRecovering(true);
 38→    setError(null);
 39→
 40→    try {
 41→      // Recover using the mnemonic (inputs factor key + creates device factor)
 42→      await recoverWithMnemonic(trimmed);
 43→
 44→      // Cancel any pending approval request (RESEARCH.md Pitfall 5)
 45→      await cancelRequest();
 46→
 47→      onRecoveryComplete();
 48→    } catch (err) {
 49→      setError(err instanceof Error ? err.message : 'Recovery failed. Please check your phrase.');
 50→    } finally {
 51→      setIsRecovering(false);
 52→    }
 53→  }, [phrase, recoverWithMnemonic, cancelRequest, onRecoveryComplete]);
 54→
 55→  const handleKeyDown = useCallback(
 56→    (e: React.KeyboardEvent) => {
 57→      if (e.key === 'Enter' && !e.shiftKey) {
 58→        e.preventDefault();
 59→        handleRecover();
 60→      }
 61→    },
 62→    [handleRecover]
 63→  );
 64→
 65→  return (
 66→    <div className="recovery-input">
 67→      <h2 className="recovery-input-title">{'// enter your recovery phrase'}</h2>
 68→
 69→      <div className="recovery-input-content">
 70→        <p className="recovery-input-description">
 71→          Enter your 24-word recovery phrase to access your vault on this device.
 72→        </p>
 73→
 74→        <textarea
 75→          className="recovery-input-textarea"
 76→          value={phrase}
 77→          onChange={(e) => setPhrase(e.target.value)}
 78→          onKeyDown={handleKeyDown}
 79→          placeholder="Enter your 24-word recovery phrase..."
 80→          rows={4}
 81→          disabled={isRecovering}
 82→          autoFocus
 83→          spellCheck={false}
 84→          autoComplete="off"
 85→          autoCapitalize="off"
 86→        />
 87→
 88→        {error && (
 89→          <div className="recovery-input-error" role="alert">
 90→            {error}
 91→          </div>
 92→        )}
 93→      </div>
 94→
 95→      <div className="recovery-input-actions">
 96→        <button
 97→          type="button"
 98→          className="recovery-input-btn recovery-input-btn-secondary"
 99→          onClick={onBack}
100→          disabled={isRecovering}
101→        >
102→          --back
103→        </button>
104→        <button
105→          type="button"
106→          className="recovery-input-btn recovery-input-btn-primary"
107→          onClick={handleRecover}
108→          disabled={isRecovering || !phrase.trim()}
109→        >
110→          {isRecovering ? 'recovering...' : '--recover'}
111→        </button>
112→      </div>
113→    </div>
114→  );
115→}
