  1→import { useCallback, useEffect, useRef, useState } from 'react';
  2→import { useNavigate } from 'react-router-dom';
  3→import { useAuth } from '../../hooks/useAuth';
  4→import { useMfa } from '../../hooks/useMfa';
  5→import { useMfaStore } from '../../stores/mfa.store';
  6→import { useAuthStore } from '../../stores/auth.store';
  7→
  8→const STORAGE_KEY_PREFIX = 'cipherbox_mfa_prompt_dismissed_';
  9→
 10→/**
 11→ * Get the localStorage key for dismissal persistence.
 12→ * Uses a hash of userEmail to avoid persisting PII in localStorage.
 13→ */
 14→async function getDismissalKeyAsync(): Promise<string> {
 15→  const email = useAuthStore.getState().userEmail;
 16→  if (!email) return `${STORAGE_KEY_PREFIX}default`;
 17→  const encoded = new TextEncoder().encode(email);
 18→  const hashBuffer = await crypto.subtle.digest('SHA-256', encoded);
 19→  const hashHex = Array.from(new Uint8Array(hashBuffer))
 20→    .map((b) => b.toString(16).padStart(2, '0'))
 21→    .join('');
 22→  return `${STORAGE_KEY_PREFIX}${hashHex.slice(0, 16)}`;
 23→}
 24→
 25→/**
 26→ * Dismissable banner shown after login for non-MFA users.
 27→ * Nudges users to set up MFA from Settings > Security.
 28→ *
 29→ * Visibility rules:
 30→ * - Only shows when authenticated AND MFA is not enabled
 31→ * - Respects in-memory dismissal (mfa.store hasSeenPrompt)
 32→ * - Respects cross-session dismissal (localStorage keyed by user)
 33→ * - Shows once per session (first render after login)
 34→ *
 35→ * Mounted in AppShell above the main content area.
 36→ */
 37→export function MfaEnrollmentPrompt() {
 38→  const navigate = useNavigate();
 39→  const { isAuthenticated } = useAuth();
 40→  const { checkMfaStatus, isMfaEnabled } = useMfa();
 41→  const hasSeenPrompt = useMfaStore((s) => s.hasSeenPrompt);
 42→  const dismissPrompt = useMfaStore((s) => s.dismissPrompt);
 43→
 44→  const [visible, setVisible] = useState(false);
 45→  const checkedRef = useRef(false);
 46→
 47→  // Check MFA status on mount and determine visibility
 48→  useEffect(() => {
 49→    if (!isAuthenticated || checkedRef.current) return;
 50→    checkedRef.current = true;
 51→
 52→    const { isMfaEnabled: enabled } = checkMfaStatus();
 53→    if (enabled) return;
 54→
 55→    // Check in-memory dismissal
 56→    if (hasSeenPrompt) return;
 57→
 58→    // Check localStorage for cross-session dismissal (async due to hashing)
 59→    void getDismissalKeyAsync().then((key) => {
 60→      try {
 61→        if (localStorage.getItem(key) === 'true') return;
 62→      } catch {
 63→        // localStorage unavailable (incognito, etc.)
 64→      }
 65→      setVisible(true);
 66→    });
 67→  }, [isAuthenticated, checkMfaStatus, hasSeenPrompt]);
 68→
 69→  const handleDismiss = useCallback(() => {
 70→    setVisible(false);
 71→    dismissPrompt();
 72→
 73→    // Persist dismissal in localStorage (async due to hashing)
 74→    void getDismissalKeyAsync().then((key) => {
 75→      try {
 76→        localStorage.setItem(key, 'true');
 77→      } catch {
 78→        // localStorage unavailable
 79→      }
 80→    });
 81→  }, [dismissPrompt]);
 82→
 83→  const handleSetupMfa = useCallback(() => {
 84→    handleDismiss();
 85→    navigate('/settings?tab=security');
 86→  }, [handleDismiss, navigate]);
 87→
 88→  // Don't render if not visible or MFA already enabled
 89→  if (!visible || !isAuthenticated || isMfaEnabled) return null;
 90→
 91→  return (
 92→    <div className="mfa-prompt" role="banner">
 93→      <span className="mfa-prompt-text">
 94→        <strong>{'// secure your account'}</strong>
 95→        {' -- enable MFA to protect your vault on new devices.'}
 96→      </span>
 97→      <button
 98→        type="button"
 99→        className="mfa-prompt-btn mfa-prompt-btn-setup"
100→        onClick={handleSetupMfa}
101→      >
102→        --setup-mfa
103→      </button>
104→      <button
105→        type="button"
106→        className="mfa-prompt-dismiss"
107→        onClick={handleDismiss}
108→        aria-label="Dismiss MFA prompt"
109→      >
110→        [x]
111→      </button>
112→    </div>
113→  );
114→}
