 13→/**
 14→ * Wallet login button using wagmi for wallet connection and viem for SIWE.
 15→ *
 16→ * Flow:
 17→ * 1. Show "Connect Wallet" button
 18→ * 2. On click, show discovered EIP-6963 wallet connectors
 19→ * 3. User selects connector, wagmi connects, gets address
 20→ * 4. Fetch nonce from backend
 21→ * 5. Create SIWE message with viem
 22→ * 6. Prompt user to sign in wallet
 23→ * 7. Send message+signature to backend for verification
 24→ * 8. Receive CipherBox JWT and call onLogin
 25→ * 9. Disconnect wagmi (no persistent wallet connection needed)
 26→ */
 27→export function WalletLoginButton({ onLogin, disabled }: WalletLoginButtonProps) {
 28→  const { connectors, connectAsync, isPending: isConnecting } = useConnect();
 29→  const { address, isConnected } = useAccount();
 30→  const { signMessageAsync } = useSignMessage();
 31→  const { disconnect } = useDisconnect();
 32→
 33→  const [phase, setPhase] = useState<LoginPhase>('idle');
 34→  const [error, setError] = useState<string | null>(null);
 35→  const [showConnectors, setShowConnectors] = useState(false);
 36→
 37→  // Track whether SIWE flow is in progress to prevent double-triggers
 38→  const siweInProgress = useRef(false);
 39→
 40→  // When wagmi connects and we have an address, trigger the SIWE flow
 41→  useEffect(() => {
 42→    if (isConnected && address && phase === 'connecting' && !siweInProgress.current) {
 43→      siweInProgress.current = true;
 44→      handleSiweFlow(address);
 45→    }
 46→  }, [isConnected, address, phase]);
 47→
 48→  const handleSiweFlow = async (walletAddress: `0x${string}`) => {
 49→    try {
 50→      setPhase('signing');
 51→
 52→      // 1. Get nonce from backend
 53→      const { nonce } = await authApi.identityWalletNonce();
 54→
 55→      // 2. Create SIWE message
 56→      const message = createSiweMessage({
 57→        address: walletAddress,
 58→        chainId: 1,
 59→        domain: window.location.host,
 60→        nonce,
 61→        uri: window.location.origin,
 62→        version: '1',
 63→        statement: 'Sign in to CipherBox encrypted storage',
 64→      });
 65→
 66→      // 3. Sign message with wallet
 67→      const signature = await signMessageAsync({ message });
 68→
 69→      setPhase('verifying');
 70→
 71→      // 4. Verify on backend, get CipherBox JWT
 72→      const { idToken, userId } = await authApi.identityWalletVerify({
 73→        message,
 74→        signature,
 75→      });
 76→
 77→      // 5. Disconnect wagmi (we don't need persistent wallet connection)
 78→      disconnect();
 79→
 80→      // 6. Continue with Core Kit login
 81→      await onLogin(idToken, userId);
 82→
 83→      // Reset state on success
 84→      setPhase('idle');
 85→      setShowConnectors(false);
 86→    } catch (err) {
 87→      const message = err instanceof Error ? err.message : 'Wallet login failed';
 88→
 89→      // Handle user rejection specifically
 90→      if (
 91→        message.includes('User rejected') ||
 92→        message.includes('user rejected') ||
 93→        message.includes('ACTION_REJECTED')
 94→      ) {
 95→        setError('Signature request was rejected');
 96→      } else {
 97→        setError(message);
 98→      }
 99→
100→      // Clean up on failure
101→      disconnect();
102→      setPhase('idle');
103→      setShowConnectors(false);
104→    } finally {
105→      siweInProgress.current = false;
106→    }
107→  };
108→
109→  const handleConnectorClick = useCallback(
110→    async (connector: (typeof connectors)[0]) => {
111→      setError(null);
112→      setPhase('connecting');
113→
114→      try {
115→        const result = await connectAsync({ connector });
116→        const walletAddress = result.accounts[0];
117→
118→        if (!walletAddress) {
119→          throw new Error('No wallet address returned');
120→        }
121→
122→        // connectAsync resolves with the address, so we can start SIWE immediately
123→        siweInProgress.current = true;
124→        await handleSiweFlow(walletAddress);
125→      } catch (err) {
126→        const msg = err instanceof Error ? err.message : 'Connection failed';
127→
128→        if (
129→          msg.includes('User rejected') ||
130→          msg.includes('user rejected') ||
131→          msg.includes('ACTION_REJECTED')
132→        ) {
133→          setError('Wallet connection was rejected');
134→        } else if (msg.includes('No provider')) {
135→          setError('No wallet detected. Please install MetaMask or another wallet.');
136→        } else {
137→          setError(msg);
138→        }
139→
140→        setPhase('idle');
141→        setShowConnectors(false);
142→        siweInProgress.current = false;
143→      }
144→    },
145→    [connectAsync, signMessageAsync, disconnect, onLogin]
146→  );
147→
148→  const handleButtonClick = () => {
149→    if (phase !== 'idle') return;
150→    setError(null);
151→    setShowConnectors(true);
152→  };
153→
154→  const handleCancel = () => {
155→    setShowConnectors(false);
156→    setError(null);
157→    if (isConnected) {
158→      disconnect();
159→    }
160→    setPhase('idle');
161→    siweInProgress.current = false;
162→  };
163→
164→  const isWorking = phase !== 'idle';
165→  const isDisabled = disabled || isWorking;
166→
167→  const buttonText = () => {
168→    switch (phase) {
169→      case 'connecting':
170→        return 'connecting wallet...';
171→      case 'signing':
172→        return 'sign the message in your wallet...';
173→      case 'verifying':
174→        return 'verifying signature...';
175→      default:
176→        return '[WALLET]';
177→    }
178→  };
179→
180→  // Filter to unique named connectors (EIP-6963 can report duplicates)
181→  const uniqueConnectors = connectors.filter(
182→    (c, i, arr) => arr.findIndex((x) => x.name === c.name) === i
183→  );
184→
185→  return (
186→    <div className="wallet-login-wrapper">
187→      {!showConnectors ? (
188→        <button
189→          type="button"
190→          data-testid="wallet-login-button"
191→          className={['wallet-login-btn', isWorking ? 'wallet-login-btn--loading' : '']
192→            .filter(Boolean)
193→            .join(' ')}
194→          onClick={handleButtonClick}
195→          disabled={isDisabled}
196→          aria-label="Sign in with wallet"
197→          aria-busy={isWorking}
198→        >
199→          {buttonText()}
200→        </button>
201→      ) : (
202→        <div className="wallet-connector-list" role="group" aria-label="Available wallets">
203→          {phase !== 'idle' ? (
204→            <div className="wallet-login-status" aria-live="polite">
205→              {buttonText()}
206→            </div>
207→          ) : uniqueConnectors.length === 0 ? (
208→            <div className="wallet-no-providers">
209→              no wallets detected. install MetaMask or another browser wallet.
210→            </div>
211→          ) : (
212→            <>
213→              <div className="wallet-connector-header">{'// select wallet'}</div>
214→              {uniqueConnectors.map((connector) => (
215→                <button
216→                  key={connector.uid}
217→                  type="button"
218→                  className="wallet-connector-option"
219→                  onClick={() => handleConnectorClick(connector)}
220→                  disabled={isConnecting || isWorking}
221→                  aria-label={`Connect with ${connector.name}`}
222→                >
223→                  [{connector.name}]
224→                </button>
225→              ))}
226→            </>
227→          )}
228→          <button
229→            type="button"
230→            className="wallet-connector-cancel"
231→            onClick={handleCancel}
232→            disabled={phase === 'verifying'}
233→            aria-label="Cancel wallet connection"
234→          >
235→            {'// cancel'}
236→          </button>
237→        </div>
238→      )}
239→      {error && (
240→        <div className="login-error" role="alert" aria-live="polite">
241→          {error}
242→        </div>
243→      )}
244→    </div>
245→  );
246→}
