  1→import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
  2→import { useDeviceApproval } from '../../hooks/useDeviceApproval';
  3→import { useMfa } from '../../hooks/useMfa';
  4→import { useAuth } from '../../hooks/useAuth';
  5→import { useCoreKit } from '../../lib/web3auth/core-kit-provider';
  6→
  7→/**
  8→ * Modal overlay shown on an existing (already logged-in) device when
  9→ * pending approval requests exist. Displays device details, a warning,
 10→ * and --approve / --deny buttons.
 11→ *
 12→ * Mounted in AppShell so it appears regardless of which page the user is on.
 13→ * Only polls and renders when authenticated, MFA is enabled, and the user
 14→ * is NOT in REQUIRED_SHARE state themselves (they'd be the requester).
 15→ */
 16→export function DeviceApprovalModal() {
 17→  const { isAuthenticated, isRequiredShare } = useAuth();
 18→  const { isRequiredShare: coreKitRequiredShare } = useCoreKit();
 19→  const { checkMfaStatus, isMfaEnabled } = useMfa();
 20→  const { pollPendingRequests, stopApproverPolling, approveRequest, denyRequest, pendingRequests } =
 21→    useDeviceApproval();
 22→
 23→  const [isResponding, setIsResponding] = useState(false);
 24→  const [respondError, setRespondError] = useState<string | null>(null);
 25→  const checkedRef = useRef(false);
 26→
 27→  // Check MFA status on mount (once)
 28→  useEffect(() => {
 29→    if (isAuthenticated && !checkedRef.current) {
 30→      checkedRef.current = true;
 31→      checkMfaStatus();
 32→    }
 33→  }, [isAuthenticated, checkMfaStatus]);
 34→
 35→  // Start/stop polling based on auth + MFA state
 36→  useEffect(() => {
 37→    const shouldPoll = isAuthenticated && isMfaEnabled && !isRequiredShare && !coreKitRequiredShare;
 38→
 39→    if (shouldPoll) {
 40→      pollPendingRequests();
 41→    } else {
 42→      stopApproverPolling();
 43→    }
 44→
 45→    return () => {
 46→      stopApproverPolling();
 47→    };
 48→  }, [
 49→    isAuthenticated,
 50→    isMfaEnabled,
 51→    isRequiredShare,
 52→    coreKitRequiredShare,
 53→    pollPendingRequests,
 54→    stopApproverPolling,
 55→  ]);
 56→
 57→  // Show one request at a time (queue)
 58→  const currentRequest = useMemo(
 59→    () => (pendingRequests.length > 0 ? pendingRequests[0] : null),
 60→    [pendingRequests]
 61→  );
 62→
 63→  // Clear error state when the displayed request changes
 64→  useEffect(() => {
 65→    setRespondError(null);
 66→  }, [currentRequest?.requestId]);
 67→
 68→  const handleApprove = useCallback(async () => {
 69→    if (!currentRequest) return;
 70→    setIsResponding(true);
 71→    setRespondError(null);
 72→    try {
 73→      await approveRequest(currentRequest.requestId!, currentRequest.ephemeralPublicKey!);
 74→    } catch (err) {
 75→      setRespondError(err instanceof Error ? err.message : 'Failed to approve');
 76→    } finally {
 77→      setIsResponding(false);
 78→    }
 79→  }, [currentRequest, approveRequest]);
 80→
 81→  const handleDeny = useCallback(async () => {
 82→    if (!currentRequest) return;
 83→    setIsResponding(true);
 84→    setRespondError(null);
 85→    try {
 86→      await denyRequest(currentRequest.requestId!);
 87→    } catch (err) {
 88→      setRespondError(err instanceof Error ? err.message : 'Failed to deny');
 89→    } finally {
 90→      setIsResponding(false);
 91→    }
 92→  }, [currentRequest, denyRequest]);
 93→
 94→  // Don't render if no pending requests
 95→  if (!currentRequest) return null;
 96→
 97→  const expiresAt = new Date(currentRequest.expiresAt!).getTime();
 98→  const createdAt = new Date(currentRequest.createdAt!).getTime();
 99→
100→  return (
101→    <div
102→      className="device-approval-backdrop"
103→      role="dialog"
104→      aria-modal="true"
105→      aria-label="Device approval request"
106→    >
107→      <div className="device-approval-modal">
108→        <div className="device-approval-header">
109→          <h2 className="device-approval-title">Device Approval Request</h2>
110→        </div>
111→
112→        <div className="device-approval-body">
113→          <div className="device-approval-details">
114→            <div className="device-approval-detail-row">
115→              <span className="device-approval-detail-label">device</span>
116→              <span className="device-approval-detail-value">{currentRequest.deviceName}</span>
117→            </div>
118→            <div className="device-approval-detail-row">
119→              <span className="device-approval-detail-label">requested</span>
120→              <span className="device-approval-detail-value">{formatTimeAgo(createdAt)}</span>
121→            </div>
122→            <div className="device-approval-detail-row">
123→              <span className="device-approval-detail-label">expires</span>
124→              <CountdownValue expiresAt={expiresAt} />
125→            </div>
126→          </div>
127→
128→          <div className="device-approval-warning">
129→            Only approve if YOU are trying to log in on another device.
130→          </div>
131→
132→          {respondError && (
133→            <div className="device-approval-warning" role="alert">
134→              {respondError}
135→            </div>
136→          )}
137→
138→          {pendingRequests.length > 1 && (
139→            <div style={{ fontSize: '10px', color: 'var(--color-text-dim)' }}>
140→              {`+${pendingRequests.length - 1} more pending request${pendingRequests.length > 2 ? 's' : ''}`}
141→            </div>
142→          )}
143→        </div>
144→
145→        <div className="device-approval-actions">
146→          <button
147→            type="button"
148→            className="device-approval-btn-deny"
149→            onClick={handleDeny}
150→            disabled={isResponding}
151→          >
152→            --deny
153→          </button>
154→          <button
155→            type="button"
156→            className="device-approval-btn-approve"
157→            onClick={handleApprove}
158→            disabled={isResponding}
159→          >
160→            {isResponding ? 'approving...' : '--approve'}
161→          </button>
162→        </div>
163→      </div>
164→    </div>
165→  );
166→}
167→
168→/**
169→ * Live countdown to expiry time.
170→ */
171→function CountdownValue({ expiresAt }: { expiresAt: number }) {
172→  const [remaining, setRemaining] = useState(() => Math.max(0, expiresAt - Date.now()));
173→  const intervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
174→
175→  useEffect(() => {
176→    intervalRef.current = setInterval(() => {
177→      setRemaining(Math.max(0, expiresAt - Date.now()));
178→    }, 1000);
179→    return () => {
180→      if (intervalRef.current) clearInterval(intervalRef.current);
181→    };
182→  }, [expiresAt]);
183→
184→  const minutes = Math.floor(remaining / 60000);
185→  const seconds = Math.floor((remaining % 60000) / 1000);
186→  const isWarning = remaining <= 3 * 60 * 1000 && remaining > 0;
187→
188→  return (
189→    <span
190→      className={`device-approval-detail-value device-approval-countdown ${isWarning ? 'warning' : ''}`}
191→    >
192→      {remaining > 0 ? `${minutes}:${seconds.toString().padStart(2, '0')}` : 'expired'}
193→    </span>
194→  );
195→}
196→
197→/**
198→ * Format a timestamp as a relative time string (e.g. "just now", "2m ago").
199→ */
200→function formatTimeAgo(timestampMs: number): string {
201→  const diff = Date.now() - timestampMs;
202→  if (diff < 60_000) return 'just now';
203→  if (diff < 3_600_000) return `${Math.floor(diff / 60_000)}m ago`;
204→  return `${Math.floor(diff / 3_600_000)}h ago`;
205→}
