  1→import { useCallback, useEffect, useState } from 'react';
  2→import { useMfa } from '../../hooks/useMfa';
  3→import { RecoveryPhraseGrid } from './RecoveryPhraseGrid';
  4→
  5→type MfaEnrollmentWizardProps = {
  6→  onComplete: () => void;
  7→  onCancel: () => void;
  8→};
  9→
 10→type WizardStep = 1 | 2 | 3;
 11→
 12→/**
 13→ * Step-by-step MFA enrollment wizard.
 14→ *
 15→ * Step 1: Explain what MFA does and that it cannot be undone.
 16→ * Step 2: Enable MFA, display recovery phrase, require acknowledgment.
 17→ * Step 3: Confirmation that MFA is enabled with factor count.
 18→ */
 19→export function MfaEnrollmentWizard({ onComplete, onCancel }: MfaEnrollmentWizardProps) {
 20→  const { enableMfa, checkMfaStatus, factorCount, threshold } = useMfa();
 21→
 22→  const [step, setStep] = useState<WizardStep>(1);
 23→  const [isLoading, setIsLoading] = useState(false);
 24→  const [error, setError] = useState<string | null>(null);
 25→  const [mnemonic, setMnemonic] = useState<string[]>([]);
 26→  const [partiallyEnabled, setPartiallyEnabled] = useState(false);
 27→  const [acknowledged, setAcknowledged] = useState(false);
 28→
 29→  const handleEnableMfa = useCallback(async () => {
 30→    setIsLoading(true);
 31→    setError(null);
 32→    try {
 33→      const phrase = await enableMfa();
 34→      setMnemonic(phrase.split(' '));
 35→      setIsLoading(false);
 36→    } catch (err) {
 37→      setIsLoading(false);
 38→      // If MFA was partially enabled (server succeeded but post-check failed),
 39→      // guide user to recovery instead of a broken retry loop
 40→      const mfaStatus = checkMfaStatus();
 41→      if (mfaStatus.isMfaEnabled) {
 42→        setPartiallyEnabled(true);
 43→        setError(
 44→          'MFA was enabled but an error occurred. Please close this dialog and use ' +
 45→            '"Regenerate Recovery Phrase" in Settings > Security to obtain your recovery phrase.'
 46→        );
 47→      } else {
 48→        setError(err instanceof Error ? err.message : 'Failed to enable MFA');
 49→      }
 50→    }
 51→  }, [enableMfa, checkMfaStatus]);
 52→
 53→  const handleGoToStep2 = useCallback(() => {
 54→    setStep(2);
 55→    if (mnemonic.length === 0) {
 56→      handleEnableMfa();
 57→    }
 58→  }, [handleEnableMfa, mnemonic.length]);
 59→
 60→  const handleGoToStep3 = useCallback(() => {
 61→    setMnemonic([]);
 62→    setStep(3);
 63→  }, []);
 64→
 65→  const handleGoBackToStep1 = useCallback(() => {
 66→    // Don't clear mnemonic -- if user goes back to step 1 and forward again,
 67→    // we skip re-calling enableMfa() to avoid generating a different phrase.
 68→    setStep(1);
 69→  }, []);
 70→
 71→  // Clear mnemonic from memory on unmount
 72→  useEffect(() => () => setMnemonic([]), []);
 73→
 74→  const toggleAcknowledged = useCallback(() => {
 75→    setAcknowledged((prev) => !prev);
 76→  }, []);
 77→
 78→  const handleCheckboxKeyDown = useCallback(
 79→    (e: React.KeyboardEvent) => {
 80→      if (e.key === 'Enter' || e.key === ' ') {
 81→        e.preventDefault();
 82→        toggleAcknowledged();
 83→      }
 84→    },
 85→    [toggleAcknowledged]
 86→  );
 87→
 88→  return (
 89→    <div className="mfa-wizard">
 90→      {/* Step indicator */}
 91→      <div className="mfa-wizard-step-indicator">
 92→        <span className="mfa-wizard-step-label">{'step ' + step + ' of 3'}</span>
 93→        <div className="mfa-wizard-progress-bar">
 94→          <div className={`mfa-wizard-progress-segment ${step >= 1 ? 'complete' : ''}`} />
 95→          <div className={`mfa-wizard-progress-segment ${step >= 2 ? 'complete' : ''}`} />
 96→          <div className={`mfa-wizard-progress-segment ${step >= 3 ? 'complete' : ''}`} />
 97→        </div>
 98→      </div>
 99→
100→      {/* Step 1: Explain MFA */}
101→      {step === 1 && (
102→        <div className="mfa-wizard-step">
103→          <h3 className="mfa-wizard-step-title">{'// enable multi-factor authentication'}</h3>
104→          <div className="mfa-wizard-step-content">
105→            <p className="mfa-wizard-text">
106→              MFA adds an extra layer of security to your vault. When enabled, logging in from a new
107→              device will require approval from an existing device or your recovery phrase.
108→            </p>
109→            <p className="mfa-wizard-text">
110→              Known devices will continue to log in seamlessly -- MFA only challenges new or unknown
111→              devices.
112→            </p>
113→            <div className="mfa-wizard-warning-box">
114→              <p className="mfa-wizard-warning-text">
115→                Enabling MFA is permanent and cannot be undone. You can manage individual factors
116→                (devices, recovery phrase) after enrollment, but MFA itself stays active.
117→              </p>
118→            </div>
119→          </div>
120→          <div className="mfa-wizard-actions">
121→            <button
122→              type="button"
123→              className="mfa-wizard-btn mfa-wizard-btn-secondary"
124→              onClick={onCancel}
125→            >
126→              --cancel
127→            </button>
128→            <button
129→              type="button"
130→              className="mfa-wizard-btn mfa-wizard-btn-primary"
131→              onClick={handleGoToStep2}
132→            >
133→              --continue
134→            </button>
135→          </div>
136→        </div>
137→      )}
138→
139→      {/* Step 2: Recovery Phrase */}
140→      {step === 2 && (
141→        <div className="mfa-wizard-step">
142→          <h3 className="mfa-wizard-step-title">{'// recovery phrase'}</h3>
143→          <div className="mfa-wizard-step-content">
144→            {isLoading && (
145→              <div className="mfa-wizard-loading">
146→                <span className="mfa-wizard-spinner" aria-hidden="true" />
147→                enabling MFA and generating recovery phrase...
148→              </div>
149→            )}
150→
151→            {error && (
152→              <div className="mfa-wizard-error" role="alert">
153→                <p>{error}</p>
154→                {!partiallyEnabled && (
155→                  <button
156→                    type="button"
157→                    className="mfa-wizard-btn mfa-wizard-btn-secondary"
158→                    onClick={handleEnableMfa}
159→                  >
160→                    --retry
161→                  </button>
162→                )}
163→              </div>
164→            )}
165→
166→            {!isLoading && !error && mnemonic.length > 0 && (
167→              <>
168→                <p className="mfa-wizard-text">
169→                  Write down these 24 words in order and store them in a safe place. This is the
170→                  only way to recover your vault if you lose all your devices.
171→                </p>
172→
173→                <RecoveryPhraseGrid words={mnemonic} />
174→
175→                <div className="mfa-wizard-danger-box" role="alert">
176→                  If you lose this phrase and all your devices, your account CANNOT be recovered.
177→                </div>
178→
179→                <div
180→                  className={`mfa-wizard-checkbox ${acknowledged ? 'checked' : ''}`}
181→                  role="checkbox"
182→                  aria-checked={acknowledged}
183→                  tabIndex={0}
184→                  onClick={toggleAcknowledged}
185→                  onKeyDown={handleCheckboxKeyDown}
186→                >
187→                  <span className="mfa-wizard-checkbox-box" aria-hidden="true">
188→                    {acknowledged ? '[x]' : '[ ]'}
189→                  </span>
190→                  <span className="mfa-wizard-checkbox-label">
191→                    I have written down and safely stored my recovery phrase
192→                  </span>
193→                </div>
194→              </>
195→            )}
196→          </div>
197→
198→          <div className="mfa-wizard-actions">
199→            <button
200→              type="button"
201→              className="mfa-wizard-btn mfa-wizard-btn-secondary"
202→              onClick={handleGoBackToStep1}
203→            >
204→              --back
205→            </button>
206→            <button
207→              type="button"
208→              className="mfa-wizard-btn mfa-wizard-btn-primary"
209→              onClick={handleGoToStep3}
210→              disabled={!acknowledged || isLoading || !!error}
211→            >
212→              --continue
213→            </button>
214→          </div>
215→        </div>
216→      )}
217→
218→      {/* Step 3: Success */}
219→      {step === 3 && (
220→        <div className="mfa-wizard-step">
221→          <h3 className="mfa-wizard-step-title">{'// mfa enabled'}</h3>
222→          <div className="mfa-wizard-step-content">
223→            <div className="mfa-wizard-success">
224→              <span className="mfa-wizard-success-icon" aria-hidden="true">
225→                [OK]
226→              </span>
227→              <p className="mfa-wizard-text">
228→                Multi-factor authentication has been successfully enabled on your account.
229→              </p>
230→            </div>
231→            <div className="mfa-wizard-factor-summary">
232→              <span className="mfa-wizard-factor-count">
233→                {factorCount} factors active, {threshold}/{factorCount} threshold
234→              </span>
235→            </div>
236→            <p className="mfa-wizard-text mfa-wizard-text-dim">
237→              New devices will require approval from an existing device or your recovery phrase. You
238→              can manage your factors from Settings &gt; Security at any time.
239→            </p>
240→          </div>
241→          <div className="mfa-wizard-actions">
242→            <button
243→              type="button"
244→              className="mfa-wizard-btn mfa-wizard-btn-primary"
245→              onClick={onComplete}
246→            >
247→              --done
248→            </button>
249→          </div>
250→        </div>
251→      )}
252→    </div>
253→  );
254→}
