  1→import { useCallback } from 'react';
  2→import {
  3→  COREKIT_STATUS,
  4→  generateFactorKey,
  5→  keyToMnemonic,
  6→  mnemonicToKey,
  7→  TssShareType,
  8→  FactorKeyTypeShareDescription,
  9→  factorKeyCurve,
 10→} from '@web3auth/mpc-core-kit';
 11→import { Point } from '@tkey/common-types';
 12→import BN from 'bn.js';
 13→import { useCoreKit } from '../lib/web3auth/core-kit-provider';
 14→import { useMfaStore } from '../stores/mfa.store';
 15→
 16→export type FactorInfo = {
 17→  factorPubHex: string;
 18→  description: string;
 19→  type: string;
 20→  /** Additional metadata set during createFactor (e.g. deviceId, browserName) */
 21→  additionalMetadata?: Record<string, string>;
 22→};
 23→
 24→export function useMfa() {
 25→  const { coreKit } = useCoreKit();
 26→
 27→  const isMfaEnabled = useMfaStore((s) => s.isMfaEnabled);
 28→  const isEnrolling = useMfaStore((s) => s.isEnrolling);
 29→  const factorCount = useMfaStore((s) => s.factorCount);
 30→  const threshold = useMfaStore((s) => s.threshold);
 31→
 32→  /**
 33→   * Check MFA status from Core Kit key details.
 34→   * MFA is enabled when totalFactors >= 2.
 35→   */
 36→  const checkMfaStatus = useCallback((): {
 37→    isMfaEnabled: boolean;
 38→    totalFactors: number;
 39→    threshold: number;
 40→  } => {
 41→    if (!coreKit || coreKit.status !== COREKIT_STATUS.LOGGED_IN) {
 42→      return { isMfaEnabled: false, totalFactors: 0, threshold: 0 };
 43→    }
 44→    try {
 45→      const details = coreKit.getKeyDetails();
 46→      const enabled = details.totalFactors >= 2;
 47→      useMfaStore.getState().setMfaEnabled(enabled);
 48→      useMfaStore.getState().setFactorDetails(details.totalFactors, details.threshold);
 49→      return {
 50→        isMfaEnabled: enabled,
 51→        totalFactors: details.totalFactors,
 52→        threshold: details.threshold,
 53→      };
 54→    } catch {
 55→      return { isMfaEnabled: false, totalFactors: 0, threshold: 0 };
 56→    }
 57→  }, [coreKit]);
 58→
 59→  /**
 60→   * Enable MFA. Returns 24-word recovery mnemonic.
 61→   * CRITICAL: commitChanges() must be called before enableMFA() in manualSync mode.
 62→   */
 63→  const enableMfa = useCallback(async (): Promise<string> => {
 64→    if (!coreKit) throw new Error('Core Kit not initialized');
 65→    if (coreKit.status !== COREKIT_STATUS.LOGGED_IN) {
 66→      throw new Error('Must be logged in to enable MFA');
 67→    }
 68→
 69→    const store = useMfaStore.getState();
 70→    if (store.isMfaEnabled) {
 71→      throw new Error('MFA is already enabled');
 72→    }
 73→
 74→    useMfaStore.getState().setEnrolling(true);
 75→    try {
 76→      // Commit any pending changes first (CRITICAL for manualSync mode -- Pitfall 1)
 77→      await coreKit.commitChanges();
 78→
 79→      // Capture TSS public key BEFORE enableMFA for stability verification (MFA-04)
 80→      const preMfaTssPub = coreKit.getKeyDetails().tssPubKey;
 81→
 82→      // enableMFA creates device factor + recovery factor atomically
 83→      const backupFactorKeyHex = await coreKit.enableMFA({});
 84→
 85→      // Commit the MFA changes
 86→      await coreKit.commitChanges();
 87→
 88→      // Defensive check: verify TSS public key unchanged after MFA enrollment (MFA-04)
 89→      const postMfaTssPub = coreKit.getKeyDetails().tssPubKey;
 90→      if (preMfaTssPub?.x && preMfaTssPub?.y && postMfaTssPub?.x && postMfaTssPub?.y) {
 91→        const preX = preMfaTssPub.x.toString('hex');
 92→        const preY = preMfaTssPub.y.toString('hex');
 93→        const postX = postMfaTssPub.x.toString('hex');
 94→        const postY = postMfaTssPub.y.toString('hex');
 95→        if (preX !== postX || preY !== postY) {
 96→          throw new Error(
 97→            'MFA enrollment failed: TSS public key changed. Your keypair identity is no longer stable. Please contact support.'
 98→          );
 99→        }
100→      }
101→
102→      // Convert to 24-word mnemonic for user display
103→      const mnemonic = keyToMnemonic(backupFactorKeyHex);
104→
105→      // Update store with new key details
106→      const details = coreKit.getKeyDetails();
107→      useMfaStore.getState().setMfaEnabled(true);
108→      useMfaStore.getState().setFactorDetails(details.totalFactors, details.threshold);
109→
110→      return mnemonic;
111→    } finally {
112→      useMfaStore.getState().setEnrolling(false);
113→    }
114→  }, [coreKit]);
115→
116→  /**
117→   * Input a factor key (hex string) to complete REQUIRED_SHARE login.
118→   * Used for both recovery and cross-device approval flows.
119→   */
120→  const inputFactorKey = useCallback(
121→    async (factorKeyHex: string): Promise<void> => {
122→      if (!coreKit) throw new Error('Core Kit not initialized');
123→      const factorKey = new BN(factorKeyHex, 'hex');
124→      await coreKit.inputFactorKey(factorKey);
125→    },
126→    [coreKit]
127→  );
128→
129→  /**
130→   * Recover with a 24-word mnemonic phrase.
131→   * After recovery, creates a device factor for the new device.
132→   */
133→  const recoverWithMnemonic = useCallback(
134→    async (mnemonic: string): Promise<void> => {
135→      if (!coreKit) throw new Error('Core Kit not initialized');
136→
137→      // Convert mnemonic to factor key hex
138→      const factorKeyHex = mnemonicToKey(mnemonic.trim().toLowerCase());
139→      await inputFactorKey(factorKeyHex);
140→
141→      // Create a device factor for this new device
142→      const newDeviceFactor = generateFactorKey();
143→      await coreKit.createFactor({
144→        shareType: TssShareType.DEVICE,
145→        factorKey: newDeviceFactor.private,
146→        shareDescription: FactorKeyTypeShareDescription.DeviceShare,
147→      });
148→      await coreKit.setDeviceFactor(newDeviceFactor.private);
149→      await coreKit.commitChanges();
150→    },
151→    [coreKit, inputFactorKey]
152→  );
153→
154→  /**
155→   * Get all factors with their descriptions.
156→   * shareDescriptions is Record<string, string[]> where keys are factor pub hex.
157→   */
158→  const getFactors = useCallback((): FactorInfo[] => {
159→    if (!coreKit || coreKit.status !== COREKIT_STATUS.LOGGED_IN) return [];
160→    try {
161→      const details = coreKit.getKeyDetails();
162→      const factors: FactorInfo[] = [];
163→
164→      for (const [factorPubHex, descriptions] of Object.entries(details.shareDescriptions)) {
165→        for (const descJson of descriptions) {
166→          try {
167→            const parsed = JSON.parse(descJson) as {
168→              module?: string;
169→              additionalMetadata?: Record<string, string>;
170→            };
171→            factors.push({
172→              factorPubHex,
173→              description: parsed.module || 'unknown',
174→              type: parsed.module || 'unknown',
175→              additionalMetadata: parsed.additionalMetadata,
176→            });
177→          } catch {
178→            factors.push({
179→              factorPubHex,
180→              description: 'unknown',
181→              type: 'unknown',
182→            });
183→          }
184→        }
185→      }
186→
187→      return factors;
188→    } catch {
189→      return [];
190→    }
191→  }, [coreKit]);
192→
193→  /**
194→   * Delete a factor by its public key hex.
195→   * Cannot delete the currently active factor or the last remaining factor.
196→   */
197→  const deleteFactor = useCallback(
198→    async (factorPubHex: string): Promise<void> => {
199→      if (!coreKit) throw new Error('Core Kit not initialized');
200→      const factorPub = Point.fromSEC1(factorKeyCurve, factorPubHex);
201→      await coreKit.deleteFactor(factorPub);
202→      await coreKit.commitChanges();
203→    },
204→    [coreKit]
205→  );
206→
207→  /**
208→   * Get the current active factor key and share type.
209→   */
210→  const getCurrentFactorKey = useCallback((): {
211→    factorKey: BN;
212→    shareType: TssShareType;
213→  } | null => {
214→    if (!coreKit || coreKit.status !== COREKIT_STATUS.LOGGED_IN) return null;
215→    try {
216→      return coreKit.getCurrentFactorKey();
217→    } catch {
218→      return null;
219→    }
220→  }, [coreKit]);
221→
222→  /**
223→   * Regenerate the recovery phrase.
224→   * Deletes the old recovery factor and creates a new one.
225→   * Returns the new 24-word mnemonic.
226→   */
227→  const regenerateRecoveryPhrase = useCallback(async (): Promise<string> => {
228→    if (!coreKit) throw new Error('Core Kit not initialized');
229→    if (coreKit.status !== COREKIT_STATUS.LOGGED_IN) {
230→      throw new Error('Must be logged in to regenerate recovery phrase');
231→    }
232→
233→    // Find the existing recovery (seedPhrase) factor
234→    const factors = getFactors();
235→    const recoveryFactor = factors.find((f) => f.type === FactorKeyTypeShareDescription.SeedPhrase);
236→
237→    if (recoveryFactor) {
238→      // Delete old recovery factor
239→      const factorPub = Point.fromSEC1(factorKeyCurve, recoveryFactor.factorPubHex);
240→      await coreKit.deleteFactor(factorPub);
241→    }
242→
243→    // Generate new recovery factor
244→    const newRecovery = generateFactorKey();
245→    await coreKit.createFactor({
246→      shareType: TssShareType.RECOVERY,
247→      factorKey: newRecovery.private,
248→      shareDescription: FactorKeyTypeShareDescription.SeedPhrase,
249→    });
250→    await coreKit.commitChanges();
251→
252→    // Convert the new factor key to mnemonic
253→    // Zero-pad to 64 hex chars (32 bytes)
254→    const factorKeyHex = newRecovery.private.toString('hex').padStart(64, '0');
255→    return keyToMnemonic(factorKeyHex);
256→  }, [coreKit, getFactors]);
257→
258→  return {
259→    // State from store
260→    isMfaEnabled,
261→    isEnrolling,
262→    factorCount,
263→    threshold,
264→
265→    // Operations
266→    checkMfaStatus,
267→    enableMfa,
268→    inputFactorKey,
269→    recoverWithMnemonic,
270→    getFactors,
271→    deleteFactor,
272→    getCurrentFactorKey,
273→    regenerateRecoveryPhrase,
274→  };
275→}
