  1→import * as secp256k1 from '@noble/secp256k1';
  2→import { hexToBytes, bytesToHex } from '@cipherbox/crypto';
  3→import { useCoreKit } from './core-kit-provider';
  4→import { COREKIT_STATUS } from './core-kit';
  5→import { authApi } from '../api/auth';
  6→
  7→export type CoreKitLoginResult = 'logged_in' | 'required_share';
  8→
  9→export function useCoreKitAuth() {
 10→  const { coreKit, status, isLoggedIn, isInitialized, isRequiredShare } = useCoreKit();
 11→
 12→  /**
 13→   * Shared Core Kit login logic: takes a CipherBox JWT and userId,
 14→   * calls loginWithJWT and commitChanges.
 15→   *
 16→   * Returns 'logged_in' if device factor found, 'required_share' if MFA
 17→   * is enabled but this device lacks a factor (caller should show recovery UI).
 18→   */
 19→  async function loginWithCoreKit(
 20→    cipherboxJwt: string,
 21→    userId: string
 22→  ): Promise<CoreKitLoginResult> {
 23→    if (!coreKit) throw new Error('Core Kit not initialized');
 24→
 25→    // Login to Core Kit with CipherBox JWT
 26→    // eslint-disable-next-line @typescript-eslint/no-explicit-any
 27→    const loginParams: any = {
 28→      verifier: 'cipherbox-identity', // Single custom verifier for all CipherBox auth
 29→      verifierId: userId,
 30→      idToken: cipherboxJwt,
 31→    };
 32→
 33→    await coreKit.loginWithJWT(loginParams);
 34→
 35→    // Handle status
 36→    if (coreKit.status === COREKIT_STATUS.LOGGED_IN) {
 37→      await coreKit.commitChanges();
 38→      return 'logged_in';
 39→    }
 40→
 41→    // REQUIRED_SHARE means MFA is enabled but device factor missing.
 42→    // Return without throwing -- the caller (useAuth) will handle this
 43→    // by obtaining a temporary backend token and showing recovery/approval UI.
 44→    if (coreKit.status === COREKIT_STATUS.REQUIRED_SHARE) {
 45→      return 'required_share';
 46→    }
 47→
 48→    throw new Error(`Unexpected Core Kit status: ${coreKit.status}`);
 49→  }
 50→
 51→  /**
 52→   * Login with Google: Backend verifies Google idToken, issues CipherBox JWT,
 53→   * then we call loginWithJWT on Core Kit.
 54→   *
 55→   * @param googleIdToken - Google OAuth idToken from GIS callback
 56→   */
 57→  async function loginWithGoogle(
 58→    googleIdToken: string
 59→  ): Promise<{ cipherboxJwt: string; email?: string; userId: string; status: CoreKitLoginResult }> {
 60→    // 1. Send Google idToken to CipherBox backend for verification + JWT issuance
 61→    const { idToken: cipherboxJwt, userId, email } = await authApi.identityGoogle(googleIdToken);
 62→
 63→    // 2. Login to Core Kit
 64→    const coreKitStatus = await loginWithCoreKit(cipherboxJwt, userId);
 65→
 66→    return { cipherboxJwt, email, userId, status: coreKitStatus };
 67→  }
 68→
 69→  /**
 70→   * Login with Email: Backend handles OTP send/verify, issues CipherBox JWT.
 71→   *
 72→   * @param email - User's email address
 73→   * @param otp - One-time password from email
 74→   */
 75→  async function loginWithEmailOtp(
 76→    email: string,
 77→    otp: string
 78→  ): Promise<{ cipherboxJwt: string; userId: string; status: CoreKitLoginResult }> {
 79→    // 1. Verify OTP with backend, get CipherBox JWT
 80→    const { idToken: cipherboxJwt, userId } = await authApi.identityEmailVerify(email, otp);
 81→
 82→    // 2. Login to Core Kit
 83→    const coreKitStatus = await loginWithCoreKit(cipherboxJwt, userId);
 84→
 85→    return { cipherboxJwt, userId, status: coreKitStatus };
 86→  }
 87→
 88→  /**
 89→   * Login with Wallet: Backend verifies SIWE signature, issues CipherBox JWT,
 90→   * then we call loginWithJWT on Core Kit.
 91→   *
 92→   * @param cipherboxJwt - CipherBox identity JWT from wallet verification
 93→   * @param userId - CipherBox user ID
 94→   */
 95→  async function loginWithWallet(
 96→    cipherboxJwt: string,
 97→    userId: string
 98→  ): Promise<{ cipherboxJwt: string; userId: string; status: CoreKitLoginResult }> {
 99→    // Login to Core Kit with the CipherBox JWT
100→    const coreKitStatus = await loginWithCoreKit(cipherboxJwt, userId);
101→
102→    return { cipherboxJwt, userId, status: coreKitStatus };
103→  }
104→
105→  /**
106→   * Get vault keypair from Core Kit's exported TSS key.
107→   * Replaces the PnP provider.request('private_key') approach.
108→   */
109→  async function getVaultKeypair(): Promise<{
110→    publicKey: Uint8Array;
111→    privateKey: Uint8Array;
112→  } | null> {
113→    if (!coreKit || coreKit.status !== COREKIT_STATUS.LOGGED_IN) return null;
114→
115→    try {
116→      const privateKeyHex = await coreKit._UNSAFE_exportTssKey();
117→      const privKeyHex = privateKeyHex.startsWith('0x') ? privateKeyHex.slice(2) : privateKeyHex;
118→      const privateKey = hexToBytes(privKeyHex);
119→      const publicKey = secp256k1.getPublicKey(privateKey, false); // uncompressed (65 bytes)
120→      return { publicKey, privateKey };
121→    } catch (err) {
122→      console.error('[CoreKit] Failed to export TSS key:', err);
123→      return null;
124→    }
125→  }
126→
127→  /**
128→   * Get compressed public key hex for backend auth.
129→   */
130→  async function getPublicKeyHex(): Promise<string | null> {
131→    const keypair = await getVaultKeypair();
132→    if (!keypair) return null;
133→    const compressed = secp256k1.getPublicKey(keypair.privateKey, true);
134→    return bytesToHex(compressed);
135→  }
136→
137→  /**
138→   * Logout from Core Kit.
139→   */
140→  async function logout(): Promise<void> {
141→    if (!coreKit) return;
142→    try {
143→      if (coreKit.status === COREKIT_STATUS.LOGGED_IN) {
144→        await coreKit.logout();
145→      }
146→    } catch (err) {
147→      console.error('[CoreKit] Logout error:', err);
148→    }
149→  }
150→
151→  return {
152→    status,
153→    isLoggedIn,
154→    isInitialized,
155→    isRequiredShare,
156→    loginWithGoogle,
157→    loginWithEmailOtp,
158→    loginWithWallet,
159→    getVaultKeypair,
160→    getPublicKeyHex,
161→    logout,
162→  };
163→}
