 3→type MfaState = {
 4→  /** Whether MFA is active for the current user */
 5→  isMfaEnabled: boolean;
 6→  /** True during enableMFA() call */
 7→  isEnrolling: boolean;
 8→  /** Total factors from getKeyDetails() */
 9→  factorCount: number;
10→  /** Threshold from getKeyDetails() */
11→  threshold: number;
12→  /** Whether user has dismissed the enrollment prompt this session */
13→  hasSeenPrompt: boolean;
14→
15→  setMfaEnabled: (enabled: boolean) => void;
16→  setEnrolling: (enrolling: boolean) => void;
17→  setFactorDetails: (count: number, threshold: number) => void;
18→  dismissPrompt: () => void;
19→  reset: () => void;
20→};
21→
22→export const useMfaStore = create<MfaState>((set) => ({
23→  isMfaEnabled: false,
24→  isEnrolling: false,
25→  factorCount: 0,
26→  threshold: 0,
27→  hasSeenPrompt: false,
28→
29→  setMfaEnabled: (enabled) => set({ isMfaEnabled: enabled }),
30→  setEnrolling: (enrolling) => set({ isEnrolling: enrolling }),
31→  setFactorDetails: (count, threshold) => set({ factorCount: count, threshold }),
32→  dismissPrompt: () => set({ hasSeenPrompt: true }),
33→  reset: () =>
34→    set({
35→      isMfaEnabled: false,
36→      isEnrolling: false,
37→      factorCount: 0,
38→      threshold: 0,
39→      hasSeenPrompt: false,
40→    }),
41→}));
