  1→import { useCallback, useMemo, useState } from 'react';
  2→import { useMfa } from '../../hooks/useMfa';
  3→import { useDeviceRegistryStore } from '../../stores/device-registry.store';
  4→import { FactorKeyTypeShareDescription } from '@web3auth/mpc-core-kit';
  5→
  6→type DeviceFactorDisplay = {
  7→  factorPubHex: string;
  8→  name: string;
  9→  lastActive: string;
 10→  isCurrent: boolean;
 11→};
 12→
 13→/**
 14→ * Authorized Devices list for the Security tab.
 15→ *
 16→ * Shows device share factors matched against the device registry for
 17→ * friendly names and last-active timestamps. Excludes seedPhrase and
 18→ * hashedShare factors (shown elsewhere or hidden).
 19→ */
 20→export function AuthorizedDevices() {
 21→  const { getFactors, deleteFactor, factorCount } = useMfa();
 22→  const registry = useDeviceRegistryStore((s) => s.registry);
 23→  const currentDeviceId = useDeviceRegistryStore((s) => s.currentDeviceId);
 24→
 25→  const [isRevoking, setIsRevoking] = useState<string | null>(null);
 26→  const [confirmRevoke, setConfirmRevoke] = useState<string | null>(null);
 27→  const [revokeError, setRevokeError] = useState<string | null>(null);
 28→
 29→  const factors = useMemo(() => getFactors(), [getFactors]);
 30→
 31→  // Build device display list from factors
 32→  const deviceFactors = useMemo((): DeviceFactorDisplay[] => {
 33→    // Filter to device share factors only
 34→    const deviceShareFactors = factors.filter(
 35→      (f) =>
 36→        f.type === FactorKeyTypeShareDescription.DeviceShare ||
 37→        f.type === 'deviceShare' ||
 38→        f.type === 'webDeviceShare'
 39→    );
 40→
 41→    // Build a map from deviceId to registry entry for quick lookup
 42→    const registryMap = new Map<string, { name: string; lastSeenAt: number }>();
 43→    if (registry?.devices) {
 44→      for (const device of registry.devices) {
 45→        if (device.status === 'authorized') {
 46→          registryMap.set(device.deviceId, {
 47→            name: device.name,
 48→            lastSeenAt: device.lastSeenAt,
 49→          });
 50→        }
 51→      }
 52→    }
 53→
 54→    return deviceShareFactors.map((factor) => {
 55→      const deviceId = factor.additionalMetadata?.deviceId;
 56→      const registryEntry = deviceId ? registryMap.get(deviceId) : undefined;
 57→      const isCurrent = deviceId != null && deviceId === currentDeviceId;
 58→
 59→      return {
 60→        factorPubHex: factor.factorPubHex,
 61→        name: registryEntry?.name || factor.additionalMetadata?.browserName || 'Unknown device',
 62→        lastActive: registryEntry?.lastSeenAt
 63→          ? formatRelativeTime(registryEntry.lastSeenAt)
 64→          : 'unknown',
 65→        isCurrent,
 66→      };
 67→    });
 68→  }, [factors, registry, currentDeviceId]);
 69→
 70→  const handleRevoke = useCallback(
 71→    async (factorPubHex: string) => {
 72→      setIsRevoking(factorPubHex);
 73→      setRevokeError(null);
 74→      try {
 75→        await deleteFactor(factorPubHex);
 76→        setConfirmRevoke(null);
 77→      } catch (err) {
 78→        setRevokeError(err instanceof Error ? err.message : 'Failed to revoke device');
 79→      } finally {
 80→        setIsRevoking(null);
 81→      }
 82→    },
 83→    [deleteFactor]
 84→  );
 85→
 86→  // Need at least 2 factors total (current device + recovery is the minimum)
 87→  const canRevoke = factorCount > 2;
 88→
 89→  if (factors.length === 0) {
 90→    return <div className="authorized-devices-loading">loading device factors...</div>;
 91→  }
 92→
 93→  if (deviceFactors.length === 0) {
 94→    return <div className="authorized-devices-empty">no device factors found.</div>;
 95→  }
 96→
 97→  return (
 98→    <div className="authorized-devices">
 99→      {revokeError && (
100→        <div className="linked-methods-error" role="alert">
101→          {revokeError}
102→          <button
103→            type="button"
104→            onClick={() => setRevokeError(null)}
105→            className="linked-methods-error-dismiss"
106→            aria-label="Dismiss error"
107→          >
108→            [x]
109→          </button>
110→        </div>
111→      )}
112→
113→      <div className="authorized-devices-list" role="list" aria-label="Authorized devices">
114→        {deviceFactors.map((device) => (
115→          <div key={device.factorPubHex} className="authorized-devices-item" role="listitem">
116→            <div className="authorized-devices-info">
117→              <span className="authorized-devices-name">{device.name}</span>
118→              <span className="authorized-devices-meta">{'last active: ' + device.lastActive}</span>
119→            </div>
120→            <div className="authorized-devices-actions">
121→              <span
122→                className={`authorized-devices-tag ${device.isCurrent ? 'current' : 'authorized'}`}
123→              >
124→                {device.isCurrent ? '[CURRENT]' : '[AUTHORIZED]'}
125→              </span>
126→              {!device.isCurrent && (
127→                <>
128→                  {confirmRevoke === device.factorPubHex ? (
129→                    <div className="authorized-devices-confirm-inline">
130→                      <button
131→                        type="button"
132→                        className="authorized-devices-revoke-btn"
133→                        onClick={() => handleRevoke(device.factorPubHex)}
134→                        disabled={!!isRevoking || !canRevoke}
135→                        style={{ color: '#EF4444' }}
136→                      >
137→                        {isRevoking === device.factorPubHex ? 'revoking...' : '--confirm'}
138→                      </button>
139→                      <button
140→                        type="button"
141→                        className="authorized-devices-revoke-btn"
142→                        onClick={() => setConfirmRevoke(null)}
143→                      >
144→                        --cancel
145→                      </button>
146→                    </div>
147→                  ) : (
148→                    <button
149→                      type="button"
150→                      className="authorized-devices-revoke-btn"
151→                      onClick={() => setConfirmRevoke(device.factorPubHex)}
152→                      disabled={!canRevoke}
153→                      title={
154→                        !canRevoke
155→                          ? 'Cannot revoke: minimum 2 factors required'
156→                          : 'Revoke this device'
157→                      }
158→                      aria-label={`Revoke ${device.name}`}
159→                    >
160→                      --revoke
161→                    </button>
162→                  )}
163→                </>
164→              )}
165→            </div>
166→          </div>
167→        ))}
168→      </div>
169→
170→      {!canRevoke && deviceFactors.length > 1 && (
171→        <div className="authorized-devices-warning" role="alert">
172→          {'// '}cannot revoke devices: minimum 2 factors required (current device + recovery
173→          phrase)
174→        </div>
175→      )}
176→    </div>
177→  );
178→}
179→
180→/**
181→ * Format a Unix ms timestamp as a relative time string.
182→ */
183→function formatRelativeTime(timestampMs: number): string {
184→  const now = Date.now();
185→  const diff = now - timestampMs;
186→
187→  if (diff < 60_000) return 'just now';
188→  if (diff < 3_600_000) return `${Math.floor(diff / 60_000)}m ago`;
189→  if (diff < 86_400_000) return `${Math.floor(diff / 3_600_000)}h ago`;
190→  if (diff < 604_800_000) return `${Math.floor(diff / 86_400_000)}d ago`;
191→
192→  return new Date(timestampMs).toLocaleDateString('en-US', {
193→    month: 'short',
194→    day: 'numeric',
195→    year: 'numeric',
196→  });
197→}
