  6→/**
  7→ * Recovery phrase status and regeneration section for the Security tab.
  8→ *
  9→ * Shows whether a recovery factor is active, allows regeneration with
 10→ * confirmation, and displays the new phrase with acknowledgment checkbox.
 11→ */
 12→export function RecoveryPhraseSection() {
 13→  const { getFactors, regenerateRecoveryPhrase } = useMfa();
 14→
 15→  const [showConfirm, setShowConfirm] = useState(false);
 16→  const [isRegenerating, setIsRegenerating] = useState(false);
 17→  const [error, setError] = useState<string | null>(null);
 18→  const [newPhrase, setNewPhrase] = useState<string[] | null>(null);
 19→  const [acknowledged, setAcknowledged] = useState(false);
 20→
 21→  const factors = useMemo(() => getFactors(), [getFactors]);
 22→  const hasRecoveryFactor = factors.some(
 23→    (f) => f.type === FactorKeyTypeShareDescription.SeedPhrase || f.type === 'seedPhrase'
 24→  );
 25→
 26→  const handleRegenerate = useCallback(async () => {
 27→    setIsRegenerating(true);
 28→    setError(null);
 29→    try {
 30→      const mnemonic = await regenerateRecoveryPhrase();
 31→      setNewPhrase(mnemonic.split(' '));
 32→      setShowConfirm(false);
 33→    } catch (err) {
 34→      setError(err instanceof Error ? err.message : 'Failed to regenerate recovery phrase');
 35→    } finally {
 36→      setIsRegenerating(false);
 37→    }
 38→  }, [regenerateRecoveryPhrase]);
 39→
 40→  const toggleAcknowledged = useCallback(() => {
 41→    setAcknowledged((prev) => !prev);
 42→  }, []);
 43→
 44→  const handleCheckboxKeyDown = useCallback(
 45→    (e: React.KeyboardEvent) => {
 46→      if (e.key === 'Enter' || e.key === ' ') {
 47→        e.preventDefault();
 48→        toggleAcknowledged();
 49→      }
 50→    },
 51→    [toggleAcknowledged]
 52→  );
 53→
 54→  const handleDismissPhrase = useCallback(() => {
 55→    setNewPhrase(null);
 56→    setAcknowledged(false);
 57→  }, []);
 58→
 59→  return (
 60→    <div className="recovery-section">
 61→      {/* Status */}
 62→      <div className="recovery-section-status">
 63→        <span
 64→          className={`recovery-section-status-dot ${hasRecoveryFactor ? '' : 'inactive'}`}
 65→          aria-hidden="true"
 66→        />
 67→        <span className="recovery-section-status-label">
 68→          {hasRecoveryFactor ? 'recovery phrase active' : 'no recovery phrase'}
 69→        </span>
 70→      </div>
 71→
 72→      {/* Error display */}
 73→      {error && (
 74→        <div className="linked-methods-error" role="alert">
 75→          {error}
