 10→import * as argon2 from 'argon2';
 11→import { randomInt } from 'crypto';
 12→import sgMail from '@sendgrid/mail';
 13→
 14→/** OTP validity in seconds */
 15→const OTP_TTL = 300; // 5 minutes
 16→/** Rate limit window in seconds */
 17→const RATE_LIMIT_TTL = 900; // 15 minutes
 18→/** Max OTP send attempts per email per rate limit window */
 19→const MAX_SEND_ATTEMPTS = 5;
 20→/** Max verification attempts per OTP */
 21→const MAX_VERIFY_ATTEMPTS = 5;
 22→
 23→@Injectable()
 24→export class EmailOtpService implements OnModuleDestroy {
 25→  private readonly logger = new Logger(EmailOtpService.name);
 26→  private readonly redis: Redis;
 27→  private readonly sendgridConfigured: boolean;
 28→  private readonly sendgridFromEmail: string;
 29→
 30→  constructor(config: ConfigService) {
 31→    this.redis = new Redis({
 32→      host: config.get('REDIS_HOST', 'localhost'),
 33→      port: config.get<number>('REDIS_PORT', 6379),
 34→      password: config.get('REDIS_PASSWORD', undefined),
 35→      lazyConnect: true,
 36→    });
 37→
 38→    const apiKey = config.get<string>('SENDGRID_API_KEY', '');
 39→    this.sendgridFromEmail = config.get<string>('SENDGRID_FROM_EMAIL', 'noreply@cipherbox.cc');
 40→
 41→    if (apiKey) {
 42→      sgMail.setApiKey(apiKey);
 43→      this.sendgridConfigured = true;
 44→      this.logger.log('SendGrid email delivery enabled');
 45→    } else {
 46→      this.sendgridConfigured = false;
 47→      this.logger.log('SendGrid not configured — OTP codes will be logged in dev mode only');
 48→    }
 49→  }
 50→
 51→  async onModuleDestroy(): Promise<void> {
 52→    await this.redis.quit();
 53→  }
 54→
 55→  /**
 56→   * Generate and store an OTP for the given email address.
 57→   *
 58→   * In non-production mode, the OTP is logged to console for testing.
 59→   *
 60→   * @throws BadRequestException if rate limit exceeded
 61→   */
 62→  async sendOtp(email: string): Promise<void> {
 63→    const normalizedEmail = email.toLowerCase().trim();
 64→
 65→    // Check rate limit
 66→    const rateLimitKey = `otp-attempts:${normalizedEmail}`;
 67→    const attempts = await this.redis.get(rateLimitKey);
 68→    if (attempts && parseInt(attempts, 10) >= MAX_SEND_ATTEMPTS) {
 69→      throw new BadRequestException('Too many OTP requests. Please try again later.');
 70→    }
 71→
 72→    // Generate 6-digit OTP
 73→    const otp = randomInt(100000, 1000000).toString();
 74→
 75→    // Hash OTP before storing
 76→    const hashedOtp = await argon2.hash(otp);
 77→
 78→    // Store hashed OTP with TTL
 79→    const otpKey = `otp:${normalizedEmail}`;
 80→    await this.redis.setex(otpKey, OTP_TTL, hashedOtp);
 81→
 82→    // Reset verify attempts for this new OTP
 83→    const verifyKey = `otp-verify-attempts:${normalizedEmail}`;
 84→    await this.redis.del(verifyKey);
 85→
 86→    // Increment rate limit counter
 87→    const currentAttempts = await this.redis.incr(rateLimitKey);
 88→    if (currentAttempts === 1) {
 89→      await this.redis.expire(rateLimitKey, RATE_LIMIT_TTL);
 90→    }
 91→
 92→    // Send OTP email via SendGrid (if configured)
 93→    if (this.sendgridConfigured) {
 94→      try {
 95→        await sgMail.send({
 96→          to: normalizedEmail,
 97→          from: this.sendgridFromEmail,
 98→          subject: 'Your CipherBox verification code',
 99→          text: `Your CipherBox verification code is: ${otp}\n\nThis code expires in 5 minutes. If you did not request this code, please ignore this email.`,
100→          html: `<p>Your CipherBox verification code is:</p><p style="font-size:24px;font-weight:bold;letter-spacing:4px;margin:16px 0">${otp}</p><p>This code expires in 5 minutes. If you did not request this code, please ignore this email.</p>`,
101→        });
102→        this.logger.log(`OTP email sent to ${normalizedEmail}`);
103→      } catch (error) {
104→        // Do not throw — OTP is already stored in Redis, user can retry
105→        this.logger.error(`Failed to send OTP email to ${normalizedEmail}`, (error as Error).stack);
106→      }
107→    }
108→
109→    // Log OTP in development mode only (not staging, not production)
110→    if (process.env.NODE_ENV === 'development' || !process.env.NODE_ENV) {
111→      this.logger.warn(`DEV OTP for ${normalizedEmail}: ${otp}`);
112→    }
113→  }
114→
115→  /**
116→   * Verify an OTP for the given email address.
117→   *
118→   * On success, the OTP is deleted (single-use).
119→   *
120→   * @throws UnauthorizedException if OTP is invalid, expired, or too many attempts
121→   */
122→  async verifyOtp(email: string, otp: string): Promise<void> {
123→    const normalizedEmail = email.toLowerCase().trim();
124→
125→    // Check verify attempt count
126→    const verifyKey = `otp-verify-attempts:${normalizedEmail}`;
127→    const verifyAttempts = await this.redis.get(verifyKey);
128→    if (verifyAttempts && parseInt(verifyAttempts, 10) >= MAX_VERIFY_ATTEMPTS) {
129→      // Delete the OTP to force re-request
130→      await this.redis.del(`otp:${normalizedEmail}`);
131→      throw new UnauthorizedException('Too many verification attempts. Please request a new code.');
132→    }
133→
134→    // Retrieve stored hash
135→    const otpKey = `otp:${normalizedEmail}`;
136→    const storedHash = await this.redis.get(otpKey);
137→
138→    if (!storedHash) {
139→      throw new UnauthorizedException('No OTP found. Please request a new code.');
140→    }
141→
142→    // Increment verify attempt counter
143→    const currentVerifyAttempts = await this.redis.incr(verifyKey);
144→    if (currentVerifyAttempts === 1) {
145→      await this.redis.expire(verifyKey, OTP_TTL);
146→    }
147→
148→    // Verify OTP against hash
149→    const isValid = await argon2.verify(storedHash, otp);
150→    if (!isValid) {
151→      throw new UnauthorizedException('Invalid OTP code.');
152→    }
153→
154→    // OTP is valid -- delete it (single-use)
155→    await this.redis.del(otpKey);
156→    await this.redis.del(verifyKey);
157→  }
158→}
