        const newError = CoreKitError.default(ERRORS.TKEY_SHARES_REQUIRED);
        newError.stack = err.stack;
        throw newError;
      }
    }
    const newError = CoreKitError.default(err.message);
    newError.stack = err.stack;
    throw newError;
  }
}

