  1→import { Injectable, UnauthorizedException } from '@nestjs/common';
  2→import { createHash, randomBytes } from 'crypto';
  3→import { getAddress } from 'viem';
  4→import { parseSiweMessage, validateSiweMessage } from 'viem/siwe';
  5→import { verifyMessage } from 'viem';
  6→
  7→@Injectable()
  8→export class SiweService {
  9→  /**
 10→   * Generate a cryptographically random nonce for SIWE.
 11→   * Returns 32 hex characters (16 random bytes).
 12→   * Exceeds EIP-4361 minimum of 8 alphanumeric characters.
 13→   */
 14→  generateNonce(): string {
 15→    return randomBytes(16).toString('hex');
 16→  }
 17→
 18→  /**
 19→   * Verify a SIWE message and signature (EOA only, no RPC needed).
 20→   * Returns the verified wallet address (EIP-55 checksummed).
 21→   *
 22→   * @throws UnauthorizedException if message is invalid or signature doesn't match
 23→   */
 24→  async verifySiweMessage(
 25→    message: string,
 26→    signature: `0x${string}`,
 27→    expectedNonce: string,
 28→    expectedDomain: string
 29→  ): Promise<string> {
 30→    // 1. Parse the SIWE message
 31→    const parsed = parseSiweMessage(message);
 32→    if (!parsed.address) {
 33→      throw new UnauthorizedException('Invalid SIWE message: missing address');
 34→    }
 35→
 36→    // 2. Validate message fields (domain, nonce, expiry, etc.)
 37→    const isValid = validateSiweMessage({
 38→      message: parsed,
 39→      domain: expectedDomain,
 40→      nonce: expectedNonce,
 41→    });
 42→    if (!isValid) {
 43→      throw new UnauthorizedException('SIWE message validation failed');
 44→    }
 45→
 46→    // 3. Verify cryptographic signature (standalone utility, no RPC client needed)
 47→    const signatureValid = await verifyMessage({
 48→      address: parsed.address,
 49→      message,
 50→      signature,
 51→    });
 52→    if (!signatureValid) {
 53→      throw new UnauthorizedException('Invalid SIWE signature');
 54→    }
 55→
 56→    // 4. Return checksummed address
 57→    return getAddress(parsed.address);
 58→  }
 59→
 60→  /**
 61→   * Hash a wallet address for database lookup.
 62→   * Normalizes with EIP-55 checksum before hashing to ensure consistency
 63→   * regardless of input casing.
 64→   *
 65→   * @returns SHA-256 hex digest of the checksummed address (64 chars)
 66→   */
 67→  hashWalletAddress(address: string): string {
 68→    const checksummed = getAddress(address);
 69→    return createHash('sha256').update(checksummed).digest('hex');
 70→  }
 71→
 72→  /**
 73→   * Hash any identifier (email, Google sub, etc.) for database lookup.
 74→   * The caller is responsible for normalization (e.g., lowercasing email).
 75→   * Unlike hashWalletAddress, this does NOT apply EIP-55 checksumming.
 76→   *
 77→   * @returns SHA-256 hex digest of the value (64 chars)
 78→   */
 79→  hashIdentifier(value: string): string {
 80→    return createHash('sha256').update(value).digest('hex');
 81→  }
 82→
 83→  /**
 84→   * Truncate a wallet address for display purposes.
 85→   * Returns first 6 + "..." + last 4 characters (e.g. "0xAbCd...1234").
 86→   * The full plaintext address is NEVER stored in the database.
 87→   */
 88→  truncateWalletAddress(address: string): string {
 89→    const checksummed = getAddress(address);
 90→    return `${checksummed.slice(0, 6)}...${checksummed.slice(-4)}`;
 91→  }
 92→
 93→  /**
 94→   * Truncate an email for display purposes.
 95→   * Returns first 3 + "..." + last 2 of local part + full domain.
 96→   * Short local parts (≤5 chars) are shown in full.
 97→   * Examples: "michael@gmail.com" → "mic...el@gmail.com", "bob@x.com" → "bob@x.com"
 98→   */
 99→  truncateEmail(email: string): string {
100→    const atIndex = email.indexOf('@');
101→    if (atIndex === -1) return email;
102→    const local = email.slice(0, atIndex);
103→    const domain = email.slice(atIndex);
104→    if (local.length <= 5) return email;
105→    return `${local.slice(0, 3)}...${local.slice(-2)}${domain}`;
106→  }
107→}
