  1→import {
  2→  Controller,
  3→  Get,
  4→  Post,
  5→  Body,
  6→  Header,
  7→  HttpCode,
  8→  HttpStatus,
  9→  Logger,
 10→  UnauthorizedException,
 11→  OnModuleDestroy,
 12→} from '@nestjs/common';
 13→import { ApiTags, ApiOperation, ApiResponse } from '@nestjs/swagger';
 14→import { Throttle, ThrottlerGuard } from '@nestjs/throttler';
 15→import { UseGuards } from '@nestjs/common';
 16→import { InjectRepository } from '@nestjs/typeorm';
 17→import { Repository } from 'typeorm';
 18→import { ConfigService } from '@nestjs/config';
 19→import Redis from 'ioredis';
 20→import { parseSiweMessage } from 'viem/siwe';
 21→import { JwtIssuerService } from '../services/jwt-issuer.service';
 22→import { GoogleOAuthService } from '../services/google-oauth.service';
 23→import { EmailOtpService } from '../services/email-otp.service';
 24→import { SiweService } from '../services/siwe.service';
 25→import { User } from '../entities/user.entity';
 26→import { AuthMethod } from '../entities/auth-method.entity';
 27→import {
 28→  GoogleLoginDto,
 29→  SendOtpDto,
 30→  VerifyOtpDto,
 31→  WalletVerifyDto,
 32→  IdentityTokenResponseDto,
 33→  SendOtpResponseDto,
 34→} from '../dto/identity.dto';
 35→
 36→@ApiTags('Identity')
 37→@Controller('auth')
 38→export class IdentityController implements OnModuleDestroy {
 39→  private readonly logger = new Logger(IdentityController.name);
 40→  private readonly redis: Redis;
 41→
 42→  constructor(
 43→    private jwtIssuerService: JwtIssuerService,
 44→    private googleOAuthService: GoogleOAuthService,
 45→    private emailOtpService: EmailOtpService,
 46→    private siweService: SiweService,
 47→    private configService: ConfigService,
 48→    @InjectRepository(User)
 49→    private userRepository: Repository<User>,
 50→    @InjectRepository(AuthMethod)
 51→    private authMethodRepository: Repository<AuthMethod>
 52→  ) {
 53→    this.redis = new Redis({
 54→      host: configService.get('REDIS_HOST', 'localhost'),
 55→      port: configService.get<number>('REDIS_PORT', 6379),
 56→      password: configService.get('REDIS_PASSWORD', undefined),
 57→      lazyConnect: true,
 58→    });
 59→  }
 60→
 61→  async onModuleDestroy(): Promise<void> {
 62→    await this.redis.quit();
 63→  }
 64→
 65→  @Get('.well-known/jwks.json')
 66→  @Header('Cache-Control', 'public, max-age=3600')
 67→  @ApiOperation({ summary: 'JWKS endpoint for CipherBox identity provider' })
 68→  @ApiResponse({
 69→    status: 200,
 70→    description: 'JWKS containing RS256 public key for JWT verification',
 71→    schema: {
 72→      type: 'object',
 73→      properties: { keys: { type: 'array', items: { type: 'object' } } },
 74→      required: ['keys'],
 75→    },
 76→  })
 77→  getJwks(): { keys: object[] } {
 78→    return this.jwtIssuerService.getJwksData();
 79→  }
 80→
 81→  @Post('identity/google')
 82→  @HttpCode(HttpStatus.OK)
 83→  @ApiOperation({
 84→    summary: 'Login with Google OAuth token, returns CipherBox identity JWT',
 85→  })
 86→  @ApiResponse({
 87→    status: 200,
 88→    description: 'CipherBox JWT issued',
 89→    type: IdentityTokenResponseDto,
 90→  })
 91→  @ApiResponse({ status: 401, description: 'Invalid Google token' })
 92→  async googleLogin(@Body() dto: GoogleLoginDto): Promise<IdentityTokenResponseDto> {
 93→    // 1. Verify Google token
 94→    const googlePayload = await this.googleOAuthService.verifyGoogleToken(dto.idToken);
 95→
 96→    // 2. Hash Google sub (immutable user ID) -- NOT email (which can change)
 97→    const identifierHash = this.siweService.hashIdentifier(googlePayload.sub);
 98→    const normalizedEmail = googlePayload.email?.toLowerCase().trim() ?? '';
 99→
100→    // 3. Find or create user by hashed identifier
101→    const { user, isNewUser } = await this.findOrCreateUserByIdentifier(
102→      identifierHash,
103→      this.siweService.truncateEmail(normalizedEmail),
104→      'google'
105→    );
106→
107→    // 4. Sign CipherBox identity JWT (include email for auth method identifier)
108→    const idToken = await this.jwtIssuerService.signIdentityJwt(user.id, normalizedEmail);
109→
110→    this.logger.log(`Google login: userId=${user.id}, isNew=${isNewUser}`);
111→
112→    return {
113→      idToken,
114→      userId: user.id,
115→      isNewUser,
116→      email: googlePayload.email,
117→    };
118→  }
119→
120→  @Post('identity/email/send-otp')
121→  @HttpCode(HttpStatus.OK)
122→  @UseGuards(ThrottlerGuard)
123→  @Throttle({ default: { limit: 5, ttl: 900000 } }) // 5 requests per 15 min per IP
124→  @ApiOperation({ summary: 'Send OTP to email address' })
125→  @ApiResponse({
126→    status: 200,
127→    description: 'OTP sent',
128→    type: SendOtpResponseDto,
129→  })
130→  @ApiResponse({
131→    status: 400,
132→    description: 'Invalid email or rate limit exceeded',
133→  })
134→  async sendOtp(@Body() dto: SendOtpDto): Promise<SendOtpResponseDto> {
135→    await this.emailOtpService.sendOtp(dto.email);
136→    return { success: true };
137→  }
138→
139→  @Post('identity/email/verify-otp')
140→  @HttpCode(HttpStatus.OK)
141→  @UseGuards(ThrottlerGuard)
142→  @Throttle({ default: { limit: 5, ttl: 900000 } }) // 5 requests per 15 min per IP
143→  @ApiOperation({
144→    summary: 'Verify email OTP and return CipherBox identity JWT',
145→  })
146→  @ApiResponse({
147→    status: 200,
148→    description: 'OTP verified, JWT issued',
149→    type: IdentityTokenResponseDto,
150→  })
151→  @ApiResponse({ status: 401, description: 'Invalid or expired OTP' })
152→  async verifyOtp(@Body() dto: VerifyOtpDto): Promise<IdentityTokenResponseDto> {
153→    // 1. Verify OTP
154→    await this.emailOtpService.verifyOtp(dto.email, dto.otp);
155→
156→    // 2. Normalize email and hash for identifier
157→    const normalizedEmail = dto.email.toLowerCase().trim();
158→    const identifierHash = this.siweService.hashIdentifier(normalizedEmail);
159→
160→    // 3. Find or create user by hashed identifier
161→    const { user, isNewUser } = await this.findOrCreateUserByIdentifier(
162→      identifierHash,
163→      this.siweService.truncateEmail(normalizedEmail),
164→      'email'
165→    );
166→
167→    // 4. Sign CipherBox identity JWT (include email for auth method identifier)
168→    const idToken = await this.jwtIssuerService.signIdentityJwt(user.id, normalizedEmail);
169→
170→    this.logger.log(`Email OTP login: userId=${user.id}, isNew=${isNewUser}`);
171→
172→    return {
173→      idToken,
174→      userId: user.id,
175→      isNewUser,
176→      email: normalizedEmail,
177→    };
178→  }
179→
180→  @Get('identity/wallet/nonce')
181→  @HttpCode(HttpStatus.OK)
182→  @UseGuards(ThrottlerGuard)
183→  @Throttle({ default: { limit: 10, ttl: 60000 } }) // 10 per 60s per IP
184→  @ApiOperation({ summary: 'Generate SIWE nonce for wallet login' })
185→  @ApiResponse({
186→    status: 200,
187→    description: 'Nonce generated',
188→    schema: {
189→      type: 'object',
190→      properties: { nonce: { type: 'string' } },
191→      required: ['nonce'],
192→    },
193→  })
194→  async getWalletNonce(): Promise<{ nonce: string }> {
195→    const nonce = this.siweService.generateNonce();
196→    // Store nonce in Redis with 5min TTL
197→    await this.redis.set(`siwe:nonce:${nonce}`, '1', 'EX', 300);
198→    return { nonce };
199→  }
200→
201→  @Post('identity/wallet')
202→  @HttpCode(HttpStatus.OK)
203→  @UseGuards(ThrottlerGuard)
204→  @Throttle({ default: { limit: 5, ttl: 900000 } }) // 5 per 15min per IP
205→  @ApiOperation({
206→    summary: 'Verify SIWE wallet signature and return CipherBox identity JWT',
207→  })
208→  @ApiResponse({
209→    status: 200,
210→    description: 'Wallet verified, JWT issued',
211→    type: IdentityTokenResponseDto,
212→  })
213→  @ApiResponse({ status: 401, description: 'Invalid nonce or signature' })
214→  async walletLogin(@Body() dto: WalletVerifyDto): Promise<IdentityTokenResponseDto> {
215→    // 1. Parse the message to extract nonce
216→    const parsed = parseSiweMessage(dto.message);
217→    if (!parsed.nonce) {
218→      throw new UnauthorizedException('Invalid SIWE message: missing nonce');
219→    }
220→
221→    // 2. Consume nonce from Redis (single-use)
222→    const nonceKey = `siwe:nonce:${parsed.nonce}`;
223→    let nonceDeleted: number;
224→    try {
225→      nonceDeleted = await this.redis.del(nonceKey);
226→    } catch (err) {
227→      this.logger.error('Redis error during nonce consumption', err);
228→      throw new UnauthorizedException('Nonce verification failed');
229→    }
230→    if (!nonceDeleted) {
231→      throw new UnauthorizedException('Invalid or expired nonce');
232→    }
233→
234→    // 3. Determine domain for validation
235→    const domain = this.configService.get<string>('SIWE_DOMAIN', 'localhost');
236→
237→    // 4. Verify SIWE message + signature
238→    const walletAddress = await this.siweService.verifySiweMessage(
239→      dto.message,
240→      dto.signature as `0x${string}`,
241→      parsed.nonce,
242→      domain
243→    );
244→
245→    // 5. Find or create user by wallet address hash
246→    const addressHash = this.siweService.hashWalletAddress(walletAddress);
247→    const truncated = this.siweService.truncateWalletAddress(walletAddress);
248→    const { user, isNewUser } = await this.findOrCreateUserByIdentifier(
249→      addressHash,
250→      truncated,
251→      'wallet'
252→    );
253→
254→    // 6. Issue CipherBox JWT (sub=userId)
255→    const idToken = await this.jwtIssuerService.signIdentityJwt(user.id);
256→
257→    this.logger.log(`Wallet login: userId=${user.id}, isNew=${isNewUser}`);
258→
259→    return { idToken, userId: user.id, isNewUser };
260→  }
261→
262→  /**
263→   * Find an existing user by hashed identifier in the AuthMethod table, or create a new user.
264→   *
265→   * Each auth method type is independent -- no cross-method email matching.
266→   * Users link methods explicitly via Settings, not auto-linked by email.
267→   *
268→   * For identity provider flow, the publicKey is set to a placeholder because
269→   * the actual MPC-derived publicKey is not known until the client completes
270→   * Core Kit login with the CipherBox JWT.
271→   */
272→  private async findOrCreateUserByIdentifier(
273→    identifierHash: string,
274→    identifierDisplay: string,
275→    authMethodType: 'google' | 'email' | 'wallet'
276→  ): Promise<{ user: User; isNewUser: boolean }> {
277→    // Look for existing auth method with this hash
278→    const existingMethod = await this.authMethodRepository.findOne({
279→      where: {
280→        type: authMethodType,
281→        identifierHash,
282→      },
283→      relations: ['user'],
284→    });
285→
286→    if (existingMethod) {
287→      // Update last used timestamp
288→      existingMethod.lastUsedAt = new Date();
289→      await this.authMethodRepository.save(existingMethod);
290→      return { user: existingMethod.user, isNewUser: false };
291→    }
292→
293→    // No cross-method linking -- create new user with unique temporary publicKey
294→    const tempId = `${Date.now()}-${Math.random().toString(36).slice(2)}`;
295→    const newUser = await this.userRepository.save({
296→      publicKey: `pending-core-kit-${tempId}`,
297→    });
298→    // Update placeholder with actual userId now that we have it
299→    newUser.publicKey = `pending-core-kit-${newUser.id}`;
300→    await this.userRepository.save(newUser);
301→
302→    // Create auth method with hash-based identifier
303→    await this.authMethodRepository.save({
304→      userId: newUser.id,
305→      type: authMethodType,
306→      identifier: identifierHash,
307→      identifierHash,
308→      identifierDisplay,
309→      lastUsedAt: new Date(),
310→    });
311→
312→    return { user: newUser, isNewUser: true };
313→  }
314→}
