 14→type MfaView = 'waiting' | 'recovery';
 15→
 16→/**
 17→ * Login page with terminal aesthetic and matrix background.
 18→ * CipherBox-branded custom login UI with Google OAuth and email OTP.
 19→ *
 20→ * When REQUIRED_SHARE is detected (MFA enabled, new device), switches
 21→ * to either DeviceWaitingScreen or RecoveryInput instead of the login form.
 22→ */
 23→export function Login() {
 24→  const {
 25→    isAuthenticated,
 26→    isLoading,
 27→    isRequiredShare,
 28→    completeRequiredShare,
 29→    loginWithGoogle,
 30→    loginWithEmail,
 31→    loginWithWallet,
 32→  } = useAuth();
 33→  const navigate = useNavigate();
 34→  const [loginError, setLoginError] = useState<string | null>(null);
 35→  const [mfaView, setMfaView] = useState<MfaView>('waiting');
 36→
 37→  // Health check for disabling connect button when API is down
 38→  const {
 39→    data: healthData,
 40→    isLoading: isHealthLoading,
 41→    isError: isHealthError,
 42→  } = useHealthControllerCheck({
 43→    query: {
 44→      refetchInterval: 30000,
 45→      retry: 2,
 46→      refetchOnWindowFocus: true,
 47→    },
 48→  });
 49→
 50→  const isApiDown = !isHealthLoading && (isHealthError || healthData?.status !== 'ok');
 51→
 52→  // Redirect if already authenticated
 53→  useEffect(() => {
 54→    if (isAuthenticated) {
 55→      navigate('/files');
 56→    }
 57→  }, [isAuthenticated, navigate]);
 58→
 59→  const handleGoogleLogin = useCallback(
 60→    async (googleIdToken: string) => {
 61→      setLoginError(null);
 62→      try {
 63→        await loginWithGoogle(googleIdToken);
 64→      } catch (err) {
 65→        setLoginError(err instanceof Error ? err.message : 'Google login failed');
 66→      }
 67→    },
 68→    [loginWithGoogle]
 69→  );
 70→
 71→  const handleEmailLogin = useCallback(
 72→    async (email: string, otp: string) => {
 73→      setLoginError(null);
 74→      try {
 75→        await loginWithEmail(email, otp);
 76→      } catch (err) {
 77→        setLoginError(err instanceof Error ? err.message : 'Email login failed');
 78→      }
 79→    },
 80→    [loginWithEmail]
 81→  );
 82→
 83→  const handleWalletLogin = useCallback(
 84→    async (idToken: string, userId: string) => {
 85→      setLoginError(null);
 86→      try {
 87→        await loginWithWallet(idToken, userId);
 88→      } catch (err) {
 89→        setLoginError(err instanceof Error ? err.message : 'Wallet login failed');
 90→      }
 91→    },
 92→    [loginWithWallet]
 93→  );
 94→
 95→  const handleRecoveryFallback = useCallback(() => {
 96→    setMfaView('recovery');
 97→  }, []);
 98→
 99→  const handleBackToWaiting = useCallback(() => {
100→    setMfaView('waiting');
101→  }, []);
102→
103→  const handleRecoveryComplete = useCallback(async () => {
104→    // Recovery succeeded: Core Kit is now LOGGED_IN.
105→    // completeRequiredShare() will do backend auth + vault load + navigate.
106→    try {
107→      await completeRequiredShare();
108→    } catch (err) {
109→      setLoginError(err instanceof Error ? err.message : 'Failed to complete login after recovery');
110→    }
111→  }, [completeRequiredShare]);
112→
113→  const handleApprovalComplete = useCallback(() => {
114→    // Approval flow already called completeRequiredShare() inside the hook.
115→    // If it failed, the hook sets approvalError which DeviceWaitingScreen renders.
116→    // Nothing extra needed here -- navigation happens in completeRequiredShare.
117→  }, []);
118→
119→  // Show loading state while checking authentication
120→  if (isLoading) {
121→    return (
122→      <>
123→        <StagingBanner variant="login" />
124→        <div className="login-container">
125→          <MatrixBackground opacity={0.3} frameInterval={50} />
126→          <div className="loading">initializing...</div>
127→          <LoginFooter />
128→        </div>
129→      </>
130→    );
131→  }
132→
133→  // REQUIRED_SHARE state: show MFA challenge UI instead of login form
134→  if (isRequiredShare) {
135→    return (
136→      <>
137→        <StagingBanner variant="login" />
138→        <div className="login-container">
139→          <MatrixBackground opacity={0.3} frameInterval={50} />
140→          {mfaView === 'waiting' ? (
141→            <DeviceWaitingScreen
142→              onRecoveryFallback={handleRecoveryFallback}
143→              onApprovalComplete={handleApprovalComplete}
144→            />
145→          ) : (
146→            <RecoveryInput
147→              onRecoveryComplete={handleRecoveryComplete}
148→              onBack={handleBackToWaiting}
149→            />
150→          )}
151→          {loginError && (
152→            <div className="login-error" role="alert" aria-live="polite">
153→              {loginError}
154→            </div>
155→          )}
156→          <LoginFooter />
157→        </div>
158→      </>
159→    );
160→  }
161→
162→  return (
163→    <>
164→      <StagingBanner variant="login" />
165→      <div className="login-container">
166→        <MatrixBackground opacity={0.3} frameInterval={50} />
167→        <div className="login-panel">
168→          <h1>CIPHERBOX</h1>
169→          <p className="tagline">zero-knowledge encrypted storage</p>
170→          <p className="login-description">
171→            your files, encrypted on your device. we never see your data.
172→          </p>
173→
174→          <div className="login-methods">
175→            <GoogleLoginButton onLogin={handleGoogleLogin} disabled={isLoading || isApiDown} />
176→
177→            <div className="login-divider">
178→              <span>{'// or'}</span>
179→            </div>
180→
181→            <EmailLoginForm onLogin={handleEmailLogin} disabled={isLoading || isApiDown} />
182→
183→            <div className="login-divider">
184→              <span>{'// or'}</span>
185→            </div>
186→
187→            <WalletLoginButton onLogin={handleWalletLogin} disabled={isLoading || isApiDown} />
188→          </div>
189→
190→          {loginError && (
191→            <div className="login-error" role="alert" aria-live="polite">
192→              {loginError}
193→            </div>
194→          )}
195→        </div>
196→        <LoginFooter />
