 24→export function useAuth() {
 25→  const navigate = useNavigate();
 26→  const {
 27→    isLoggedIn: coreKitLoggedIn,
 28→    isInitialized: coreKitInitialized,
 29→    isRequiredShare,
 30→    loginWithGoogle: coreKitLoginGoogle,
 31→    loginWithEmailOtp: coreKitLoginEmail,
 32→    loginWithWallet: coreKitLoginWallet,
 33→    getVaultKeypair,
 34→    getPublicKeyHex,
 35→    logout: coreKitLogout,
 36→  } = useCoreKitAuth();
 37→  const {
 38→    accessToken,
 39→    isAuthenticated,
 40→    lastAuthMethod,
 41→    userEmail,
 42→    setAccessToken,
 43→    setLastAuthMethod,
 44→    setUserEmail,
 45→    setVaultKeypair,
 46→    logout: clearAuthState,
 47→  } = useAuthStore();
 48→
 49→  const [isLoggingIn, setIsLoggingIn] = useState(false);
 50→  const [isLoggingOut, setIsLoggingOut] = useState(false);
 51→  const restoringRef = useRef(false);
 52→
 53→  // Stable refs for functions used in the session restoration effect.
 54→  // This prevents the effect from re-firing when function references change.
 55→  const initializeOrLoadVaultRef = useRef<(() => Promise<void>) | null>(null);
 56→  const coreKitLogoutRef = useRef<(() => Promise<void>) | null>(null);
 57→
 58→  // Pending auth state for REQUIRED_SHARE flow
 59→  // Stored so completeRequiredShare() can resume after factor input
 60→  const [pendingCipherboxJwt, setPendingCipherboxJwt] = useState<string | null>(null);
 61→  const [pendingAuthMethod, setPendingAuthMethod] = useState<string | null>(null);
 62→
 63→  const isLoading = !coreKitInitialized || isLoggingIn || isLoggingOut;
 64→
 65→  // Get vault store actions
 66→  const setVaultKeys = useVaultStore((state) => state.setVaultKeys);
 67→
 68→  /**
 69→   * Initialize vault for new users or load existing vault keys.
 70→   * Called after successful backend authentication.
 71→   * Uses Core Kit's getVaultKeypair() instead of PnP provider.
 72→   */
 73→  const initializeOrLoadVault = useCallback(async (): Promise<void> => {
 74→    // Get user's secp256k1 keypair from Core Kit TSS export
 75→    const userKeypair = await getVaultKeypair();
 76→
 77→    if (!userKeypair) {
 78→      throw new Error('Failed to get vault keypair from Core Kit');
 79→    }
 80→
 81→    // Store keypair in auth store for crypto operations
 82→    setVaultKeypair(userKeypair);
 83→
 84→    try {
 85→      // Try to fetch existing vault
 86→      const existingVault = await vaultApi.getVault();
 87→
 88→      // Store TEE keys if available
 89→      if (existingVault.teeKeys) {
 90→        useAuthStore.getState().setTeeKeys(existingVault.teeKeys);
 91→      }
 92→
 93→      // Vault exists - decrypt keys and load into store
 94→      // decryptVaultKeys derives the IPNS public key internally from the decrypted private key
 95→      const decryptedVault = await decryptVaultKeys(
 96→        {
 97→          encryptedRootFolderKey: hexToBytes(existingVault.encryptedRootFolderKey),
 98→          encryptedIpnsPrivateKey: hexToBytes(existingVault.encryptedRootIpnsPrivateKey),
 99→        },
100→        userKeypair.privateKey
101→      );
102→
103→      setVaultKeys({
104→        rootFolderKey: decryptedVault.rootFolderKey,
105→        rootIpnsKeypair: decryptedVault.rootIpnsKeypair,
106→        rootIpnsName: existingVault.rootIpnsName,
107→        vaultId: existingVault.id,
108→      });
109→    } catch (error) {
110→      const is404 = (error as { response?: { status?: number } })?.response?.status === 404;
111→
112→      if (is404) {
113→        // New user - initialize vault
114→        console.log(
115→          '[Auth] New user on Core Kit. If migrating from PnP, vault re-initialization may be needed.'
116→        );
117→        const newVault = await initializeVault(userKeypair.privateKey);
118→        const encryptedVault = await encryptVaultKeys(newVault, userKeypair.publicKey);
119→        const rootIpnsName = await deriveIpnsName(newVault.rootIpnsKeypair.publicKey);
120→
121→        const storedVault = await vaultApi.initVault({
122→          ownerPublicKey: bytesToHex(userKeypair.publicKey),
123→          encryptedRootFolderKey: bytesToHex(encryptedVault.encryptedRootFolderKey),
124→          encryptedRootIpnsPrivateKey: bytesToHex(encryptedVault.encryptedIpnsPrivateKey),
125→          rootIpnsName,
126→        });
127→
128→        if (storedVault.teeKeys) {
129→          useAuthStore.getState().setTeeKeys(storedVault.teeKeys);
130→        }
131→
132→        setVaultKeys({
133→          rootFolderKey: newVault.rootFolderKey,
134→          rootIpnsKeypair: newVault.rootIpnsKeypair,
135→          rootIpnsName,
136→          vaultId: storedVault.id,
137→          isNewVault: true,
138→        });
139→      } else {
140→        console.error('[useAuth] Failed to load vault:', error);
141→        throw error;
142→      }
143→    }
144→
145→    // Non-blocking device registry initialization (fire-and-forget)
146→    // Placed after vault load so registry failures never block login
147→    void (async () => {
148→      try {
149→        const deviceKeypair = await getOrCreateDeviceIdentity();
150→        const deviceInfo = detectDeviceInfo();
151→        const result = await initializeOrSyncRegistry({
152→          userPrivateKey: userKeypair.privateKey,
153→          userPublicKey: userKeypair.publicKey,
154→          deviceKeypair,
155→          deviceInfo: { ...deviceInfo, ipHash: '' },
156→        });
157→        if (result) {
158→          useDeviceRegistryStore
159→            .getState()
160→            .setRegistry(result.registry, result.ipnsName, deviceKeypair.deviceId);
161→        }
162→      } catch (error) {
163→        console.error('[Auth] Device registry init failed (non-blocking):', error);
164→      }
165→    })();
166→  }, [getVaultKeypair, setVaultKeypair, setVaultKeys]);
167→
168→  /**
169→   * Complete backend authentication after Core Kit login.
170→   * Sends the CipherBox-issued JWT (which we used for Core Kit loginWithJWT)
171→   * to the backend with loginType 'corekit'. The backend verifies it against
172→   * its own JWKS since CipherBox is the identity provider.
173→   */
174→  const completeBackendAuth = useCallback(
175→    async (authMethod: string, cipherboxJwt: string): Promise<void> => {
176→      // 1. Get real publicKey from Core Kit TSS export
177→      const publicKey = await getPublicKeyHex();
178→      if (!publicKey) {
179→        throw new Error('Failed to get publicKey from Core Kit');
180→      }
181→
182→      // publicKey available for debugging via DevTools if needed
183→
184→      // 2. Authenticate with CipherBox backend
185→      // Backend verifies our CipherBox JWT and resolves placeholder publicKey
186→      const response = await authApi.login({
187→        idToken: cipherboxJwt,
188→        publicKey,
189→        loginType: 'corekit',
190→      });
191→
192→      // 3. Store access token (refresh token in HTTP-only cookie)
193→      setAccessToken(response.accessToken);
194→
195→      // 4. Remember auth method for UX
196→      setLastAuthMethod(authMethod);
197→
198→      // 5. Initialize or load vault
199→      await initializeOrLoadVault();
200→    },
201→    [getPublicKeyHex, setAccessToken, setLastAuthMethod, initializeOrLoadVault]
202→  );
203→
204→  /**
205→   * Complete the login flow after REQUIRED_SHARE is resolved.
206→   * Called after inputFactorKey() succeeds (from recovery phrase or cross-device approval).
207→   * Uses the stored pendingCipherboxJwt to call completeBackendAuth with the REAL publicKey
208→   * (since Core Kit is now LOGGED_IN), then navigates to /files.
209→   */
210→  const completeRequiredShare = useCallback(async (): Promise<void> => {
211→    const jwt = pendingCipherboxJwt;
212→    const method = pendingAuthMethod;
213→
214→    if (!jwt || !method) {
215→      throw new Error('No pending auth info for REQUIRED_SHARE completion');
216→    }
217→
218→    // Core Kit should now be LOGGED_IN after inputFactorKey()
219→    // Complete backend auth with the REAL publicKey (replaces the placeholder session)
220→    await completeBackendAuth(method, jwt);
221→
222→    // Clear pending state
223→    setPendingCipherboxJwt(null);
224→    setPendingAuthMethod(null);
225→
226→    // Navigate to files
227→    navigate('/files');
228→  }, [pendingCipherboxJwt, pendingAuthMethod, completeBackendAuth, navigate]);
229→
230→  /**
231→   * Login with Google OAuth token.
232→   * Flow: Google idToken -> CipherBox backend -> CipherBox JWT ->
233→   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
234→   */
235→  const loginWithGoogle = useCallback(
236→    async (googleIdToken: string): Promise<void> => {
237→      if (isLoggingIn) return;
238→      setIsLoggingIn(true);
239→      try {
240→        // 1. Core Kit login via CipherBox identity provider
241→        const {
242→          cipherboxJwt,
243→          email,
244→          userId,
245→          status: coreKitStatus,
246→        } = await coreKitLoginGoogle(googleIdToken);
247→
248→        if (coreKitStatus === 'required_share') {
249→          // MFA enabled but device factor missing.
250→          // Store pending auth info for later completion after factor input.
251→          setPendingCipherboxJwt(cipherboxJwt);
252→          setPendingAuthMethod('google');
253→
254→          // Obtain temporary backend access token so the new device can
255→          // call bulletin board API endpoints (device-approval/*).
256→          // Uses placeholder publicKey since Core Kit is in REQUIRED_SHARE
257→          // state and we can't export the TSS key yet.
258→          const tempLoginResponse = await authApi.login({
259→            idToken: cipherboxJwt,
260→            publicKey: `pending-core-kit-${userId}`,
261→            loginType: 'corekit',
262→          });
263→          setAccessToken(tempLoginResponse.accessToken);
264→
265→          if (email) {
266→            setUserEmail(email);
267→          }
268→
269→          // Do NOT call completeBackendAuth() or navigate('/files')
270→          // The component tree will see isRequiredShare === true and
271→          // render recovery/approval UI.
272→          return;
273→        }
274→
275→        // Normal path: Core Kit logged in, proceed as before
276→        // 2. Complete backend auth + vault init
277→        await completeBackendAuth('google', cipherboxJwt);
278→
279→        // 3. Store email for UI display (returned from identity endpoint)
280→        if (email) {
281→          setUserEmail(email);
282→        }
283→
284→        // 4. Navigate to files
285→        navigate('/files');
286→      } catch (error) {
287→        console.error('[useAuth] Google login failed:', error);
288→        throw error;
289→      } finally {
290→        setIsLoggingIn(false);
291→      }
292→    },
293→    [isLoggingIn, coreKitLoginGoogle, completeBackendAuth, setAccessToken, setUserEmail, navigate]
294→  );
295→
296→  /**
297→   * Login with Email OTP.
298→   * Flow: email+otp -> CipherBox backend -> CipherBox JWT ->
299→   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
300→   */
301→  const loginWithEmail = useCallback(
302→    async (email: string, otp: string): Promise<void> => {
303→      if (isLoggingIn) return;
304→      setIsLoggingIn(true);
305→      try {
306→        // 1. Core Kit login via CipherBox identity provider
307→        const { cipherboxJwt, userId, status: coreKitStatus } = await coreKitLoginEmail(email, otp);
308→
309→        if (coreKitStatus === 'required_share') {
310→          setPendingCipherboxJwt(cipherboxJwt);
311→          setPendingAuthMethod('email');
312→
313→          const tempLoginResponse = await authApi.login({
314→            idToken: cipherboxJwt,
315→            publicKey: `pending-core-kit-${userId}`,
316→            loginType: 'corekit',
317→          });
318→          setAccessToken(tempLoginResponse.accessToken);
319→          setUserEmail(email);
320→          return;
321→        }
322→
323→        // Normal path
324→        await completeBackendAuth('email', cipherboxJwt);
325→        setUserEmail(email);
326→        navigate('/files');
327→      } catch (error) {
328→        console.error('[useAuth] Email login failed:', error);
329→        throw error;
330→      } finally {
331→        setIsLoggingIn(false);
332→      }
333→    },
334→    [isLoggingIn, coreKitLoginEmail, completeBackendAuth, setAccessToken, setUserEmail, navigate]
335→  );
336→
337→  /**
338→   * Login with Wallet (SIWE).
339→   * Flow: Wallet connects + signs SIWE message -> backend verifies ->
340→   * CipherBox JWT -> Core Kit loginWithJWT -> backend /auth/login (corekit type)
341→   */
342→  const loginWithWallet = useCallback(
343→    async (cipherboxJwt: string, userId: string): Promise<void> => {
344→      if (isLoggingIn) return;
345→      setIsLoggingIn(true);
346→      try {
347→        // 1. Core Kit login via CipherBox identity provider
348→        const { status: coreKitStatus } = await coreKitLoginWallet(cipherboxJwt, userId);
349→
350→        if (coreKitStatus === 'required_share') {
351→          setPendingCipherboxJwt(cipherboxJwt);
352→          setPendingAuthMethod('wallet');
353→
354→          const tempLoginResponse = await authApi.login({
355→            idToken: cipherboxJwt,
356→            publicKey: `pending-core-kit-${userId}`,
357→            loginType: 'corekit',
358→          });
359→          setAccessToken(tempLoginResponse.accessToken);
360→          return;
361→        }
362→
363→        // Normal path
364→        await completeBackendAuth('wallet', cipherboxJwt);
365→        navigate('/files');
366→      } catch (error) {
367→        console.error('[useAuth] Wallet login failed:', error);
368→        throw error;
369→      } finally {
370→        setIsLoggingIn(false);
371→      }
372→    },
373→    [isLoggingIn, coreKitLoginWallet, completeBackendAuth, setAccessToken, navigate]
374→  );
375→
376→  /**
377→   * Legacy login() for backward compatibility with AuthButton.
378→   * With Core Kit, login requires specifying the auth method.
379→   * This will be replaced by the new Login UI in Plan 04.
380→   */
381→  const login = useCallback(async () => {
382→    // Plan 04 will build the new login UI with method selection.
383→    // For now, this is a no-op placeholder that AuthButton calls.
384→    console.warn(
385→      '[useAuth] login() called without method -- ' +
386→        'use loginWithGoogle() or loginWithEmail() instead. ' +
387→        'Plan 04 will provide the new Login UI.'
388→    );
389→  }, []);
390→
391→  // Complete logout: Backend -> Core Kit -> Clear state
392→  const logout = useCallback(async () => {
393→    if (isLoggingOut) return;
394→
395→    setIsLoggingOut(true);
396→    try {
397→      // 1. Call backend logout (clears cookie)
398→      if (accessToken) {
399→        try {
400→          await authApi.logout();
401→        } catch {
402→          // Ignore errors -- we'll clear state anyway
403→        }
404→      }
405→
406→      // 2. Logout Core Kit (clears session from localStorage)
407→      await coreKitLogout();
408→
409→      // 3. Clear local state
410→      // [SECURITY: HIGH-02] Clear vault and folder stores
411→      // BEFORE auth state to zero crypto keys from memory
412→      useFolderStore.getState().clearFolders();
413→      useVaultStore.getState().clearVaultKeys();
414→      useSyncStore.getState().reset();
415→      useDeviceRegistryStore.getState().clearRegistry();
416→      useMfaStore.getState().reset();
417→      clearAuthState();
418→
419→      // 4. Clear pending REQUIRED_SHARE state
420→      setPendingCipherboxJwt(null);
421→      setPendingAuthMethod(null);
422→
423→      // 5. Navigate to login
424→      navigate('/');
425→    } catch (error) {
426→      console.error('[useAuth] Logout failed:', error);
427→      // Still clear state even if backend fails
428→      useFolderStore.getState().clearFolders();
429→      useVaultStore.getState().clearVaultKeys();
430→      useSyncStore.getState().reset();
431→      useDeviceRegistryStore.getState().clearRegistry();
432→      useMfaStore.getState().reset();
433→      clearAuthState();
434→      setPendingCipherboxJwt(null);
435→      setPendingAuthMethod(null);
436→      navigate('/');
437→    } finally {
438→      setIsLoggingOut(false);
439→    }
440→  }, [accessToken, coreKitLogout, clearAuthState, navigate, isLoggingOut]);
441→
442→  // Keep function refs up-to-date for use in session restoration effect.
443→  // Using refs prevents the effect from re-firing when function identities change.
444→  initializeOrLoadVaultRef.current = initializeOrLoadVault;
445→  coreKitLogoutRef.current = coreKitLogout;
446→
447→  // Session restoration: if Core Kit restores a session from localStorage
448→  // on init, we have LOGGED_IN status without going through login flow.
449→  // Complete backend auth + vault loading.
450→  useEffect(() => {
451→    const restoreSession = async () => {
452→      // Only restore if Core Kit has a session but we don't have
453→      // a backend access token yet
454→      if (coreKitLoggedIn && !isAuthenticated && !isLoggingIn && !restoringRef.current) {
455→        restoringRef.current = true;
456→        setIsLoggingIn(true);
457→        try {
458→          // Try to refresh using the HTTP-only cookie first
459→          const response = await authApi.refresh();
460→          setAccessToken(response.accessToken);
461→
462→          // Restore email from backend if available
463→          if (response.email) {
464→            setUserEmail(response.email);
465→          }
466→
467→          // Load vault keys from Core Kit keypair
468→          await initializeOrLoadVaultRef.current?.();
469→        } catch {
470→          // No valid backend session -- user needs to re-login
471→          // Core Kit session exists but backend cookie expired
472→          // Clear Core Kit session to avoid inconsistent state
473→          try {
474→            await coreKitLogoutRef.current?.();
475→          } catch {
476→            // Ignore logout errors during cleanup
477→          }
478→        } finally {
479→          restoringRef.current = false;
480→          setIsLoggingIn(false);
481→        }
482→      }
483→    };
484→    restoreSession();
485→  }, [coreKitLoggedIn, isAuthenticated, isLoggingIn, setAccessToken, setUserEmail]);
486→
487→  return {
488→    isLoading,
489→    isAuthenticated,
490→    isRequiredShare,
491→    lastAuthMethod,
492→    userEmail,
493→    pendingAuthMethod,
494→    login,
495→    loginWithGoogle,
496→    loginWithEmail,
497→    loginWithWallet,
498→    completeRequiredShare,
499→    logout,
500→  };
501→}
