123          encryptedRootFolderKey: bytesToHex(encryptedVault.encryptedRootFolderKey),
124          encryptedRootIpnsPrivateKey: bytesToHex(encryptedVault.encryptedIpnsPrivateKey),
125          rootIpnsName,
126        });
