264
265          if (email) {
266            setUserEmail(email);
267          }
268
269          // Do NOT call completeBackendAuth() or navigate('/files')
270          // The component tree will see isRequiredShare === true and
271          // render recovery/approval UI.
272          return;
273        }
274
275        // Normal path: Core Kit logged in, proceed as before
276        // 2. Complete backend auth + vault init
277        await completeBackendAuth('google', cipherboxJwt);
278
279        // 3. Store email for UI display (returned from identity endpoint)
280        if (email) {
281          setUserEmail(email);
282        }
283
284        // 4. Navigate to files
285        navigate('/files');
286      } catch (error) {
287        console.error('[useAuth] Google login failed:', error);
288        throw error;
289      } finally {
290        setIsLoggingIn(false);
291      }
292    },
293    [isLoggingIn, coreKitLoginGoogle, completeBackendAuth, setAccessToken, setUserEmail, navigate]
294  );
295
296  /**
297   * Login with Email OTP.
298   * Flow: email+otp -> CipherBox backend -> CipherBox JWT ->
299   * Core Kit loginWithJWT -> backend /auth/login (corekit type)
300   */
301  const loginWithEmail = useCallback(
302    async (email: string, otp: string): Promise<void> => {
303      if (isLoggingIn) return;
304      setIsLoggingIn(true);
305      try {
306        // 1. Core Kit login via CipherBox identity provider
307        const { cipherboxJwt, userId, status: coreKitStatus } = await coreKitLoginEmail(email, otp);
308
309        if (coreKitStatus === 'required_share') {
310          setPendingCipherboxJwt(cipherboxJwt);
311          setPendingAuthMethod('email');
312
313          const tempLoginResponse = await authApi.login({
314            idToken: cipherboxJwt,
315            publicKey: `pending-core-kit-${userId}`,
316            loginType: 'corekit',
317          });
318          setAccessToken(tempLoginResponse.accessToken);
319          setUserEmail(email);
320          return;
321        }
322
323        // Normal path
324        await completeBackendAuth('email', cipherboxJwt);
325        setUserEmail(email);
326        navigate('/files');
327      } catch (error) {
328        console.error('[useAuth] Email login failed:', error);
329        throw error;
330      } finally {
331        setIsLoggingIn(false);
332      }
333    },
334    [isLoggingIn, coreKitLoginEmail, completeBackendAuth, setAccessToken, setUserEmail, navigate]
335  );
336
337  /**
338   * Login with Wallet (SIWE).
339   * Flow: Wallet connects + signs SIWE message -> backend verifies ->
340   * CipherBox JWT -> Core Kit loginWithJWT -> backend /auth/login (corekit type)
341   */
342  const loginWithWallet = useCallback(
343    async (cipherboxJwt: string, userId: string): Promise<void> => {
344      if (isLoggingIn) return;
345      setIsLoggingIn(true);
346      try {
347        // 1. Core Kit login via CipherBox identity provider
348        const { status: coreKitStatus } = await coreKitLoginWallet(cipherboxJwt, userId);
349
350        if (coreKitStatus === 'required_share') {
351          setPendingCipherboxJwt(cipherboxJwt);
352          setPendingAuthMethod('wallet');
353
354          const tempLoginResponse = await authApi.login({
355            idToken: cipherboxJwt,
356            publicKey: `pending-core-kit-${userId}`,
357            loginType: 'corekit',
358          });
359          setAccessToken(tempLoginResponse.accessToken);
360          return;
361        }
362
363        // Normal path
364        await completeBackendAuth('wallet', cipherboxJwt);
365        navigate('/files');
366      } catch (error) {
367        console.error('[useAuth] Wallet login failed:', error);
368        throw error;
369      } finally {
370        setIsLoggingIn(false);
371      }
372    },
373    [isLoggingIn, coreKitLoginWallet, completeBackendAuth, setAccessToken, navigate]
374  );
375
376  /**
377   * Legacy login() for backward compatibility with AuthButton.
378   * With Core Kit, login requires specifying the auth method.
379   * This will be replaced by the new Login UI in Plan 04.
380   */
381  const login = useCallback(async () => {
382    // Plan 04 will build the new login UI with method selection.
383    // For now, this is a no-op placeholder that AuthButton calls.
384    console.warn(
385      '[useAuth] login() called without method -- ' +
386        'use loginWithGoogle() or loginWithEmail() instead. ' +
387        'Plan 04 will provide the new Login UI.'
388    );
389  }, []);
390
391  // Complete logout: Backend -> Core Kit -> Clear state
392  const logout = useCallback(async () => {
393    if (isLoggingOut) return;
394
395    setIsLoggingOut(true);
396    try {
397      // 1. Call backend logout (clears cookie)
398      if (accessToken) {
399        try {
400          await authApi.logout();
401        } catch {
402          // Ignore errors -- we'll clear state anyway
403        }
404      }
405
406      // 2. Logout Core Kit (clears session from localStorage)
407      await coreKitLogout();
408
409      // 3. Clear local state
410      // [SECURITY: HIGH-02] Clear vault and folder stores
411      // BEFORE auth state to zero crypto keys from memory
412      useFolderStore.getState().clearFolders();
413      useVaultStore.getState().clearVaultKeys();
414      useSyncStore.getState().reset();
415      useDeviceRegistryStore.getState().clearRegistry();
416      useMfaStore.getState().reset();
417      clearAuthState();
418
419      // 4. Clear pending REQUIRED_SHARE state
420      setPendingCipherboxJwt(null);
421      setPendingAuthMethod(null);
422
423      // 5. Navigate to login
424      navigate('/');
425    } catch (error) {
426      console.error('[useAuth] Logout failed:', error);
427      // Still clear state even if backend fails
428      useFolderStore.getState().clearFolders();
429      useVaultStore.getState().clearVaultKeys();
430      useSyncStore.getState().reset();
431      useDeviceRegistryStore.getState().clearRegistry();
432      useMfaStore.getState().reset();
433      clearAuthState();
434      setPendingCipherboxJwt(null);
435      setPendingAuthMethod(null);
436      navigate('/');
437    } finally {
438      setIsLoggingOut(false);
439    }
440  }, [accessToken, coreKitLogout, clearAuthState, navigate, isLoggingOut]);
441
442  // Keep function refs up-to-date for use in session restoration effect.
443  // Using refs prevents the effect from re-firing when function identities change.
444  initializeOrLoadVaultRef.current = initializeOrLoadVault;
445  coreKitLogoutRef.current = coreKitLogout;
446
447  // Session restoration: if Core Kit restores a session from localStorage
448  // on init, we have LOGGED_IN status without going through login flow.
449  // Complete backend auth + vault loading.
450  useEffect(() => {
451    const restoreSession = async () => {
452      // Only restore if Core Kit has a session but we don't have
453      // a backend access token yet
454      if (coreKitLoggedIn && !isAuthenticated && !isLoggingIn && !restoringRef.current) {
455        restoringRef.current = true;
456        setIsLoggingIn(true);
457        try {
458          // Try to refresh using the HTTP-only cookie first
459          const response = await authApi.refresh();
460          setAccessToken(response.accessToken);
461
462          // Restore email from backend if available
463          if (response.email) {
464            setUserEmail(response.email);
465          }
466
467          // Load vault keys from Core Kit keypair
468          await initializeOrLoadVaultRef.current?.();
469        } catch {
470          // No valid backend session -- user needs to re-login
471          // Core Kit session exists but backend cookie expired
472          // Clear Core Kit session to avoid inconsistent state
473          try {
474            await coreKitLogoutRef.current?.();
475          } catch {
476            // Ignore logout errors during cleanup
477          }
478        } finally {
479          restoringRef.current = false;
480          setIsLoggingIn(false);
481        }
482      }
483    };
484    restoreSession();
485  }, [
486    coreKitLoggedIn,
487    isAuthenticated,
488    isLoggingIn,
489    setAccessToken,
490    setUserEmail,
491  ]);
492
493  return {
494    isLoading,
495    isAuthenticated,
496    isRequiredShare,
497    lastAuthMethod,
498    userEmail,
499    pendingAuthMethod,
500    login,
501    loginWithGoogle,
502    loginWithEmail,
503    loginWithWallet,
504    completeRequiredShare,
505    logout,
506  };
507}
